define('Trevera.AddToCart.Message.View'
  , [
    'SCView'
    , 'Cart.Confirmation.Helpers'
    , 'add_to_cart_confirm_message.tpl'
    , 'Backbone'
  ]
  , function (
    SCViewModule
    , CartConfirmationHelpers
    , add_to_cart_confirm_message_tpl
    , Backbone
  ) {
    'use strict';

    // @class Trevera.AddToCart.Message.View @extends Backbone.View
    var SCView = SCViewModule.SCView;

    function AddToCartMessageView(options) {
      SCView.call(this, options);
      this.PDP         = options.PDP;
      this.LAYOUT      = options.application.getComponent('Layout');
      this.template    = add_to_cart_confirm_message_tpl;
      this.isQuickView = options.isQuickview;
      this.model       = {
        message            : options.message,
        requireConfirmation: options.requireConfirmation,
        dataConfirmAction  : options.dataConfirmAction
      };

      this.PDP.cancelableOn('dateSetOnPDP', function () {
        //jQuery('[data-type="add-to-cart"]').trigger('click');
      });
      this.updateDateField = function updateDateField(evt) {
        evt.preventDefault();
        var $acknowledged = this.isQuickView ? jQuery('#in-modal-confirm_message') : jQuery('#confirm_message');
        if ($acknowledged.is(":checked")) {
          var date       = new Date();
          var dateString = ((date.getMonth() + 1) < 10 ? '0' : '') + (date.getMonth() + 1) + '/' + ((date.getDate()) < 10 ? '0' : '') + date.getDate() + '/' + date.getFullYear();
          jQuery('#custcol_trevera_popup_acknowledged').val(dateString).trigger('blur');
          setTimeout(function () {
            jQuery('[data-type="add-to-cart"]').trigger('click');
          }, 1000)

        }
        else {
          this.LAYOUT.showMessage({
            message : 'Please Confirm that you understand this message by checking the checkbox.',
            type    : 'error',
            selector: 'ConfirmMessageError'
          });
        }
      };
      this.continueWithAdd = function (evt) {
        evt.preventDefault();
        jQuery('[data-type="add-to-cart"]').trigger('click');
      };
    }

    AddToCartMessageView.prototype             = Object.create(SCView.prototype);
    AddToCartMessageView.prototype.constructor = AddToCartMessageView;
    AddToCartMessageView.prototype.getContext  = function () {
      return {
        message            : this.model.message,
        requireConfirmation: this.model.requireConfirmation,
        dataConfirmAction  : this.model.dataConfirmAction,
        inModal       : !this.isQuickView
      }
    };
    AddToCartMessageView.prototype.getEvents   = function () {
      return {
        'click [data-action="updatefieldandcontinue"]': 'updateDateField',
        'click [data-action="continue"]'              : 'continueWithAdd'
      }
    };

    return AddToCartMessageView
  });

// Exclude additional facets from the faceted navigation on search and category pages
// usage in templates:
// <div data-view="Facets.FacetedNavigationItems" data-exclude-facets="commercecategoryname,category{{additionalExclude}}"></div>
// copy template into theme.
define(
  'Trevera.Facets.Extension'
  , [
    'Utils',
    'underscore',
    'jQuery'
  ]
  , function (
    Utils,
    _,
    jQuery
  ) {
    'use strict';

    return {
      toggleMenu: function (e) {
        var $target  = jQuery(e.currentTarget);
        var descArea = $target.parent('.facets-item-cell-grid-desc').find('.description-copy');
        if (jQuery(descArea).hasClass('open')) {
          jQuery(descArea).removeClass('open');
          $target.text('more...')
        }
        else {
          jQuery(descArea).addClass('open');
          $target.text('less...')
        }
      },

      mountToApp: function mountToApp(container) {
        var PLP         = container.getComponent('PLP')
          , LAYOUT      = container.getComponent('Layout')
          , ENVIRONMENT = container.getComponent('Environment');

        if (PLP && LAYOUT) {

          LAYOUT.addToViewContextDefinition(
            'Facets.Browse.View'
            , 'treveraExtras'
            , 'object'
            , function treveraExtras(context) {

              var categoryInfo = PLP.getCategoryInfo() || {};
              var cssClass     = "category-level-unknown";
              if (categoryInfo && categoryInfo.idpath) {
                cssClass = "category-level-" + categoryInfo.idpath.split("|").length
              }
              if (categoryInfo && categoryInfo.fullurl) {
                cssClass = "category-level-" + categoryInfo.fullurl.split("/").length
              }

              var filters           = PLP.getAllFilters()
                , facets            = _.filter(filters, function (filter) {
                return filter.id !== 'category' && filter.values && filter.values.length > 1
              })
                , hasSelectedFacets = _.size(facets) > 0
                , hasSubcategories  = _.size(categoryInfo) > 0 //this.model.get('category') ? this.model.get('category').get('categories').length > 0 :// false
                , hasItems          = PLP.getItemsInfo().length > 0;
              return {
                categoryLevelCSSClass : cssClass,
                additionalExclude     : ',' + ENVIRONMENT.getConfig('trevera.facets.excludedFacets'),
                narrowByCopy          : ENVIRONMENT.getConfig('trevera.facets.narrowByCopy') || "Narrow By",
                showCategoryCellsTitle: categoryInfo.categories && categoryInfo.categories.length > 0 && ENVIRONMENT.getConfig('trevera.facets.showSubCategoryTitle'),
                categoryCellsTitle    : ENVIRONMENT.getConfig('trevera.facets.subcategoryTitle') || "Browse by Category",
                hasSelectedFacets     : hasSelectedFacets,
                hasSubcategories      : hasSubcategories,
                showItems             : hasItems || (!hasItems && hasSelectedFacets) || !(!hasItems && !hasSelectedFacets && hasSubcategories),
                hasFacets             : facets.length > 0
              }

            }
          );

          LAYOUT.addToViewContextDefinition(
            'Facets.ItemCell.View'
            , 'treveraExtras'
            , 'object'
            , function treveraExtras(context) {
              var items = PLP.getItemsInfo()
                , item  = _.find(items, {internalid: context.itemId});

              var thumbnail             = context.thumbnail;
              var images                = item.itemimages_detail ? item.itemimages_detail : "";
              var filters               = _.find(PLP.getFilters(), function (filter) {
                return filter.id == customColorId
              });
              var filterValueNormalized = !!filters && filters.value ? filters.value[0].replace(/-/g, ' ').replace(/~/g, '-') : '';
              if (images && filters && filters.value[0] && (images[filters.value[0]] || images[filterValueNormalized])) {
                _.each(filters.value, function (filter) {
                  if (images[filter] && images[filter].urls) {
                    thumbnail = images[filter].urls[0];
                  }
                  if (images[filterValueNormalized] && images[filterValueNormalized].urls) {
                    thumbnail = images[filterValueNormalized].urls[0];
                  }
                });
              }

              var filters     = _.find(PLP.getFilters(), function (filter) { return filter.id == customColorId })
                , existingUrl = context.url;
              return {
                thumbnail           : thumbnail,
                url                 : existingUrl,
                isEnvironmentBrowser: SC.ENVIRONMENT.jsEnvironment === 'browser',
                isTouch             : SC.ENVIRONMENT.isTouchEnabled,
                isSmallScreen       : Utils.getViewportWidth() < 768,
                storeDescription    : item.storedescription,
                itemResizeIDs       : {
                  grid   : ENVIRONMENT.getConfig('trevera.facets.gridResizeID') || 'thumbnail'
                  , table: ENVIRONMENT.getConfig('trevera.facets.tableResizeID') || 'thumbnail'
                  , list : ENVIRONMENT.getConfig('trevera.facets.listResizeID') || 'thumbnail'
                }
              }
            }
          );

          var self = this;
          //handle descriptions on PLP
          LAYOUT.addToViewEventsDefinition(
            'Facets.ItemCell.View',
            'click [data-action="show-full-description"]',
            function (event) {
              self.toggleMenu(event);
            }
          );
        }

      }
    }
  });

define('Trevera.PDP.Helpers'
  , [
    'Backbone',
    'Utils',
    'jQuery',
    'underscore'
  ], function (
    Backbone,
    Utils,
    jQuery,
    _
  ) {
    'use strict';

    return {
      checkForQuantity: function checkForQuantity(PDP, LAYOUT) {
        var selectedChilds = PDP.getSelectedMatrixChilds();
        if (selectedChilds.length === 1) {
          console.log('check for max', selectedChilds[0]);
          if (!selectedChilds[0].isbackorderable) {
            var currentQuantity = parseInt(jQuery('.product-details-quantity-value').val());
            console.log(currentQuantity, selectedChilds[0].quantityavailable);

            if (currentQuantity > selectedChilds[0].quantityavailable) {
              PDP.setQuantity(selectedChilds[0].quantityavailable);
              LAYOUT.showMessage({
                message : 'You have added more to your cart than what is available. We\'ve adjusted the quantity for you.',
                type    : 'error',
                selector: 'Product.Price',
                timeout : 5000
              });
            }

          }
        }
      },

      setSingleOptions: function setSingleOptions(PDP) {
        var selectedChilds = PDP.getSelectedMatrixChilds();

        var item          = PDP.getItemInfo();
        var matrixOptions = _.filter(item.options, {isMatrixDimension: true}); // find matrix dimensions
        var optionMap     = {};

        _.each(matrixOptions, function (option) { // map matrix options to values on available matrix children
          optionMap[option.itemOptionId] = _.uniq(_.pluck(selectedChilds, option.itemOptionId));
        });

        _.each(matrixOptions, function (option) {
          // console.log(option, option.value, optionMap[option.itemOptionId]);
          // console.log('option.value', option.value);
          if ((!option.value || _.isUndefined(option.value)) && optionMap[option.itemOptionId].length === 1 && optionMap[option.itemOptionId][0].length > 0) {
            // if the value isn't set for the option and there is only one available option
            var matchingOptionValue = _.where(option.values, {label: optionMap[option.itemOptionId][0]}); // find the internalid
            if (_.size(matchingOptionValue) === 1 && parseInt(matchingOptionValue[0].internalid) > 0) {
              PDP.setOption(option.cartOptionId, matchingOptionValue[0].internalid); // set the option
            }
          }
          if (optionMap[option.itemOptionId].length === 1 && optionMap[option.itemOptionId][0].length > 0) {
            // TODO: Hide the option selector here using jQuery
            // console.log('option.cartOptionId', option.cartOptionId);
            var selector  = '#' + option.cartOptionId + '-container';
            var $selector = jQuery(selector);
            // console.log('selector', selector);
            // console.log('$selector', $selector);
            // console.log('$selector.length', $selector.length);
            $selector.hide();
          }
        });
      },

      limitQuantityFeatureOn: function (container) {
        var PDP    = container.getComponent('PDP');
        var LAYOUT = container.getComponent('Layout');
        var self = this;

        if (PDP) {
          LAYOUT.addToViewContextDefinition(
            'ProductDetails.Quantity.View'
            , 'quantityAvailable'
            , 'string'
            , function quantityAvailable(context) {
              var matrxiChilds = PDP.getSelectedMatrixChilds(); // get all matrix children
              if (matrxiChilds.length === 1) { // if length == 1 then matrix is fully configured
                var child = _.find(context.model.item.matrixchilditems_detail, function (childs) {
                  return childs.internalid === matrxiChilds[0].internalid;
                });

                if (!!child) {
                  return child.isbackorderable ? 1000 : child.quantityavailable;
                }
              }
              // if matrix not fully configured or non matrix item, use whats on the model
              if (context.model && context.model.item) {
                return context.model.item.isbackorderable ? 1000 : context.model.item.quantityavailable;
              }
              return 1000;
            }
          );

