.checkout-layout {
	@extend %sc-layout;
}

.checkout-layout-header {
	@include common-header;
}

.checkout-layout-content, .checkout-layout-notifications{
	@extend .container;
	@include site-max-width-container;
    padding-left: $sc-content-padding;
    padding-right: $sc-content-padding;
}

.checkout-layout-content {
    padding-top: $sc-padding-lv2;
}

.checkout-layout-footer {
	@extend %sc-footer !optional;
}

.checkout-layout-breadcrumb{}

.checkout-layout-no-javascript-msg {
	@extend .message-warning;
	width: 100%;
}

.cms-landing-page {
  @extend .container;
}
.cms-landing-page-row {
	@extend .row;
}
.cms-landing-page-row-full-col {
	@extend .col-md-12;
}

.cookie-warning-banner-view {
	@extend .global-views-message;
	@extend .global-views-message-info;
}

.cookie-warning-banner-view-close-button {
	@extend .global-views-message-button;
}

{{#if showBanner}}
	<div class="cookie-warning-banner-view alert" role="alert">
		<div>
			{{{cookieMessage}}}
			{{#if showLink}}
			 <a href="https://system.netsuite.com{{linkHref}}" data-toggle="show-in-modal" data-page-header="{{linkContent}}">{{linkContent}}</a>
			{{/if}}
		</div>
		{{#if closable}}
			<button class="cookie-warning-banner-view-close-button" data-action="close-message" type="button" data-dismiss="alert">&times;</button>
		{{/if}}
	</div>
{{/if}}




{{!----
Use the following context variables when customizing this template: 
	
	showBanner (Boolean)
	cookieMessage (String)
	showLink (Boolean)
	linkHref (String)
