	font-weight:$sc-font-weight-normal;
}

.deposit-application-details-applied-amount-value {
	@extend .sc-value;
	font-weight:$sc-font-weight-normal;
}

.deposit-application-details-table-header {
	border-bottom:1px solid $sc-color-theme-border-light;
	display:none;

	@media(min-width: $screen-md-min) {
		width:100%;
		display:table-row-group;
		color:$sc-color-copy-light;
		font-size:$sc-font-size-m;

		th {
			font-weight:$sc-font-weight-normal;
			padding:$sc-padding-lv2;
			display:table-cell;
		}
	}
}

.deposit-application-details-table-header-amount {
	text-align:right;
	width:25%;
}

.deposit-application-details-table-header-transaction-date {
	text-align:right;
	width:25%;
}

.deposit-application-details-table-header-number {
	width:50%;
}

.deposit-application-details-accordion-container-table {

	.deposit-application-details-table-header-number {
		padding-left:$sc-padding-lv4;
	}

	.deposit-application-details-table-header-amount {
		padding-right:$sc-padding-lv4;
	}

	.deposit-details-applied-amount {
		padding-right:$sc-padding-lv4;
	}

	.recordviews-title {
		width:100%;

		@media(min-width: $screen-md-min) {
			width:50%;
			padding-left:$sc-padding-lv4;
		}
	}

	.recordviews-date {
		text-align:left;
		width:100%;

		@media(min-width: $screen-md-min) {
			text-align:right;
			width:25%;
		}
	}

	.recordviews-currency {
		text-align:left;
		width:100%;

		@media(min-width: $screen-md-min) {
			text-align:right;
			width:25%;
			padding-right:$sc-padding-lv4;
		}
	}
}

.deposit-application-details-info-card {
	@extend .info-card;
}

