{{#if isTrackingNumberCollectionEmpty}}
	{{#if showContentOnEmpty}}
		<span class="order-history-list-tracking-number-not-available-label">{{translate 'Tracking Number:'}}</span>
		<span class="order-history-list-tracking-number-not-available {{contentClass}}">
			{{translate 'N/A'}}
		</span>
	{{/if}}
{{else}}
	{{#if isTrackingNumberCollectionLengthEqual1}}
		{{#if showTrackPackagesLabel}}
			<span class="order-history-list-tracking-number-label">
				{{translate 'Track Package'}}:
			</span>
		{{/if}}
			<span class="order-history-list-tracking-number-available-label">{{translate 'Tracking Number:'}} </span>
		{{#if firstTrackingNumberName}}
			<a target="_blank" class="order-history-list-tracking-number-control-numbers-link" data-action="tracking-number" href="{{firstTrackingNumberURL}}">{{firstTrackingNumberName}} {{firstTrackingNumberText}}</a>
		{{else}}
			<a target="_blank" class="order-history-list-tracking-number-control-numbers-link" data-action="tracking-number" href="{{firstTrackingNumberURL}}">{{firstTrackingNumberText}}</a>
		{{/if}}
	{{else}}
		<div class="order-history-list-tracking-number-control">
			<button class="order-history-list-tracking-number-control-button"  data-toggle="dropdown">
				{{translate 'Track Packages'}}
				<i class="order-history-list-tracking-number-control-toggle-icon"></i>
			</button>
			<div class="order-history-list-tracking-number-control-numbers {{#if collapseElements}}collapsed{{/if}}">
				<ul>
				{{#each trackingNumbers}}
					<li>
						<a target="_blank" class="order-history-list-tracking-number-control-numbers-link" data-action="tracking-number" href="{{serviceURL}}">{{trackingNumber}}</a>
						{{serviceName}}
					</li>
				{{/each}}
				</ul>
			</div>
		</div>
	{{/if}}
{{/if}}



{{!----
Use the following context variables when customizing this template:

	isTrackingNumberCollectionEmpty (Boolean)
	isTrackingNumberCollectionLengthEqual1 (Boolean)
	showContentOnEmpty (Boolean)
	contentClass (String)
	trackingNumbersLength (Number)
	collapseElements (Boolean)
	trackingNumbers (Array)
	showTrackPackagesLabel (Boolean)

----}}

{{#if showBackToAccount}}
	<a href="/" class="order-history-list-button-back">
		<i class="order-history-list-button-back-icon"></i>
		{{translate 'Back to Account'}}
	</a>
{{/if}}

<section class="order-history-list">
	<header class="order-history-list-header">
		<h2>{{pageHeader}}</h2>
	</header>


	<div class="order-history-list-header-nav">
		<div class="order-history-list-header-button-group">
			{{#if openIsActive}}
			<span class="order-history-list-header-button-open-active">{{translate 'Open'}}</span>
			{{else}}
			<a href="/open-purchases" class="order-history-list-header-button-open">{{translate 'Open'}}</a>
			{{/if}}

			{{#if isSCISIntegrationEnabled}}
			{{#if inStoreIsActive}}
			<span class="order-history-list-header-button-instore-active">{{translate 'In Store'}}</span>
			{{else}}
			<a href="/instore-purchases" class="order-history-list-header-button-instore">{{translate 'In Store'}}</a>
			{{/if}}
			{{/if}}

			{{#if allIsActive}}
			<span class="order-history-list-header-button-all-active">{{translate 'All'}}</span>
			{{else}}
			<a href="/purchases" class="order-history-list-header-button-all">{{translate 'All'}}</a>
			{{/if}}
		</div>
	</div>

	<div data-view="ListHeader" {{#if openIsActive}}style="display:none;"{{/if}}></div>

	{{#if collectionLengthGreaterThan0}}
	<div class="order-history-list-recordviews-container">
		<table class="order-history-list-recordviews-actionable-table">
			<thead class="order-history-list-recordviews-actionable-header">
			<tr>
				<th class="order-history-list-recordviews-actionable-title-header">
					<span>{{translate 'Purchase No.'}}</span>
				</th>
				{{#each columns}}
				<th class="order-history-list-recordviews-actionable-{{type}}-header">
					<span>{{label}}</span>
				</th>
				{{/each}}
				<th class="order-history-list-recordviews-actionable-actions-header">
					<span>{{translate 'Track Items'}}</span>
				</th>
			</tr>
			</thead>
			<tbody class="order-history-list" data-view="Order.History.Results"></tbody>
		</table>
	</div>

	{{else}}
	{{#if isLoading}}
		<p class="order-history-list-empty">{{translate 'Loading...'}}</p>
	{{else}}
	<div class="order-history-list-empty-section">
		<h5>{{translate 'You don\'t have any purchases in your account right now'}}</h5>

		{{#unless allIsActive}}
			<h5>{{translate 'To see a list of all your past purchases, you can go to the tab <a href="/purchases" class="">All</a>'}}</h5>
		{{/unless}}

		{{#if isSCISIntegrationEnabled}}
		{{#if openIsActive}}
			<h5>{{translate 'If you are looking to review some past purchases made in one of our brick and mortar stores, please check the tab <a href="/instore-purchases" class="">In Store</a>'}}</h5>
		{{/if}}
		{{/if}}
	</div>
	{{/if}}

	{{/if}}

	{{#if showPagination}}
		<div class="order-history-list-case-list-paginator">
			<div data-view="GlobalViews.Pagination"></div>
				{{#if showCurrentPage}}
			<div data-view="GlobalViews.ShowCurrentPage"></div>
				{{/if}}
		</div>
	{{/if}}
</section>



{{!----
Use the following context variables when customizing this template:

pageHeader (String)
collectionLengthGreaterThan0 (Boolean)
isLoading (Boolean)
showPagination (Boolean)
showBackToAccount (Boolean)
isSCISIntegrationEnabled (Boolean)
allIsActive (Boolean)
openIsActive (Boolean)
inStoreIsActive (Boolean)

----}}

{{#if showPayments}}
	<table class="order-history-other-payments-table">
		<thead>
			<th>
				{{translate 'Other Payments' }}
			</th>
			<th>
				{{translate 'Date'}}
			</th>
			<th>
				{{translate 'Amount'}}
			</th>
		</thead>
		{{#if showCreditMemos}}
			{{#each creditMemos}}
				<tr data-recordtype="{{recordtype}}" data-id="{{internalid}}">
					<td data-type='link' class="order-history-other-payments-table-body">
						<span class="order-history-other-payments-table-body-label">
							{{#if ../showLinks}}
								<a href="transactionhistory/{{recordtype}}/{{internalid}}">
								 	{{translate 'Credit Memo #$(0)' tranid }}
								</a>
							{{else}}
								{{translate 'Credit Memo #$(0)' tranid }}
							{{/if}}
						</span>
					</td>
					<td data-type="payment-date" class="order-history-other-payments-table-body-date">
						<span  class="order-history-other-payments-table-body-date-label">{{translate 'Date: '}}</span>
						<span class="order-history-other-payments-table-body-date-value">{{trandate}}</span>
					</td>
					<td data-type="payment-total" class="order-history-other-payments-table-body-amount">
						<span class="order-history-other-payments-table-body-amount-label">{{translate 'Amount: '}}</span>
						<span class="order-history-other-payments-table-body-amount-value">{{amount_formatted}}</span>
					</td>
				</tr> 
			{{/each}}
		{{/if}}
		{{#if showDepositApplications}}
			{{#each depositApplications}}
				<tr data-recordtype="{{recordtype}}" data-id="{{internalid}}">
					<td data-type='link' class="order-history-other-payments-table-body">
						<span class="order-history-other-payments-table-body-label">
							{{#if ../showLinks}}
								<a href="transactionhistory/{{recordtype}}/{{internalid}}">
									{{translate 'Deposit Application #$(0)' tranid }}
								</a>
							{{else}}
								{{translate 'Deposit Application #$(0)' tranid }}
							{{/if}}
						</span>
					</td>
					<td data-type="payment-date" class="order-history-other-payments-table-body-date">
						<span  class="order-history-other-payments-table-body-date-label">{{translate 'Date: '}}</span>
						<span class="order-history-other-payments-table-body-date-value">{{trandate}}</span>
					</td>
					<td data-type="payment-total" class="order-history-other-payments-table-body-amount">
						<span class="order-history-other-payments-table-body-amount-label">{{translate 'Amount: '}}</span>
						<span class="order-history-other-payments-table-body-amount-value">{{amount_formatted}}</span>
					</td>
				</tr> 
			{{/each}}
		{{/if}}

	</table>
{{/if}}



