files.contents:
.payment-wizard-layout-header {
	@extend .wizard-header;
}

.payment-wizard-show-credit-transaction-module-accordion-divider {
	margin:$sc-margin-lv4 0;
}

.payment-wizard-show-credit-transaction-module-accordion-head {
	@extend .accordion-head;
}

.payment-wizard-show-credit-transaction-module-accordion-head-toggle {
	@extend .accordion-head-toggle;
}

.payment-wizard-show-credit-transaction-module-accordion-toggle-icon {
	@extend .acordion-head-toggle-icon;
}

.payment-wizard-show-credit-transaction-module-accordion-body {
	@extend .accordion-body;
}

.payment-wizard-show-credit-transaction-module-accordion-body-header {
	padding: $sc-padding-lv3;
	display: none;
    border-bottom: 1px solid $sc-color-theme-border-light;

	@media (min-width: $screen-sm-min) {
		display: block;
	}
}
.payment-wizard-show-credit-transaction-module-accordion-body-footer {
	padding: $sc-padding-lv3;

	.payment-wizard-show-credit-transaction-module-accordion-container-row {
		text-align: right;
		border-bottom: 0;
	}
}
.payment-wizard-show-credit-transaction-module-accordion-body-header-label {
	@extend .sc-label;
	text-align: right;
}

.payment-wizard-show-credit-transaction-module-accordion-container-row {
	@extend .row;
	margin: 0;
	padding: $sc-padding-lv3 0;
    border-bottom: 1px solid $sc-color-theme-border-light;
}
.payment-wizard-show-credit-transaction-module-accordion-container-row-left {
	@extend .col-sm-4;
	color: $sc-color-link;
}
.payment-wizard-show-credit-transaction-module-accordion-container-row-right {
	@extend .col-sm-8;
	text-align: left;

	@media (min-width: $screen-sm-min) {
		text-align: right;
	}

}
.payment-wizard-show-credit-transaction-module-accordion-container-label {
	@extend .sc-label;
	display: inline;

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.payment-wizard-show-credit-transaction-module-accordion-container-row-label {
	@extend .sc-label;
}

.payment-wizard-showinvoices-module {
	margin-bottom: $sc-margin-lv3;
}
.payment-wizard-showinvoices-module-expander-head {
	@extend .accordion-head;
}

.payment-wizard-showinvoices-module-expander-head-toggle {
	@extend .accordion-head-toggle;
}

.payment-wizard-showinvoices-module-expander-head-toggle-icon {
	@extend .acordion-head-toggle-icon;
}

.payment-wizard-showinvoices-module-expander-body {
	@extend .accordion-body;
}

.payment-wizard-showinvoices-module-expander-body-wrapper {
	padding-top: $sc-padding-lv3;
	padding-bottom: $sc-padding-lv3;
}

.payment-wizard-showinvoices-module-records {
	@extend .table-flex;

}

.payment-wizard-showinvoices-module-mobile-header-amount {
	@extend .sc-label;
	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.payment-wizard-showinvoices-module-subtotal {
	text-align:right;
	td {
		border-top: 1px solid $sc-color-theme-border-light;
		padding:$sc-padding-lv6 $sc-padding-lv3 $sc-padding-lv3 0;
	}
}

.payment-wizard-showinvoices-module-subtotal-label {
	@extend .sc-label;
}

.payment-wizard-showinvoices-module-value-amount {
	@extend .sc-value;
	float:none;
	@media (min-width: $screen-sm-min) {
		float:right;
	}
}

.payment-wizard-showinvoices-module-value-title {
	@extend .sc-value;
	display:block;
	float:none;
	@media (min-width: $screen-sm-min) {
		float:left;
	}
}

.payment-wizard-showinvoices-module-value-subtotal {
	@extend .sc-value;
	font-weight: $sc-font-weight-bold;
}


.payment-wizard-showinvoices-module-amount {
	padding-bottom: $sc-padding-lv2;
	float:none;

	@media (min-width: $screen-sm-min) {
		float:right;
	}
}

.payment-wizard-showinvoices-module-records-head {
	display:none;
	@media (min-width: $screen-sm-min) {
		display: table-row;
		border-bottom: 1px solid $sc-color-theme-border-light;
	}

	th {
		padding-bottom:$sc-padding-lv3;
	}
}

.payment-wizard-showinvoices-module-header-number-value {
	display:none;
}

.payment-wizard-showinvoices-module-header-amount {
	text-align:right;
	font-weight: $sc-font-weight-normal;
	width:100%;
}

.payment-wizard-showinvoices-module-invoice {
	td {
		padding-top:$sc-padding-lv3;
		padding-bottom:$sc-padding-lv3;
		width: 100%;
		clear:both;
	}
}

.payment-wizard-step-title{
	margin-bottom: $sc-margin-lv4;
}

.payment-wizard-step-content-wrapper{
	@extend .row;
}

.payment-wizard-step-content-main{
	@extend .col-xs-12;
	@extend .col-sm-8;
	.list-header-view-filters {
		padding: 0;
	}
}

.payment-wizard-step-content-secondary{
	@extend .col-xs-12;
	@extend .col-sm-4;
	float: right;
	margin-bottom: $sc-margin-lv4;

}

.payment-wizard-step-actions{
	@extend .col-xs-12;
	@extend .col-sm-8;
}

.payment-wizard-step-button-container{
	@extend .wizard-step-button-container;
}

.payment-wizard-step-button-back{
	@extend .wizard-step-button-back;
}

.payment-wizard-step-button-continue{
	@extend .wizard-step-button-continue
}

.payment-wizard-step-content-wrapper{
	.order-wizard-address-module-show-addresses-container{
		@extend .box-column;
	}
}

.payment-wizard-summary-module {
	margin-bottom: $sc-margin-lv4;
}

.payment-wizard-summary-module-container {
	@extend .summary-container;
}

.payment-wizard-summary-module-title {
	@extend .summary-title;
}

.payment-wizard-summary-module-button-continue {
	@extend .button-primary;
	@extend .button-large;
	@extend .button-generic;
	display: none;

	@media (min-width: $screen-sm-min) {
		display: block;
	}
}

.payment-wizard-summary-module-grid-float {
	@extend .summary-grid-float;
}

.payment-wizard-summary-module-invoices-value {
	@extend .summary-grid-right;
}

.payment-wizard-summary-module-estimated-total-value {
	@extend .summary-grid-right;
}

.payment-wizard-summary-module-credits-subtotal-value {
	@extend .summary-grid-right;
}

.payment-wizard-summary-module-deposits-subtotal-value {
	@extend .summary-grid-right;
}

.payment-wizard-summary-module-alert-information {
	@extend .message-success;
	display:block;
	margin: $sc-margin-lv3;
}

{{#if showEditAction}}
	<a class="payment-wizard-credit-transaction-edit-action-anchor" data-action="edit" href="#">{{translate 'Edit'}}</a>
{{/if}}




{{!----
Use the following context variables when customizing this template: 
	
	showEditAction (Boolean)

----}}

{{#if showAction}}
<div class="payment-wizard-invoice-action-row">
	<a data-action="edit" class="payment-wizard-invoice-action-button" href="#" >
		{{#if isPayfull}}
		{{translate 'Edit Payment'}}
		{{else}}
		{{translate 'Partial Payment'}}
		{{/if}}
	</a>
	<a data-action="remove" class="payment-wizard-invoice-action">
		{{translate 'Remove'}}
	</a>
</div>
{{/if}}

{{!----
Use the following context variables when customizing this template:

isPayfull (Boolean)
showAction (Boolean)

----}}

.pickup-in-store-fulfillment-options {
    font-size: $sc-font-size-s;
    color: $sc-color-success;
}


.pickup-in-store-fulfillment-options-ship-icon {
    @extend .icon-truck;
}

.pickup-in-store-fulfillment-options-pickup-icon {
    @extend .icon-home;
}

.pickup-in-store-fulfillment-options-stock-msg-in {
	@extend .stock-info-in;
	@extend .product-line-stock-msg;
}

.pickup-in-store-fulfillment-options-stock-icon-in {
	@extend .product-line-stock-icon;
	@extend .stock-info-in-icon;
}

.pickup-in-store-fulfillment-options-stock-msg-description {
	@extend .stock-info-description;
	@extend .product-line-stock-msg;
}

.pickup-in-store-fulfillment-options-stock-icon-description {
	@extend .product-line-stock-icon;
	@extend .icon-info;
	color: $sc-color-theme-700;
}

.pickup-in-store-location-info {

    padding: $sc-padding-lv1;

    div h5 {
        color: $sc-neutral-shade-500;
        font-weight: bold;
    }
}

.pickup-in-store-location-info-container{
    div{
        margin-bottom: $sc-margin-lv2;
    }

    .pickup-in-store-location-info-container-additional-info{
        color: $sc-color-success;
    }
}

.pickup-in-store-location-info-buttons{
    button{
        margin: $sc-margin-lv1 0;
    }
}

.pickup-in-store-location-info-get-directions-button {
    @extend .button-tertiary;
    @extend .button-small;
}

.pickup-in-store-location-info-change-store-button {
    @extend .button-tertiary;
    @extend .button-small;
}

.pickup-in-store-location-info-container-address {}
.pickup-in-store-location-info-container-open-hours {}
.pickup-in-store-location-info-container-open-hours-list {}

.pickup-in-store-store-selector-item-detail {}

.pickup-in-store-store-selector-item-detail-item-image {
    display: inline-block;
    vertical-align: top;
	max-width: 70px;
    margin-right: $sc-margin-lv4;
}

.pickup-in-store-store-selector-item-detail-details {
    display: inline-block;
    vertical-align: top;
}

.pickup-in-store-store-selector-item-detail-product-title {
	@extend .medium-title;
}

.pickup-in-store-store-selector-item-detail-list-divider {
    margin: $sc-margin-lv4 0;
    height: 2px;
    background-color: $sc-color-theme-background;
    clear:both;
    display: none;

    @media (min-width: $screen-sm-min) {
        display: block;
    }
}

.pickup-in-store-store-selector-item-detail-product-name {}

.pickup-in-store-store-selector-list-row {
    @extend .row;

    padding-top: $sc-padding-lv3;
    padding-bottom: $sc-padding-lv3;
    margin-left: 0;
    margin-right: 0;
    border-top: 1px solid $sc-color-theme-border-light;
}

.pickup-in-store-store-selector-list-row:first-child {
    @media (min-width: $screen-md-min) {
        border: none;
        padding-top: 0;
    }
}

.pickup-in-store-store-selector-list-row-detail {
    @extend .col-sm-5;
    @extend .col-md-5;

    padding-left: 0;

    @media (max-width: $screen-sm-max) {
        padding-right: 0;
        margin-bottom: $sc-margin-lv2;
    }
}

.pickup-in-store-store-selector-list-row-location-name {
    color: $sc-neutral-shade-500;
    font-weight: bold;
    margin-top: 0;
    margin-bottom: 0;
}

.pickup-in-store-store-selector-list-row-location-address {
    color: $sc-color-copy-light;
    font-size: $sc-font-size-s;
}

.pickup-in-store-store-selector-list-view-location-data {
    @extend .pickup-in-store-view-location-data;
}

.pickup-in-store-store-selector-list-row-opening-hours:hover,
.pickup-in-store-store-selector-list-row-opening-hours:focus {
    color: $sc-color-link-hover;
}

.pickup-in-store-store-selector-list-row-opening-hours-flyout-content {
    @extend .dropdown-menu;
    border-radius: 0;
    border-color: $sc-color-link;
    padding: $sc-padding-lv2;

    p {
        font-size: $sc-font-size-s;
    }

    .locator-venue-details-container-services-hours {
        margin: 0;
    }
}

.pickup-in-store-store-selector-list-row-opening-hours-flyout-content-up{
    bottom: $sc-margin-lv4;
    top: auto;
}

.pickup-in-store-store-selector-list-row-icon-angle-down {
    @extend .icon-angle-down;
}

.pickup-in-store-store-selector-list-row-stock {
    @extend .col-sm-4;
    @extend .col-md-4;

    @media (max-width: $screen-sm-max) {
        padding-left: 0;
        padding-right: 0;
        margin-bottom: $sc-margin-lv4;
    }
}

.pickup-in-store-store-selector-list-row-stock-status-available {
    color: $sc-color-success;
}

.pickup-in-store-store-selector-list-row-stock-status-out-of-stock {
    color: $sc-color-error;
}

.pickup-in-store-store-selector-list-row-button-box {
    @extend .col-sm-3;
    @extend .col-md-3;

    padding-right: 0;
    text-align: right;

    .pickup-in-store-store-selector-list-row-no-available {
        color: $sc-color-error;
    }

    @media (max-width: $screen-sm-max) {
        padding-left: 0;
        padding-right: 0;
    }
}

.pickup-in-store-store-selector-list-row-select-for-pickup {
    @extend .button-primary;
    @extend .button-small;

    @media (max-width: $screen-sm-max) {
        width: auto;
        float: right;
    }
}
.pickup-in-store-store-selector-list-row-store-selected {
    color: $sc-color-success;
    font-weight: $sc-font-weight-semibold;

    i {
        @extend .fa;
        @extend %fa-check;
    }
}

.pickup-in-store-store-selector-list-row-opening-hours-content {}
.pickup-in-store-store-selector-list-row-dropdown {
    position: relative;
}
.pickup-in-store-store-selector-list-row-location {}

.pickup-in-store-store-selector-list-divider {
    margin: $sc-margin-lv4 0;
    height: 2px;
    background-color: $sc-color-theme-background;
    clear:both;
    display: none;

    @media (min-width: $screen-md-min) {
        display: block;
    }
}

.pickup-in-store-store-selector-list-result-title {
    margin-bottom: $sc-margin-lv4;

    span {
        color: $sc-color-primary;
    }

    @media (max-width: $screen-sm-max) {
        margin-bottom: $sc-margin-lv2;
    }
}

.pickup-in-store-store-selector-list-result-rows {
    @media (min-width: $screen-sm-min) {
         overflow-y: auto;
    }
}

.pickup-in-store-store-selector-list-refine-search {
    margin-bottom: $sc-margin-lv4;
    display: block;

    @media (min-width: $screen-sm-min) {
        display: none;
    }
}

.pickup-in-store-store-selector-list-result-item-selected-detail-mobile {
    margin-bottom: $sc-margin-lv4;

    @media (min-width: $screen-sm-min) {
        display: none;
    }
}

.pickup-in-store-store-selector-list-result-item-selected-detail-desktop {
    * {
        font-size: 0.95em !important;
    }

    @media (max-width: $screen-xs-max) {
        display: none;
    }
}

.pickup-in-store-store-selector-search {
    margin-top: $sc-margin-lv4;
}

.pac-container {
    z-index: 1050;
}

.pickup-in-store-store-selector-search .store-locator-search-search-view {
    padding: 0;
}

.pickup-in-store-store-selector-search input {
    @extend .input-medium;
}

.pickup-in-store-store-selector-search .store-locator-search-enter-location label {
    display: block;
    clear: both;
    font-weight: normal;
}

@media (min-width: $screen-md-min) {

    .pickup-in-store-store-selector-search .store-locator-search-group-form-controls {


        display: inline-block;
    }
    .pickup-in-store-store-selector-search .store-locator-search-input-autocomplete{
    	width: inherit;
    }

    .pickup-in-store-store-selector-search .store-locator-search-buttons-container {
        display: inline-block;
        vertical-align: top;
        margin-left: $sc-margin-lv4;
    }

    .pickup-in-store-store-selector-search .store-locator-search-buttons-container-find {
        display: inline-block;
        width: auto;
        padding-left: 0;
        float: none;
        margin-bottom: 0;
        vertical-align: middle;
    }

    .pickup-in-store-store-selector-search .store-locator-search-buttons-container-find .store-locator-search-button-find {
        width: auto;
    }

    .pickup-in-store-store-selector-search .store-locator-search-buttons-container-or-wrap {
        display: inline-block;
        width: auto;
        vertical-align: middle;
        padding: 0;
        float: none;
    }

    .pickup-in-store-store-selector-search .store-locator-search-buttons-container-or-wrap .store-locator-search-buttons-container-or {
        width: auto;
        border: none;
        padding: 0;
        float: none;
        margin-bottom: 0;

        span {
            padding: 0;
            top: 0;
        }
    }

    .pickup-in-store-store-selector-search .store-locator-search-buttons-container-geolocalization {
        display: inline-block;
        vertical-align: middle;
        width: auto;
        float: none;
    }

    .pickup-in-store-store-selector-search .store-locator-search-buttons-container-geolocalization .store-locator-search-button-current {
        width: auto;
    }
}

.pickup-in-store-store-selector {}
.pickup-in-store-store-selector-search {}

.pickup-in-store-option {
    display: block;
    width: 100%;
    margin-bottom: $sc-margin-lv4;
}

.pickup-in-store-option {

    .pickup-in-store-option-column {
        display: table-cell;
        vertical-align: top;

        &:first-child {
            padding-right: $sc-padding-lv2;
        }

        @media (max-width: $screen-xs) {
          max-width: 90%;
        }
    }

    .pickup-in-store-option-column label,
    .pickup-in-store-option-column p {
        margin-bottom: 0;
    }
}

.pickup-in-store-divider-desktop {
    margin: $sc-padding-lv4 0;
    height: 1px;
    background-color: $sc-color-theme-300;
    clear: both
}

.pickup-in-store-select-store-label {
    color: $sc-color-copy-light;
}

.pickup-in-store-option-status-message {
    @extend .message-info;
}
.pickup-in-store-option-status-message-out {
    @extend .message-error;
    @extend .stock-info-out;
}

.pickup-in-store-option-status-message-icon{
  @extend .fa;
  @extend %fa-info-circle;
  margin-right: $sc-margin-lv1;
}

.pickup-in-store-option-pickup-status {}

.pickup-in-store-option-status-available {
    color: $sc-color-success;
}

.pickup-in-store-option-status-not-available {
    color: $sc-color-error;
}

.pickup-in-store-option-status-pre-order {
    color: $sc-color-theme-500;
}

.pickup-in-store-option-pickup-label-free{
  color: $sc-color-success;
}


.pickup-in-store-view-location-data {
  border-radius: 0;
  border-color: $sc-color-link;
  @include box-shadow(1px 1px 1px rgba(0, 0, 0, 0.176));
  padding: $sc-padding-lv2;
}

.pickup-in-store-view-location-data-link {
    color: $sc-color-link;
    cursor: pointer;
}

.pickup-in-store-view-location-data-link, .pickup-in-store-option .dropdown {
  display: inline-block;
}

.pickup-in-store-dropdown {
    @extend .dropdown;
}

.pickup-in-store-dropdown-menu {
    @extend .dropdown-menu;
    right: 0;
    left: auto;
    min-width: 250px;

    @media (max-width: $screen-sm-max) {
      left: 0;
      right: auto;
    }
}

.pickup-in-store-icon-angle-down {
    @extend .icon-angle-down;
}

.pickup-in-store-select-store-modal .global-views-modal {
    @extend .modal-lg;
    max-height: calc(100% - $sc-margin-lv2*3);
}

.pickup-in-store-select-store-modal .global-views-modal-content-header {
    margin-bottom: 0;
}

.pickup-in-store-store-selected-details-get-directions-button,
.pickup-in-store-store-selected-details-change-store-button  {
    @extend .button-tertiary;
    @extend .button-small;
    margin-top: $sc-margin-lv2;
}

.pickup-in-store-store-selected-details-get-directions-button {
    margin-right: $sc-margin-lv2;
}

.pickup-in-store-option-pickup-label {}

.pickup-in-store-option-pickup-label.disabled
{
    color: $sc-color-copy-light;
}

.pickup-in-store-select-store-location-name {}
.pickup-in-store-change-store-link {}
.pickup-in-store-select-store-link {}
.pickup-in-store-store-selected-details-buttons {}
.pickup-in-store-select-location {}
.pickup-in-store-after-select-location {}
.pickup-in-store-option-ship-status {}


.pickup-in-store-options {
@media (max-width: $screen-xs-max) {
        margin-top: $sc-margin-lv4;
    }
}

.pickup-in-store-view-location-data .locator-venue-details-container *{
    font-size: $sc-font-size-s;
    line-height: 1.6;
}

{{#if isAvailableForShip}}
	<div class="pickup-in-store-fulfillment-options-ship pickup-in-store-fulfillment-options">
		<i class="pickup-in-store-fulfillment-options-ship-icon" aria-hidden="true"></i>
		{{translate 'Ship'}}
	</div>
{{/if}}

{{#if isAvailableForPickup}}
	<div class="pickup-in-store-fulfillment-options-pickup pickup-in-store-fulfillment-options">
		<i class="pickup-in-store-fulfillment-options-pickup-icon" aria-hidden="true"></i>
		{{translate 'Pick up in Store'}}
	</div>
{{/if}}

{{!----
The context variables for this template are not currently documented. Use the {{log this}} helper to view the context variables in the Console of your browser's developer tools.

----}}

{{#if showDividerLines}}
	<div class="pickup-in-store-divider-desktop"></div>
{{/if}}

<div class="pickup-in-store">
	{{#if isAvailable}}

		{{#unless isAvailableForPickupOnly }} <!-- only available for pickup -->
			<div class="pickup-in-store-option">

				{{#if isAvailableForShipOnly }} <!-- only available for shipping -->
					<div>
						<p class="pickup-in-store-option-status-message">
							<i class="pickup-in-store-option-status-message-icon"></i>
							<span>{{translate 'Only available for Shipping'}}</span>
						</p>
					</div>
				{{else}}
					<div class="pickup-in-store-option-column" data-action="selectShip">
						<input
							type="radio"
							name="ship-pickup-{{itemInternalId}}"
							class="pickup-in-store-option-ship"
							id="pickup-in-store-option-ship-{{itemInternalId}}"
							{{#if isShipSelected}} checked="checked" {{/if}} />
					</div>
				{{/if}}

				<div class="pickup-in-store-option-column">
					{{#unless isAvailableForShipOnly }}
						<label for="pickup-in-store-option-ship-{{itemInternalId}}"> {{translate 'Ship'}}</label>
					{{/unless}}

					<div>
						{{#if stockInfo.isInStock }}
							{{#if showQuantityAvailable }}
								<p class="pickup-in-store-option-ship-status pickup-in-store-option-status-available">
									{{translate '$(0) Available' stockInfo.stock }}
								</p>
							{{else}}
								<p class="pickup-in-store-option-ship-status pickup-in-store-option-status-available">
									{{translate 'Available'}}
								</p>
							{{/if}}
						{{/if}}
						{{#if stockInfo.showOutOfStockMessage}}
							<span class="pickup-in-store-option-ship-status pickup-in-store-option-status-not-available">{{stockInfo.outOfStockMessage}}</span>
							<span class="pickup-in-store-option-status-pre-order"> - {{translate 'Pre-order now!'}}</span>
						{{/if}}
					</div>
				</div>
			</div>
		{{/unless}}

		{{#unless isAvailableForShipOnly}}
			<div class="pickup-in-store-option">
				{{#if isAvailableForPickupOnly }}
					<div>
						<p class="pickup-in-store-option-status-message">
							<i class="pickup-in-store-option-status-message-icon"></i>
							<span>{{translate 'Only available for Pickup'}}</span>
						</p>
					</div>
				{{else}}
					<div class="pickup-in-store-option-column">
						<input
							id="pickup-in-store-option-pickup-{{itemInternalId}}"
							type="radio"
							data-action="selectPickup"
							name="ship-pickup-{{itemInternalId}}"
							class="pickup-in-store-option-pickup"
							data-type="sc-pusher"
							data-target="pickup-in-store-select-store-pusher"
                            {{#if isLocationSelected}}
                                {{#if locationHasStock}}
                                    {{#if isPickupSelected}}
                                        checked="checked"
                                    {{/if}}
                                {{else}}
                                    disabled
                                {{/if}}
                            {{/if}} />
					</div>
				{{/if}}

				<div class="pickup-in-store-option-column">
					<label class="pickup-in-store-option-pickup-label {{#if isLocationSelected}}{{#unless locationHasStock}}disabled{{/unless}}{{/if}}" for="pickup-in-store-option-pickup-{{itemInternalId}}">
						{{translate 'Pickup in Store'}} -
						<span class="pickup-in-store-option-pickup-label-free">
							{{translate 'FREE'}}!
						</span>
					</label>

					<div>
						{{#if isLocationSelected}}
							<div class="pickup-in-store-after-select-location">
								{{#if locationHasStock}}

									<div class="pickup-in-store-dropdown">
										<a id="pickup-in-store-view-location-dropdown" class="pickup-in-store-view-location-data-link" data-toggle="dropdown" aria-expanded="true">
											<span class="pickup-in-store-option-pickup-status pickup-in-store-option-status-available">
											{{#if showQuantityAvailable }}
												{{#if showCutoffTime}}
													{{#if nextPickupDayIsToday}}
														{{translate '$(0) available today' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsTomorrow}}
														{{translate '$(0) available tomorrow' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsSunday}}
														{{translate '$(0) available on Sunday' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsMonday}}
														{{translate '$(0) available on Monday' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsTuesday}}
														{{translate '$(0) available on Tuesday' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsWednesday}}
														{{translate '$(0) available on Wednesday' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsThursday}}
														{{translate '$(0) available on Thursday' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsFriday}}
														{{translate '$(0) available on Friday}' locationStock}}
													{{/if}}
													{{#if nextPickupDayIsSaturday}}
														{{translate '$(0) available on Saturday' locationStock}}
													{{/if}}
												{{else}}
													{{translate '$(0) available' locationStock}}
												{{/if}}
											{{else}}
												{{#if showCutoffTime}}
													{{#if nextPickupDayIsToday}}
														{{translate 'Available today'}}
													{{/if}}
													{{#if nextPickupDayIsTomorrow}}
														{{translate 'Available tomorrow'}}
													{{/if}}
													{{#if nextPickupDayIsSunday}}
														{{translate 'Available on Sunday'}}
													{{/if}}
													{{#if nextPickupDayIsMonday}}
														{{translate 'Available on Monday'}}
													{{/if}}
													{{#if nextPickupDayIsTuesday}}
														{{translate 'Available on Tuesday'}}
													{{/if}}
													{{#if nextPickupDayIsWednesday}}
														{{translate 'Available on Wednesday'}}
													{{/if}}
													{{#if nextPickupDayIsThursday}}
														{{translate 'Available on Thursday'}}
													{{/if}}
													{{#if nextPickupDayIsFriday}}
														{{translate 'Available on Friday'}}
													{{/if}}
													{{#if nextPickupDayIsSaturday}}
														{{translate 'Available on Saturday'}}
													{{/if}}
												{{else}}
													{{translate 'Available'}}
												{{/if}}
											{{/if}}
											</span>

											<span class="pickup-in-store-select-store-label"> {{translate 'at'}} </span>

											<span class="pickup-in-store-select-store-location-name">{{location.name}}</span> <i class="pickup-in-store-icon-angle-down"></i>
										</a>

										<div class="pickup-in-store-view-location-data pickup-in-store-dropdown-menu" aria-labelledby="pickup-in-store-view-location-dropdown">
											<div data-view="PickupInStore.StoreLocationInfo"></div>

											<div class="pickup-in-store-store-selected-details-buttons">
												<a class="pickup-in-store-store-selected-details-get-directions-button" href="{{getDirectionsUrl}}" target="_blank">
													{{translate 'Get Directions'}}
												</a>
												<button class="pickup-in-store-store-selected-details-change-store-button" data-action="changeStore" type="button">
													{{translate 'Change Store'}}
												</button>
											</div>
										</div>
									</div>
								{{else}} <!-- available for pickup but the item is out of stock -->
									<p>
										<span class="pickup-in-store-option-pickup-status pickup-in-store-option-status-not-available">{{translate 'Not available'}}</span>
										<span class="pickup-in-store-select-store-label"> {{translate 'at'}} </span>
										<span>{{location.name}}</span>
									</p>
									<p>
										<a data-action="selectPickupLink" class="pickup-in-store-change-store-link">{{translate 'Change Store'}}</a>
									</p>
								{{/if}}
							</div>
						{{else}}
							<div class="pickup-in-store-select-location" data-action="selectPickupLink">
								<a class="pickup-in-store-select-store-link">{{translate 'Select Store'}}</a>
								<span class="pickup-in-store-select-store-label"> {{translate 'to check availability'}}.</span>
							</div>
						{{/if}}
					</div>
				</div>
		</div>
		{{/unless}}

	{{else}}
		<p class="pickup-in-store-option-status-message-out">
			<i class="pickup-in-store-option-status-message-icon"></i>
			<span>{{translate 'Not available'}}</span>
		</p>
	{{/if}}
</div>

{{#if showDividerLines}}
	<div class="pickup-in-store-divider-desktop"></div>
{{/if}}


