files.contents:
.quick-order-divider {
	@extend .accordion-divider;
}
.quick-order-expander-head {
	@extend .accordion-head;
}
.quick-order-expander-head-toggle {
	@extend .accordion-head-toggle;
	border-color: $sc-color-theme-border-light;
}
.quick-order-expander-toggle-icon {
	@extend .accordion-toggle-icon;
}
.quick-order-expander-body {
	@extend .accordion-body;
	&.in {
		overflow: visible;
	}
}
.quick-order-expander-container {
	@extend .accordion-container;
	margin: 0;

	.quick-add-box {
		margin: 0;
	}
}

.quickorder-accesspoints-headerlink-link {
    @extend .header-careers-link;
	display:inline;
	color:$sc-color-secondary;
	padding-right: $sc-padding-lv2;

}

{{#if showTitle}}
	<a class="{{#if hasClass}} {{className}} {{else}}quickorder-accesspoints-headerlink-link{{/if}}" href="#" data-touchpoint="{{cartTouchPoint}}" data-hashtag="#cart?openQuickOrder=true" title="{{translate 'Quick Order'}}">
		{{translate title}}
	</a>
{{/if}}

{{!----
Use the following context variables when customizing this template:

	hasClass (Boolean)
	cartTouchPoint (String)
	className (String)

----}}

