// Quantity input
//
// Some of the molecules listed on this styleguide are responsive and change their behaviour according to the device or browser width.
// This is one of those, so, you might change the width of your browser window and see the molecule change its behaviour.
//
// Markup:
// <div class="quantity-input-container">
//   <button class="quantity-input-button-remove">-</button>
//   <input type="number" name="quantity" class="quantity-input" value="1" min="1" />
//   <button class="quantity-input-button-add">+</button>
// </div>
//
// Styleguide 3.11

.quantity-input-container {
	display: block;
}

input[type="text"].quantity-input,
input[type="number"].quantity-input {
 	width: 60px;
 	height: 40px;
 	text-align: right;
 	padding: $sc-margin-lv2;
	background-color: white;
    border: 1px solid $sc-color-theme-border;
	float: left;
	margin-bottom: 0;
    display: inline-block;
    vertical-align: bottom;

	@media (min-width: $screen-md-min) {
		display: inline-block;
		background: white;
		float: none;
		border: 1px solid $sc-color-theme-border;
	}
}

.quantity-input-button {
	background-color: $sc-color-theme-500;
	color: $sc-neutral-shade-0;
	border: none;
	width: 40px;
	height: 40px;
	//float: left;
    display: inline-block;

	&:hover, &:active  {
		background-color: $sc-color-theme-700;
	}

	&:focus {
		outline: none;
	}

	@media (min-width: $screen-md-min) {
		//display: none;
	}
}

.quantity-input-button-add {
	@extend .quantity-input-button;
	border-radius: 0 $sc-border-radius $sc-border-radius 0;
}

.quantity-input-button-remove {
	@extend .quantity-input-button;
	border-radius: $sc-border-radius 0 0 $sc-border-radius;
}

// Header variables
$sc-header-top-height: 22px; /* editable({"type": "string", "label": "Top section height"}) */
$sc-header-top-background-color: $sc-color-primary; /* editable({"type": "color", "label": "Top section background color"}) */
$sc-header-top-text-color: white; /* editable({"type": "color", "label": "Top section text color"}) */
$sc-header-height: 38px; /* editable({"type": "string", "label": "Height containing right hand links"}) */
$sc-header-padding: $sc-padding-lv4;
$sc-mobile-header-height: 60px; /* editable({"type": "string", "label": "Mobile header height"}) */
$sc-header-background-color: white; /* editable({"type": "color", "label": "Background for Header, Navigation and Submenu"}) */
$sc-header-shadow-color: $sc-color-secondary;
$sc-header-margin-bottom-desktop: $sc-margin-lv3;
$sc-header-message-color: #fff; /* editable({"type": "color", "label": "Color of text before top links"}) */

// Header logo variables
$sc-mobile-header-logo-width: 150px; /* editable({"type": "string", "label": "Logo width - mobile"}) */
$sc-tablet-header-logo-width: 200px; /* editable({"type": "string", "label": "Logo width - tablet"}) */
$sc-desktop-header-logo-width: 200px; /* editable({"type": "string", "label": "Logo width - desktop"}) */
$sc-mobile-header-logo-top-margin: 18px; /* editable({"type": "string", "label": "Logo top margin - mobile"}) */
$sc-desktop-header-logo-top-margin: 2px; /* editable({"type": "string", "label": "Logo top margin - desktop"}) */
$sc-mobile-header-logo-left-padding: 15px; /* editable({"type": "string", "label": "Logo left padding - mobile"}) */

// Header links variables
$sc-header-link-font-family: $sc-theme-secondary-font; /* editable({"type": "string", "label": "Link font family"}) */
$sc-header-link-font-style: normal; /* editable({
	"type": "string",
	"label": "Link font style",
	"options": [
		{"value": "italic", "text": "italic"},
		{"value": "normal", "text": "normal"},
		{"value": "oblique", "text": "oblique"}
	]
}) */
$sc-header-link-desktop-font-size: 0.933rem; /* editable({"type": "string", "label": "Link font size - desktop"}) */
$sc-header-link-font-size: 0.8rem; /* editable({"type": "string", "label": "Link font size - mobile/tablet"}) */
$sc-header-link-separator-color: rgba(255, 255, 255, 0.2); /* editable({"type": "color", "label": "Separator color"}) */
$sc-header-link-text-color: #aaa; /* editable({"type": "color", "label": "Link color"}) */
$sc-header-link-hover-text-color: lighten(#fff, 20); /* editable({"type": "color", "label": "Link hover color"}) */
$sc-header-link-active-text-color: lighten(#fff, 17); /* editable({"type": "color", "label": "Link active color"}) */
$sc-header-link-text-transform: none; /* editable({
	"type": "string",
	"label": "Link text transform",
	"options": [
		{"value": "none", "text": "none"},
		{"value": "uppercase", "text": "uppercase"},
		{"value": "lowercase", "text": "lowercase"},
		{"value": "capitalize", "text": "capitalize"}
	]
}) */
$sc-mobile-header-sidebar-link-color: $sc-color-link; /* editable({"type": "color", "label": "Mobile side link color"}) */
$sc-header-icon-color: #000; /* editable({"type": "color", "label": "Icon color"}) */
$sc-header-top-bar-icon-color:$sc-header-icon-color; /* editable({"type": "color", "label": "Icon color"}) */
$sc-header-icon-hover-color: #fff; /* editable({"type": "color", "label": "Icon hover/active color"}) */

$sc-top-header-link-color: #fff;

// Header social icons
$sc-header-social-icon-color: $sc-neutral-shade-300; /* editable({"type": "color", "label": "Social icon color"}) */
$sc-header-social-icon-hover-color: $sc-neutral-shade; /* editable({"type": "color", "label": "Social icon hover color"}) */

$sc-mobile-header-border-color: transparent;
$sc-mobile-header-border-width: 0;

{{!—- Edited for Posh Theme: --}}

{{#if isPriceEnabled}}
    <div class="cart-item-summary-item-list-actionable-qty">
        <form action="#" class="cart-item-summary-item-list-actionable-qty-form" data-action="update-quantity" data-validation="control-group">
            <input type="hidden" name="internalid" id="update-internalid-{{lineId}}" class="update-internalid-{{lineId}}" value="{{lineId}}">
            <label class="cart-item-summary-label" for="quantity-{{lineId}}" data-validation="control">
                {{#if showQuantity}}
                    <input type="hidden" name="quantity" id="quantity-{{lineId}}" value="1">
                {{else}}
                    <div class="cart-item-summary-item-list-actionable-container-qty">
                        <label class="cart-item-summary-item-list-actionable-label-qty">{{translate 'Quantity'}}</label>
                        <div class="cart-item-summary-item-list-actionable-input-qty">
                            {{#unless trvPromotionExtras.hideControls}}
                                {{#if hfExtras.showControls}}
                                    <button type="button" class="cart-item-summary-quantity-remove" data-action="minus" {{#if isMinusButtonDisabled}}disabled{{/if}}>-</button>
                                {{/if}}
                            {{/unless}}
                            <input type="number" data-type="cart-item-quantity-input" name="quantity" id="quantity-{{lineId}}" class="cart-item-summary-quantity-value quantity-{{lineId}}" value="{{line.quantity}}" min="1" {{#if trvPromotionExtras.hideControls}}disabled{{/if}}>
                            {{#unless trvPromotionExtras.hideControls}}
                                {{#if hfExtras.showControls}}
                                    <button type="button" class="cart-item-summary-quantity-add" data-action="plus" {{#if isPlusButtonDisabled}}disabled{{/if}}>+</button>
                                {{/if}}
                            {{/unless}}

                        </div>
                        {{#if showMinimumQuantity}}
                            <small class="cart-item-summary-quantity-title-help">
                                {{translate 'Minimum of $(0) required' minimumQuantity}}
                            </small>
                        {{/if}}
                        {{#if hfExtras.showMaximumQuantity}}
                            <br>
                            <small class="cart-item-summary-quantity-title-help">
                                {{translate 'Maximum of $(0) allowed' hfExtras.maximumQuantity}}
                            </small>
                        {{/if}}
                        {{#if showMaximumQuantity}}
                            <small class="cart-item-summary-quantity-title-help">
                                {{translate 'Maximum of $(0) allowed' maximumQuantity}}
                            </small>
                        {{/if}}
                    </div>
                {{/if}}
                <div data-type="alert-placeholder"></div>
            </label>
        </form>
    </div>

    <div data-view="Quantity.Pricing"></div>

    <div class="cart-item-summary-item-list-actionable-amount">
        <span class="cart-item-summary-item-list-actionable-amount-label">{{translate 'Amount' }}</span>
        <div class="cart-item-summary-prices">
            <span class="cart-item-summary-amount-value">{{ line.total_formatted }}</span>
            {{#if showComparePrice}}
                <small class="muted cart-item-summary-item-view-old-price">{{ line.amount_formatted}}</small>
                {{#if showTooltip}}
                    <i data-toggle="tooltip" data-placement="bottom" title="{{tooltip}}"></i>
                {{/if}}
            {{/if}}
        </div>
    </div>

    <div data-view="PromocodeList" class="cart-item-summary-promocodes"></div>
{{/if}}




{{!----
Use the following context variables when customizing this template:

	line (Object)
	line.item (Object)
	line.item.internalid (Number)
	line.item.type (String)
	line.quantity (Number)
	line.internalid (String)
	line.options (Array)
	line.location (String)
	line.fulfillmentChoice (String)
	lineId (String)
	isMinusButtonDisabled (Boolean)
	showQuantity (Boolean)
	showComparePrice (Boolean)
	showMinimumQuantity (Boolean)
	minimumQuantity (Number)
	isPriceEnabled (Boolean)

----}}

// Edited for Posh Theme

// header myaccount menu
.header-menu-myaccount {
    font-size: $sc-font-size-s; // was $sc-small-font-size;
}

///////////////////////////////////////////////////////////////
//header secondary nav
.header-menu-secondary-nav {
    margin: 0 0 0 ($sc-main-navigation-link-margin - 10px);
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    font-size: $sc-main-navigation-link-font-size;

    @media (min-width: $screen-md-min) {
        display: block;
    }

    @media (min-width: $screen-lg-min) {
        margin: 0 0 0 $sc-main-navigation-link-margin;
    }
}

///////////////////////////////////////////////////////////////
//header search

.header-menu-search {
    display: none;
    //order: 2;
    //padding-right: $sc-padding-lv2;
    @media (min-width: $screen-md-min) {
        display: block;
        //margin-left: $sc-margin-lv2;
        padding-left: $sc-padding-lv2;
        padding-right: $sc-padding-lv2;
        border-left: 1px solid $sc-header-top-background-color;
    }
    @media (min-width: $screen-lg-min) {
        margin-left: $sc-margin-lv3;
        padding-right: $sc-padding-lv3;
        padding-left: $sc-padding-lv3;
    }
}

.header-menu-mobile-search {
    margin-top: 5px;
}
.header-menu-mobile-search,
.header-menu-mobile-cart {
    display: inline-block;
    margin-left: 10px;
    margin-right: 10px;
    @media (min-width: $screen-md-min) {
        display: none;
    }
}

.header-mini-cart-menu-cart-link {
    height: 34px;
    line-height: 34px;
}

.header-menu-mobile-cart {
    height: 30px;
    & .header-mini-cart-menu-cart-link {
        overflow: visible !important;
        line-height: normal;
        height: auto;
        margin-bottom: 4px;
    }
}

.header-menu-search-link {
    font-size: 20px;
    background: none;
    cursor: pointer;
    position: relative;
    color: $sc-top-header-link-color;
    @media (min-width: $screen-md-min) {
        line-height: $sc-header-height;
        border: none;
        padding: 0;
        margin-top: 7px;
    }

    &:hover {
        color: $sc-header-icon-hover-color;
    }

    &.active {
        //Search Content dropdown arrow
        &::before {
            position: absolute;
            bottom: -17px;
            right: -0px;
            display: inline-block;
            border-right: 12px solid transparent;
            border-bottom: 12px solid $sc-color-theme-border;
            border-left: 12px solid transparent;
            border-bottom-color: transparentize($sc-theme-color-overlay, 1);
            content: '';
            z-index: 1001;
        }

        &::after {
            position: absolute;
            bottom: -20px;
            right: -0px;
            display: inline-block;
            border-right: 11px solid transparent;
            border-bottom:11px solid $sc-color-theme-border;
            border-left: 11px solid transparent;
            content: '';
            z-index: 1001;
        }
    }
}

.header-menu-search-link:focus {
    outline: none;
}

.header-menu-search-link.active {
    color: $sc-header-icon-hover-color;
}

///////////////////////////////////////////////////////////////
//header maxi menu

.header-menu-level1 {
    display: none;

    @media (min-width: $screen-md-min) {
        display: block;
        min-height: $sc-main-navigation-height;
    }
}

.header-menu-level1 > li {
    display: inline-block;
    position: relative;

    &:last-of-type {
        .header-menu-level-container {
            right: 0;
        }
    }
}

.header-menu-level1-anchor {
    font-size: 30px;
    display: block;
    position: relative;
    height: $sc-main-navigation-height;
    line-height: $sc-main-navigation-height;
    //padding: 0 $sc-padding-lv2;
    margin-right: $sc-main-navigation-link-margin-right - 10px;
    margin-left: $sc-main-navigation-link-margin-left - 10px;
    color: $sc-main-navigation-link-color;
    font-family: $sc-font-family-header;
    font-weight: $sc-font-weight-normal;
    text-transform: $sc-main-navigation-link-text-transform;
    letter-spacing: $sc-main-navigation-link-letter-spacing;

    &:hover, &:focus {
        color: $sc-main-navigation-link-hover-color;
    }

    &:active {
        color: $sc-main-navigation-link-active-color;
    }


    @media (min-width: $screen-md-min) {
        margin-right: $sc-main-navigation-link-margin-right - 5;
        margin-left: $sc-main-navigation-link-margin-left - 5;
        font-size: $sc-font-size-xl;
    }

    @media (min-width: 1320px) {
        margin-right: $sc-main-navigation-link-margin-right;
        margin-left: $sc-main-navigation-link-margin-left;
        font-size: $sc-font-size-xxxxl;
    }
}

.open .header-menu-level1-anchor {
    color: $sc-main-navigation-link-hover-color;
}

.header-menu-home-anchor,
.header-menu-shop-anchor {
    @extend .header-menu-level1-anchor;
}

//maxi menu level 2
.header-menu-back {
    display: none;
}

.header-menu-level2-anchor {
    text-transform: $sc-main-navigation-submenu-heading-text-transform;
    font-weight: $sc-font-weight-normal;
    color: $sc-main-navigation-submenu-heading-color;
    padding: $sc-main-navigation-submenu-v-padding $sc-main-navigation-submenu-h-padding;
    display: block;
    text-align: left;
    height: auto;
    min-width: $sc-main-navigation-submenu-heading-width;
    background-color: $sc-header-background-color;
    font-family: $sc-font-family-body;
    font-size: $sc-font-size-base;

    &:hover {
        color: $sc-main-navigation-submenu-heading-hover-color;
        background-color: $sc-main-navigation-submenu-heading-bgr-hover-color;
    }

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-base + 2;
    }

    @media (min-width: $screen-lg-min) {
        font-size: $sc-font-size-base + 2;
    }
}

//maxi menu level 3
.header-menu-level3-anchor {
    display: block;
    text-transform: $sc-main-navigation-submenu-link-text-transform;
    padding: $sc-main-navigation-submenu-link-vertical-padding $sc-main-navigation-submenu-link-horizontal-padding 0;
    color: $sc-main-navigation-submenu-link-color;
    text-align: $sc-main-navigation-submenu-link-align;

    &:hover {
        color: $sc-main-navigation-submenu-link-hover-color;
    }

    &:active, &.active {
        color: $sc-main-navigation-submenu-link-active-color;
    }
}

.header-menu-categories-thumbnail-wrapper {
    position: relative; // necessary for header-menu-level3 to achieve full width
}

//container fix for maxi menu on desktop
.header-menu-push-icon, .header-menu-pop-icon {
    display: none;
}

.header-menu-level-container {
    // for 3rd level use
    //background-color: rgba($sc-theme-color-overlay, 0.7);
    //left: 0;
    //width: 100%;

    position: absolute;
    top: 100%;
    z-index: 1003;
    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));

    transition: opacity 150ms ease-out,
    visibility 150ms ease-out;
    opacity: 0;
    height: 0;
    //overflow: hidden;
    visibility: hidden;

    > li {
        //@extend .container; // for 3rd level use
        > ul {
            > li {
                position: relative;

                &.categories-menu-arrow {
                    &:after {
                        border: solid rgba(0, 0, 0, 0.3);
                        border-width: 2px 2px 0 0;
                        display: block;
                        content: '';
                        transform: rotate(45deg);
                        width: 6px;
                        height: 6px;
                        position: absolute;
                        z-index: 1;
                        right: 15px;
                        top: 50%;
                        margin-top: -3px;
                    }
                }

                > ul {
                    pointer-events: none;
                    opacity: 0;
                    position: absolute;
                    left: 85%;
                    background: transparentize($sc-theme-color-overlay, 1-0.175);
                    width: 100%;
                    top: 0;
                    text-align: left;
                    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));
                    transition: opacity 0.1s ease-out,
                    left 0.1s ease-in-out;

                    > li {
                        a {
                            @extend .header-menu-level2-anchor;

                        }
                    }
                }

                &:hover {
                    > a {
                        background-color: $sc-main-navigation-submenu-heading-bgr-hover-color;
                    }

                    > ul {
                        pointer-events: auto;
                        opacity: 1;
                        left: 100%;
                        //changing values for a quick roll-back
                        transition: opacity 0.3s ease-out,
                        left 0.1s ease-in-out;
                    }
                }
            }
        }
    }
}

//maxi menu opened
.open .header-menu-level-container {
    display: block;
    opacity: 1;
    visibility: visible;
    height: auto;
}

.categories-thumbnail {
    display: inline-block;

}

// Edited for Posh Theme

.header-careers-link,
.header-profile-welcome-link,
.header-profile-login-link,
.header-profile-register-link,
.header-profile-loading-link {
    @extend %header-link;
    color: $sc-color-copy-light;
    padding: 0 $sc-padding-lv2;
}

.header-careers-link {
    text-transform: uppercase;
    margin-right: $sc-margin-lv2;
    @media (max-width: $screen-sm-max) {
        margin-right: 0;
    }
}

.header-profile-message {
    .header-sidebar-links & {
        display: none;
    }

    @media (min-width: $screen-md-min) {
        display: inline-block;
        font-style: normal;
        color: $sc-header-message-color;
        padding-right: $sc-padding-lv2;
    }
}

.header-profile-login-link {
    min-width: auto;
    padding-right: 0;
    background: $sc-color-tertiary;
    @media screen and (min-width: $screen-md-min) {
    }

    &:after {
        content: " ";
        white-space: pre;
    }
}

.header-menu-profile-logged-out {
    .header-profile-login-link {
        color: $sc-color-primary;
        text-transform: uppercase;
        font-family: $sc-font-family-body;
        @media (min-width: $screen-md-min) {
            padding-left: $sc-padding-lv8;
            padding-right: $sc-padding-lv8;
            margin-left: $sc-margin-lv3;
            margin-right: $sc-margin-lv3;
            height: $sc-header-height + 2px; //$sc-target-size;
            line-height: $sc-header-height + 2px; //$sc-target-size;
        }
    }
}

.header-profile-register-link {
    padding-left: 0;
    display: inline-block;
}

.header-profile-welcome-link-name {
    //display: block;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;

    /*@media (min-width: $screen-md-min){
        display: inline;
    }*/
}

.header-profile-welcome-user-icon {
    display: none;
    /*@extend .fa;
    @extend %fa-user;

    float:left;
    font-size: $sc-font-size-l;
    padding: $sc-padding-base 0;
    margin-right: $sc-margin-lv3;

    @media (min-width: $screen-md-min){
        display: none;
    }*/
}

.header-profile-welcome-carret-icon {
    display: none;

    /*@extend .fa;
    @extend %fa-angle-down;
    margin-left: $sc-margin-base;

    @media (min-width: $screen-md-min){
        display: inline;
    }*/
}

//////////////////////////////////////////////////////////
//login register

.header-profile-menu-login {
    color: $sc-mobile-header-sidebar-link-color;
    font-family: $sc-theme-secondary-font;
    font-style: italic;
    font-size: $sc-header-link-font-size;
}

.header-profile-menu-login-container {
    //margin-right: $sc-margin-base;
}

.header-profile-menu-login li {
    display: inline-block;
    @media screen and (max-width: $screen-md-min) {
        display: block;
        &:nth-child(2) {
            display: none;
        }
    }
}

.header-profile-login-icon {
    @extend .fa;
    @extend %fa-user;
    font-size: $sc-font-size-m;
    display: none !important;
}

//////////////////////////////////////////////////////////
//my account menu
.header-profile-menu-myaccount-container {
    @extend .header-menu-level-container;
    background-color: white;
    z-index: 1002;
    right: 0;
    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));

    left: auto;
    right: 0;
    min-width: 0;
    padding: $sc-padding-lv4;
    width: 700px;

    > li {
        > ul {
            > li {
                position: static;

                > ul {
                    border: 0;
                    background: transparent;
                    opacity: 1;
                    position: static;
                    left: auto;
                    top: auto;
                    transition: none;
                    @include box-shadow(none);

                    > li {
                        a {
                            min-width: auto;

                            &:hover {
                                background-color: transparent;
                            }
                        }
                    }
                }

                &:hover, &:active, &:focus {
                    > a {
                        background-color: transparent;
                    }

                    > ul {
                        pointer-events: auto;
                        opacity: 1;
                        position: static;
                        left: auto;
                        transition: none;
                    }
                }
            }
        }
    }
}

.open .header-profile-menu-myaccount-container {
    padding: $sc-padding-lv3 $sc-padding-lv3 $sc-padding-lv7;
}

.header-profile-loading-icon {
}

.header-profile-loading-indicator {
}

///////////////////////////////////////////////////////////////
//header sidebar overlay and wrapper

.header-sidebar-overlay {
    position: absolute;
    top: 0;
    right: 0;
    width: 0;
    height: 0;
    z-index: 1001;
    background-color: transparent;
    opacity: 0;
    @include transition(opacity 600ms ease);

    @media (min-width: $screen-md-min) {
        display: none;
    }
}

.header-sidebar-wrapper {
    height: 100%;
    @extend %scroll-y;
