.facets-category-cell-list {}

.facets-category-cell-list-heading {}

.facets-category-cell-list-anchor {}

/* Edited for Manor Theme */

.facets-facet-browse-category-cells-container {
    display: flex;
    margin: $sc-margin-lv4;
    flex-wrap: wrap;
    justify-content: center;
    @media screen and (max-width: $screen-md-max) {
        //display: none;
    }
}

.facets-category-cell {
    text-align: center;
    width: $sc-subcategory-width;
    flex-basis: $sc-subcategory-width;
    margin-bottom: 30px;
    margin-right: $sc-subcategory-margin-right;

    @media (min-width: $screen-sm-min) {
        width: $sc-subcategory-desktop-width;
        flex-basis: $sc-subcategory-desktop-width;
        margin-bottom: 40px;
        margin-left: $sc-subcategory-desktop-margin-left;
        margin-right: $sc-subcategory-desktop-margin-right;
    }
}

.facets-category-cell-image {
    // width: 100%;
    // height: auto;
}

.facets-category-cell-image:hover,
.facets-category-cell-anchor:hover .facets-category-cell-image {
    opacity: 0.8;
    //transition: opacity 0.75s;
    //-webkit-transition: opacity 0.75s;
}

.facets-category-cell-title { /*caption*/
    width: 100%;
    text-align: center;
    font-family: $sc-theme-secondary-font;
    font-size: $sc-font-size-xxxxl;
    text-transform: uppercase;
    font-weight: $sc-font-weight-light;
    height: 100%;
    line-height: 200px;
    background: black;
    color:white;

    a:hover & {
        color: white;
    }
}

.facets-empty {

}

.facets-empty-title {

}

.facets-empty-merchandising-zone {
    margin-bottom: $sc-margin-lv3;
    margin-top: $sc-margin-lv3;
}

/* Edited for Posh Theme */

.facets-facet-browse {
    @extend .container;
    @include site-max-width-container;
    padding-left: 0;
    padding-right: 0;

    @media (min-width: $screen-md-min) {
        padding-left: $sc-padding-lv3;
        padding-right: $sc-padding-lv3;
    }
}

.facets-facet-browse-breadcrum {
    @extend .breadcrumb;
}

.facets-facet-browse-category {
    @extend .clearfix;
}

/////////////////////////////////////////////////////////////////////////////////////////////
//Facet Browse - Header
.facets-facet-browse-header {
    @extend .clearfix;
    padding: $sc-margin-lv1 0 $sc-margin-lv2;
    width: 100%;
    @media (min-width: $screen-md-min) {
        //border-top: 1px solid $sc-color-theme-border;
        padding: $sc-padding-lv3 0;
        &.category-list-header {
            //border-top: none;
        }
    }
}

.facets-facet-browse-title {
    @extend .list-header-title-ahead;
    text-align: center;

    @media (max-width: $screen-sm-min) {
        margin-top: $sc-margin-lv2 !important;
    }

    @media (min-width: $screen-sm-min) and (max-width: $screen-md-min) {
        padding-left: $sc-padding-lv3;
    }

    @media (min-width: $screen-md-min) {
        text-align: left;
        margin-top: $sc-margin-lv1;
    }
}

.facets-facet-browse-title-alt {
}

.facets-facet-browse-list-header {
    @extend .list-header;
}

.facets-facet-browse-list-header-actions {
    @extend .list-header-actions;
    height: $sc-target-size;
    display: none;

    @media (min-width: $screen-md-min) {
        height: auto;
        display: block;
    }
}

.facets-facet-browse-list-header-actions-mobile {
    @media (min-width: $screen-md-min) {
        display: none;
    }
}

.facets-facet-browse-list-header-expander {
    @extend .list-header-expander;

    @media (max-width: $screen-sm-min) {
        display: flex;
        justify-content: center;
    }

    @media (min-width: $screen-sm-min) and (max-width: $screen-md-min) {
        margin-right: $sc-margin-lv3;
    }
}

.facets-facet-browse-list-header-expander-button {
    @extend .list-header-expander-button;
    margin-right: 0;
}

.facets-facet-browse-list-header-expander-icon {
    @extend .list-header-expander-icon;
}

.facets-facet-browse-list-header-filters {
    @extend .list-header-filters;
    margin: 0;
}

.facets-facet-browse-list-header-filters-wrapper {
    @extend .list-header-filters-wrapper;

    @media (max-width: $screen-md-min) {
        padding: $sc-padding-lv4 $sc-padding-lv6;
    }
}

.facets-facet-browse-list-header-filters-row {
    @extend .list-header-filters-row;
}

.facets-facet-browse-list-header-filter-column {
    @extend .list-header-filter-column;
}

.facets-facet-browse-list-header-filter-facets {
    display: none !important; //currently no facets, uncomment if facets ever added
    @extend .list-header-filter-facets;
    border-radius: 0;
    padding: $sc-padding-lv2;

    background-color: #626362; // change to variable
    color: $sc-neutral-shade-0;

    .facets-facet-browse-list-header-filter-facets-icon {
        @extend .list-header-filter-facets-icon;
        font-size: 22px;
    }
}

/////////////////////////////////////////////////////////////////////////////////////////////
//Facet Browse - Header main content layout

.facets-facet-browse-content,
.facets-facet-browse-empty-items {
    @extend .clearfix;
    margin-top: 10px;
    padding-top: 20px;

    &.no-items {
        .facets-facet-browse-results {
            width: 100% !important;
        }
    }

    @media (min-width: $screen-md-min) {
        border-top: 1px solid $sc-color-theme-border;
    }
}

.facets-facet-browse-empty-items {
    margin-top: $sc-margin-lv4;

    @media (max-width: $screen-md-min) {
        padding: 0 $sc-padding-lv3;
    }
}

.facets-facet-browse-facets-sidebar {
    display: none;

    @media (min-width: $screen-md-min) {
        display: block;
    }
}

.facets-faceted-navigation-item-cateogry {
    padding-right: $sc-padding-lv3;
}

.facets-faceted-navigation-item-cateogry .facets-faceted-navigation-item-facet-group-expander-icon {
    line-height: 66px + $sc-padding-lv6;
}

.facets-facet-browse-facets {
    @extend .col-md-3;
    //@include box-shadow(0 0 6px $sc-color-secondary);
    padding: 0;
    //background-color: $sc-neutral-shade-0;
    overflow-x: hidden;

    @media (min-width: $screen-md-min) {
        @include box-shadow(none);
        overflow: hidden;
        padding-right: $sc-padding-lv3;
        width: 20%;
        // background-color:transparent;
    }

    @media (min-width: $screen-lg-min) {
        @include box-shadow(none);
        overflow: hidden;
        padding-right: $sc-padding-lv3;
        width: 16%;
        // background-color:transparent;
    }
}

.facets-facet-browse-facets[data-pusher="sc-pushable-md"] .sc-pusher-header {
    margin-left: 0px;
}

// Handles customization to would trigger header nav to open from right-side of page
.header-sidebar-opened {
    .facets-facet-browse-facets[data-pusher="sc-pushable-md"] {
        display: none;
    }
}


.facets-facet-browse-results {
    @extend .col-md-9;
    padding: 0;

    @media (min-width: $screen-md-min) {
        width: 80%;
    }

    @media (min-width: $screen-lg-min) {
        width: 84%;
    }
}

/////////////////////////////////////////////////////////////////////////////////////////////
//Facet Browse - Items

.facets-facet-browse-items .backbone-collection-view-row {
    margin-bottom: $sc-margin-lv4;

    //the margin is only for the first backbone-collection-view-row
    .backbone-collection-view-row {
        margin-bottom: 0;
    }
}

.facets-facet-browse-items .item-view-stock-msg-description {
    margin-top: 0;
}

.facets-facet-browse-narrowedby {
    padding: 0 $sc-padding-lv3;
    text-transform: capitalize;
    line-height: 35px;
}

.facets-facet-browse-items {
    margin-top: $sc-margin-lv4;
    padding: 0 $sc-padding-lv3;

    @media (min-width: $screen-md-min) {
        margin-top: 0;
        padding: 0;
    }

    .item-cell-price {
        margin-bottom: $sc-margin-lv2;
    }

    .item-cell-image {
        max-width: 100%;
        max-height: 100%;
    }
}

/////////////////////////////////////////////////////////////////////////////////////////////
//Facet Browse - Pagination

.facets-facet-browse-pagination {
    @extend .clearfix;
    text-align: center;
    margin-bottom: $sc-margin-lv8;
}

.facets-facet-browse-banner-section-bottom {
    clear: both;
}

.facets-facet-browse-cms-area-2 {
    @extend .col-md-9;
    @extend .col-md-offset-3;
}

