	@extend small;
	color: $sc-color-secondary;
	margin-left: $sc-margin-lv2;
	display: inline-block;
}

.facets-facets-display-filter-delete-icon {
	@extend .icon-base;
	@extend %fa-times-circle;

	position: relative;
	top: 2px;
	color: $sc-color-secondary;
	margin-left: $sc-margin-lv1;
}

