    position: relative;
    text-align: center;
    @media screen and (min-width: $screen-md-min) {
        min-height: 220px;
    }
    display: flex;
    align-self: center;
    flex-wrap: wrap;
    align-items: center;
}

.facets-item-cell-grid-link-image {
    width: 100%;
