				<ul class="facets-faceted-navigation-item-color-picker">
					{{#each displayValues}}
					<li >
						<a href="{{link}}" title="{{label}}" data-color="{{label}}" class="facets-faceted-navigation-item-color-option {{#if isLightColor}}white-border{{/if}} {{#if isActive}}active{{/if}}">
