// Edited for Posh Theme

// header myaccount menu
.header-menu-myaccount {
    font-size: $sc-font-size-s; // was $sc-small-font-size;
}

///////////////////////////////////////////////////////////////
//header secondary nav
.header-menu-secondary-nav {
    margin: 0 0 0 ($sc-main-navigation-link-margin - 10px);
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    font-size: $sc-main-navigation-link-font-size;

    @media (min-width: $screen-md-min) {
        display: block;
    }

    @media (min-width: $screen-lg-min) {
        margin: 0 0 0 $sc-main-navigation-link-margin;
    }
}

///////////////////////////////////////////////////////////////
//header search

.header-menu-search {
    display: none;
    //order: 2;
    //padding-right: $sc-padding-lv2;
    @media (min-width: $screen-md-min) {
        display: block;
        //margin-left: $sc-margin-lv2;
        padding-left: $sc-padding-lv2;
        padding-right: $sc-padding-lv2;
        border-left: 1px solid $sc-header-top-background-color;
    }
    @media (min-width: $screen-lg-min) {
        margin-left: $sc-margin-lv3;
        padding-right: $sc-padding-lv3;
        padding-left: $sc-padding-lv3;
    }
}

.header-menu-mobile-search {
    margin-top: 5px;
}
.header-menu-mobile-search,
.header-menu-mobile-cart {
    display: inline-block;
    margin-left: 10px;
    margin-right: 10px;
    @media (min-width: $screen-md-min) {
        display: none;
    }
}

.header-mini-cart-menu-cart-link {
    height: 34px;
    line-height: 34px;
}

.header-menu-mobile-cart {
    height: 30px;
    & .header-mini-cart-menu-cart-link {
        overflow: visible !important;
        line-height: normal;
        height: auto;
        margin-bottom: 4px;
    }
}

.header-menu-search-link {
    font-size: 20px;
    background: none;
    cursor: pointer;
    position: relative;
    color: $sc-top-header-link-color;
    @media (min-width: $screen-md-min) {
        line-height: $sc-header-height;
        border: none;
        padding: 0;
        margin-top: 7px;
    }

    &:hover {
        color: $sc-header-icon-hover-color;
    }

    &.active {
        //Search Content dropdown arrow
        &::before {
            position: absolute;
            bottom: -17px;
            right: -0px;
            display: inline-block;
            border-right: 12px solid transparent;
            border-bottom: 12px solid $sc-color-theme-border;
            border-left: 12px solid transparent;
            border-bottom-color: transparentize($sc-theme-color-overlay, 1);
            content: '';
            z-index: 1001;
        }

        &::after {
            position: absolute;
            bottom: -20px;
            right: -0px;
            display: inline-block;
            border-right: 11px solid transparent;
            border-bottom:11px solid $sc-color-theme-border;
            border-left: 11px solid transparent;
            content: '';
            z-index: 1001;
        }
    }
}

.header-menu-search-link:focus {
    outline: none;
}

.header-menu-search-link.active {
    color: $sc-header-icon-hover-color;
}

///////////////////////////////////////////////////////////////
//header maxi menu

.header-menu-level1 {
    display: none;

    @media (min-width: $screen-md-min) {
        display: block;
        min-height: $sc-main-navigation-height;
    }
}

.header-menu-level1 > li {
    display: inline-block;
    position: relative;

    &:last-of-type {
        .header-menu-level-container {
            right: 0;
        }
    }
}

.header-menu-level1-anchor {
    font-size: 30px;
    display: block;
    position: relative;
    height: $sc-main-navigation-height;
    line-height: $sc-main-navigation-height;
    //padding: 0 $sc-padding-lv2;
    margin-right: $sc-main-navigation-link-margin-right - 10px;
    margin-left: $sc-main-navigation-link-margin-left - 10px;
    color: $sc-main-navigation-link-color;
    font-family: $sc-font-family-header;
    font-weight: $sc-font-weight-normal;
    text-transform: $sc-main-navigation-link-text-transform;
    letter-spacing: $sc-main-navigation-link-letter-spacing;

    &:hover, &:focus {
        color: $sc-main-navigation-link-hover-color;
    }

    &:active {
        color: $sc-main-navigation-link-active-color;
    }


    @media (min-width: $screen-md-min) {
        margin-right: $sc-main-navigation-link-margin-right - 5;
        margin-left: $sc-main-navigation-link-margin-left - 5;
        font-size: $sc-font-size-xl;
    }

    @media (min-width: 1320px) {
        margin-right: $sc-main-navigation-link-margin-right;
        margin-left: $sc-main-navigation-link-margin-left;
        font-size: $sc-font-size-xxxxl;
    }
}

.open .header-menu-level1-anchor {
    color: $sc-main-navigation-link-hover-color;
}

.header-menu-home-anchor,
.header-menu-shop-anchor {
    @extend .header-menu-level1-anchor;
}

//maxi menu level 2
.header-menu-back {
    display: none;
}

.header-menu-level2-anchor {
    text-transform: $sc-main-navigation-submenu-heading-text-transform;
    font-weight: $sc-font-weight-normal;
    color: $sc-main-navigation-submenu-heading-color;
    padding: $sc-main-navigation-submenu-v-padding $sc-main-navigation-submenu-h-padding;
    display: block;
    text-align: left;
    height: auto;
    min-width: $sc-main-navigation-submenu-heading-width;
    background-color: $sc-header-background-color;
    font-family: $sc-font-family-body;
    font-size: $sc-font-size-base;

    &:hover {
        color: $sc-main-navigation-submenu-heading-hover-color;
        background-color: $sc-main-navigation-submenu-heading-bgr-hover-color;
    }

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-base + 2;
    }

    @media (min-width: $screen-lg-min) {
        font-size: $sc-font-size-base + 2;
    }
}

//maxi menu level 3
.header-menu-level3-anchor {
    display: block;
    text-transform: $sc-main-navigation-submenu-link-text-transform;
    padding: $sc-main-navigation-submenu-link-vertical-padding $sc-main-navigation-submenu-link-horizontal-padding 0;
    color: $sc-main-navigation-submenu-link-color;
    text-align: $sc-main-navigation-submenu-link-align;

    &:hover {
        color: $sc-main-navigation-submenu-link-hover-color;
    }

    &:active, &.active {
        color: $sc-main-navigation-submenu-link-active-color;
    }
}

.header-menu-categories-thumbnail-wrapper {
    position: relative; // necessary for header-menu-level3 to achieve full width
}

//container fix for maxi menu on desktop
.header-menu-push-icon, .header-menu-pop-icon {
    display: none;
}

.header-menu-level-container {
    // for 3rd level use
    //background-color: rgba($sc-theme-color-overlay, 0.7);
    //left: 0;
    //width: 100%;

    position: absolute;
    top: 100%;
    z-index: 1003;
    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));

    transition: opacity 150ms ease-out,
    visibility 150ms ease-out;
    opacity: 0;
    height: 0;
    //overflow: hidden;
    visibility: hidden;

    > li {
        //@extend .container; // for 3rd level use
        > ul {
            > li {
                position: relative;

                &.categories-menu-arrow {
                    &:after {
                        border: solid rgba(0, 0, 0, 0.3);
                        border-width: 2px 2px 0 0;
                        display: block;
                        content: '';
                        transform: rotate(45deg);
                        width: 6px;
                        height: 6px;
                        position: absolute;
                        z-index: 1;
                        right: 15px;
                        top: 50%;
                        margin-top: -3px;
                    }
                }

                > ul {
                    pointer-events: none;
                    opacity: 0;
                    position: absolute;
                    left: 85%;
                    background: transparentize($sc-theme-color-overlay, 1-0.175);
                    width: 100%;
                    top: 0;
                    text-align: left;
                    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));
                    transition: opacity 0.1s ease-out,
                    left 0.1s ease-in-out;

                    > li {
                        a {
                            @extend .header-menu-level2-anchor;

                        }
                    }
                }

                &:hover {
                    > a {
                        background-color: $sc-main-navigation-submenu-heading-bgr-hover-color;
                    }

                    > ul {
                        pointer-events: auto;
                        opacity: 1;
                        left: 100%;
                        //changing values for a quick roll-back
                        transition: opacity 0.3s ease-out,
                        left 0.1s ease-in-out;
                    }
                }
            }
        }
    }
}

//maxi menu opened
.open .header-menu-level-container {
    display: block;
    opacity: 1;
    visibility: visible;
    height: auto;
}

.categories-thumbnail {
    display: inline-block;

}

// Edited for Posh Theme

.header-careers-link,
.header-profile-welcome-link,
.header-profile-login-link,
.header-profile-register-link,
.header-profile-loading-link {
    @extend %header-link;
    color: $sc-color-copy-light;
    padding: 0 $sc-padding-lv2;
}

.header-careers-link {
    text-transform: uppercase;
    margin-right: $sc-margin-lv2;
    @media (max-width: $screen-sm-max) {
        margin-right: 0;
    }
}

.header-profile-message {
    .header-sidebar-links & {
        display: none;
    }

    @media (min-width: $screen-md-min) {
        display: inline-block;
        font-style: normal;
        color: $sc-header-message-color;
        padding-right: $sc-padding-lv2;
    }
}

.header-profile-login-link {
    min-width: auto;
    padding-right: 0;
    background: $sc-color-tertiary;
    @media screen and (min-width: $screen-md-min) {
    }

    &:after {
        content: " ";
        white-space: pre;
    }
}

.header-menu-profile-logged-out {
    .header-profile-login-link {
        color: $sc-color-primary;
        text-transform: uppercase;
        font-family: $sc-font-family-body;
        @media (min-width: $screen-md-min) {
            padding-left: $sc-padding-lv8;
            padding-right: $sc-padding-lv8;
            margin-left: $sc-margin-lv3;
            margin-right: $sc-margin-lv3;
            height: $sc-header-height + 2px; //$sc-target-size;
            line-height: $sc-header-height + 2px; //$sc-target-size;
        }
    }
}

.header-profile-register-link {
    padding-left: 0;
    display: inline-block;
}

.header-profile-welcome-link-name {
    //display: block;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;

    /*@media (min-width: $screen-md-min){
        display: inline;
    }*/
}

.header-profile-welcome-user-icon {
    display: none;
    /*@extend .fa;
    @extend %fa-user;

    float:left;
    font-size: $sc-font-size-l;
    padding: $sc-padding-base 0;
    margin-right: $sc-margin-lv3;

    @media (min-width: $screen-md-min){
        display: none;
    }*/
}

.header-profile-welcome-carret-icon {
    display: none;

    /*@extend .fa;
    @extend %fa-angle-down;
    margin-left: $sc-margin-base;

    @media (min-width: $screen-md-min){
        display: inline;
    }*/
}

//////////////////////////////////////////////////////////
//login register

.header-profile-menu-login {
    color: $sc-mobile-header-sidebar-link-color;
    font-family: $sc-theme-secondary-font;
    font-style: italic;
    font-size: $sc-header-link-font-size;
}

.header-profile-menu-login-container {
    //margin-right: $sc-margin-base;
}

.header-profile-menu-login li {
    display: inline-block;
    @media screen and (max-width: $screen-md-min) {
        display: block;
        &:nth-child(2) {
            display: none;
        }
    }
}

.header-profile-login-icon {
    @extend .fa;
    @extend %fa-user;
    font-size: $sc-font-size-m;
    display: none !important;
}

//////////////////////////////////////////////////////////
//my account menu
.header-profile-menu-myaccount-container {
    @extend .header-menu-level-container;
    background-color: white;
    z-index: 1002;
    right: 0;
    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));

    left: auto;
    right: 0;
    min-width: 0;
    padding: $sc-padding-lv4;
    width: 700px;

    > li {
        > ul {
            > li {
                position: static;

                > ul {
                    border: 0;
                    background: transparent;
                    opacity: 1;
                    position: static;
                    left: auto;
                    top: auto;
                    transition: none;
                    @include box-shadow(none);

                    > li {
                        a {
                            min-width: auto;

                            &:hover {
                                background-color: transparent;
                            }
                        }
                    }
                }

                &:hover, &:active, &:focus {
                    > a {
                        background-color: transparent;
                    }

                    > ul {
                        pointer-events: auto;
                        opacity: 1;
                        position: static;
                        left: auto;
                        transition: none;
                    }
                }
            }
        }
    }
}

.open .header-profile-menu-myaccount-container {
    padding: $sc-padding-lv3 $sc-padding-lv3 $sc-padding-lv7;
}

.header-profile-loading-icon {
}

.header-profile-loading-indicator {
}

///////////////////////////////////////////////////////////////
//header sidebar overlay and wrapper

.header-sidebar-overlay {
    position: absolute;
    top: 0;
    right: 0;
    width: 0;
    height: 0;
    z-index: 1001;
    background-color: transparent;
    opacity: 0;
    @include transition(opacity 600ms ease);

    @media (min-width: $screen-md-min) {
        display: none;
    }
}

.header-sidebar-wrapper {
    height: 100%;
    @extend %scroll-y;
    overflow-x: hidden; //for right hand side menu
}

.header-sidebar-profile-menu {
    @media screen and (max-width: $screen-md-max) {
        li {
            width: 100%;
        }
    }
}

.header-sidebar-links {
    margin-top: $sc-margin-lv6;

    a {
        @extend %header-link;
        text-align: left;
        color: $sc-mobile-header-sidebar-link-color;
    }

    a.header-profile-register-link {
        ///padding-left: 0;
        display: inline-block;
    }
}


.header-sidebar-logo {
    padding-top: $sc-padding-lv4;
    padding-bottom: $sc-padding-lv2;
}

.header-sidebar-logo,
.header-sidebar-menu {
    //background-color: $sc-mobile-navigation-link-background-color;
    //box-shadow: 15px 0 15px -10px transparentize($sc-theme-color-overlay, 1-0.175) inset;
}

.header-sidebar-logo,
.header-sidebar-menu,
.header-sidebar-links a {
    padding-left: $sc-margin-lv7;
}

.header-sidebar-links a {
    margin-right: $sc-margin-lv1;
    padding: 0 12px 0 24px;
    font-family: $sc-font-family;

    @media screen and (max-width: $screen-sm-max) {
        display: block;
    }
}

///////////////////////////////////////////////////////////////
//header sidebar animation

#main {
    @include transition-transform(400ms ease);
}

.header-sidebar-opened {

    @include translate(-$sc-sidebar-size, 0); //in from right
    height: 100%;
    width: 100%;
    position: fixed;

    @media (min-width: $screen-md-min) {
        @include translate(0, 0);
        height: auto;
        position: static;
    }

    .header-sidebar-overlay {
        width: 100%;
        height: 100%;
        opacity: 0.3;
    }
}


///////////////////////////////////////////////////////////////
//header sidebar menu layout and animation

.header-sidebar-menu-wrapper {
    width: 100%;
    position: relative;
    overflow: hidden;
}

.header-sidebar-menu-wrapper a {
    display: block;
    font-weight: normal;
}

.header-sidebar-menu {
    margin: 0;
    overflow: hidden;
    height: auto !important;
    //padding-top: 45px;
    padding-right: 0px;
    padding-left: 0;
    padding-bottom: 20px;
}

.header-sidebar-menu-transition {
    @include transition(height 200ms);
}

//hide all the li's and a's
.header-sidebar-menu li,
.header-sidebar-menu-path > a {
    display: none;
}

// selective display required li's
// First rule is for the li marked as path
// second rule is for the first children inside the opened li

.header-sidebar-menu .header-sidebar-menu-path,
.header-sidebar-menu-opened > ul > li {
    display: block;
    padding-bottom: 0;
}
.header-sidebar-menu-opened > ul > li {
    padding: $sc-padding-lv1 $sc-padding-lv2 $sc-padding-lv1 $sc-padding-lv1;
    background: white;
    margin-left: 20px;
    border-bottom: 1px solid $sc-color-theme-border;

}

.header-sidebar-menu-path.header-sidebar-menu-opened {
    > a {
        display: none;
    }
}

.header-sidebar-menu-myaccount.header-sidebar-menu-opened {
    .header-menu-myaccount-anchor,
    .header-menu-myaccount-overview {
        display: none;
    }
}


.header-sidebar-menu-flyer {
    position: absolute;
    width: 100%;
    top: 0;
    right: 0;
    margin: 0;
}

//Hide and show required li's
.header-sidebar-menu-flyer li {
    display: none;
}

.header-sidebar-menu-flyer > li {
    display: block;
}

.header-sidebar-menu-flyer.header-sidebar-menu-flyer-in {
    @include animation(headerSidebarMenuFlyerIn 300ms ease-in-out);
}

.header-sidebar-menu-flyer.header-sidebar-menu-flyer-out {
    @include animation(headerSidebarMenuFlyerOut 300ms ease-in-out);
}

@-webkit-keyframes headerSidebarMenuFlyerIn {
    0% {
        @include translate($sc-sidebar-size, 0);
    }

    100% {
        @include translate(0, 0);
    }
}

@keyframes headerSidebarMenuFlyerIn {
    0% {
        @include translate($sc-sidebar-size, 0);
    }

    100% {
        @include translate(0, 0);
    }
}

@-webkit-keyframes headerSidebarMenuFlyerOut {
    0% {
        @include translate(0, 0);
    }

    100% {
        @include translate($sc-sidebar-size, 0);
    }
}

@keyframes headerSidebarMenuFlyerOut {
    0% {
        @include translate(0, 0);
    }

    100% {
        @include translate($sc-sidebar-size, 0);
    }
}

///////////////////////////////////////////////////////////////
//header sidebar menu look and feel

.header-sidebar-menu-wrapper a, .header-sidebar-menu-wrapper span {
    @extend .small-title-navigable;
    text-transform: uppercase;
    padding: $sc-padding-lv2 0;
    display: block;
}

.header-sidebar-menu-wrapper {
    color: $sc-mobile-navigation-link-color;

    a, span {
        color: $sc-mobile-navigation-link-color;

        &:hover, &:focus {
            color: $sc-mobile-navigation-link-hover-color;
        }

        &:active {
            color: $sc-mobile-navigation-link-hover-color;
        }
    }

    li {
        color: $sc-mobile-navigation-link-color;
    }
}

.header-sidebar-menu-wrapper li:last-child > a {
    border-bottom: 0;
}

.header-sidebar-menu-wrapper .header-sidebar-menu-back {
    margin: 0;
    text-transform: capitalize;
}

.header-sidebar-menu-push-icon {
    @extend .icon-angle-right;
    font-size: $sc-navigation-icon-size;
    float: right;
    color: $sc-mobile-navigation-link-color;
}

.header-sidebar-menu-pop-icon {
    @extend .icon-angle-left;
    font-size: $sc-font-size-l;
    margin-right: $sc-margin-lv2;
    color: $sc-mobile-navigation-link-color;
}

.header-sidebar-user-logout-icon {
    @extend .fa;
    @extend %fa-sign-out;
}

.header-sidebar-wrapper .header-profile-welcome-link {
    line-height: 1.1em;
    height: initial;
    text-align: left;
    padding: $sc-padding-lv3;
    //padding-top: $sc-padding-lv3;
    //padding-bottom: $sc-padding-lv3;
    font-family: $sc-font-family;
    cursor: default;
    color: $sc-color-secondary;
}

.header-sidebar-menu-link.active {
    color: $sc-color-copy;
}

.header-message {
    padding: 0;
    background-color: $sc-color-theme-background;
    text-align: center;

    .global-views-message {
        @extend .container;
        margin-bottom: 0px;
        @media (min-width: $screen-md-min) {
            padding: $sc-padding-lv3;
            margin-left: -$sc-margin-lv6;
            margin-right: -$sc-margin-lv6;
        }
    }
}

.header-main-wrapper {
    position: relative;
    background-color: $sc-color-primary;
}

.header-main-nav {
    background: #fff;
    @extend .container;
    @include site-max-width-container;
    min-height: $sc-mobile-header-height;
    padding: $sc-padding-lv3 0;
    width: auto;

    a, button {
        font-family: 'Arial', sans-serif;

        i {
            color: $sc-header-icon-color;
        }

        &:active {
            i {
                color: $sc-header-icon-hover-color;
            }
        }
    }

    border-bottom: $sc-mobile-header-border-width solid $sc-mobile-header-border-color;

    @media (min-width: $screen-md-min) {
        min-height: $sc-header-height;
        background: #000;
        padding: 0 0;
        box-shadow: none;
        border: none;

        a {
            color: $sc-header-link-text-color;

            i {
                color: $sc-header-link-text-color;
            }

            &:hover, &:focus {
                color: $sc-header-link-hover-text-color;

                i {
                    color: $sc-header-link-hover-text-color;
                }
            }

            &:active {
                color: $sc-header-link-active-text-color;

                i {
                    color: $sc-header-link-active-text-color;
                }
            }
        }

    }
}

.header-content {
    @extend .clearfix;
    display: flex;
    flex-wrap: wrap;
    align-items: center;

    @media(min-width: $screen-md-min) {
        display: block;
    }
}


///////////////////////////////////////////////////////////////
//header sidebar toggle
.header-sidebar-toggle-wrapper {
    margin-left: $sc-margin-lv2;

    @media(min-width: $screen-md-min) {
        display: none;
    }
}

.header-sidebar-toggle {
    width: $sc-target-size-mobile;
    text-align: center;
    background-color: transparent;
    width: 25px;
    height: 30px;
    margin-right: 15px;

    & span,
    & span:before,
    & span:after {
        cursor: pointer;
        border-radius: 1px;
        height: 2px;
        width: 25px;
        background-color: $sc-header-icon-color;
        position: absolute;
        display: block;
        content: '';
        transition: all 500ms ease-in-out;
    }

    & span:before {
        top: -8px;
    }

    & span:after {
        bottom: -8px;
    }
}

.header-sidebar-opened .header-sidebar-toggle {
    & span {
        background-color: transparent;
    }

    & span:before,
    & span:after {
        top: 0;
    }

    & span:before {
        transform: rotate(45deg);
    }

    & span:after {
        transform: rotate(-45deg);
    }
}

