/* Edited for Posh Theme */

$sc-color-list-header-view-accordion-body-header: #f1f7fa;

[data-action="sc-pushing"] .product-reviews-center {
  padding: 0;

  @media (min-width: $screen-sm-min){
    padding: $sc-padding-lv4;
  }
}

.product-reviews-center {
  @extend .row;
}

.product-reviews-center-container{
  @extend .col-md-12;
  @extend .col-lg-12;

  clear:both;
  display:block;
  overflow:hidden;
  padding-top:$sc-padding-lv4;
  padding-bottom:$sc-padding-lv4;
  margin-top:-$sc-margin-lv4;

  @media (min-width: $screen-sm-min){
    margin-top: 0;
    padding-top: 0;
  }
}

.product-reviews-center-container-header-no-reviews-available {
    h4, p {
        @media (min-width: $screen-sm-min){
            padding-left: 100px;
            padding-right: 100px;
        }
    }
}

[data-action="sc-pushing"] .product-reviews-center-review-container {
  margin: 0 -20px;
  padding: 0 $sc-padding-lv4 $sc-padding-lv4 $sc-padding-lv4;
  background: $sc-neutral-shade-0;

  @media (min-width: $screen-sm-min){
    padding: $sc-padding-lv4;
    border-top:1px solid $sc-color-theme-border-light;
  }

}

[data-action="sc-pushing"] .product-reviews-center-container {
  padding: $sc-padding-lv4 0;
}

.product-reviews-center-container-header{
  padding: 0 0 $sc-padding-lv3;

  .global-views-star-rating {
    margin-bottom: $sc-margin-lv3;
  }

  @media (min-width: $screen-sm-min){
    padding: 0px;
  }

  &-no-reviews-available {
      h3 {
        //   @extend %product-details-title-styles;
          font-weight: $sc-font-weight-light;
          margin-bottom: 10px;
      }
      h4 {
          font-weight: $sc-font-weight-semibold;
          margin-bottom: 10px;
      }
  }
}

.product-reviews-center-container-header-title-container {
    margin-bottom: $sc-margin-lv8;
}

.product-reviews-center-container-list-header-view-container {
    width: 81%;
    margin: 0 auto $sc-margin-lv6;
    border-bottom: 1px solid $sc-color-theme-border;
    display: flex;
    justify-content: center;

    .list-header-view {
        width: 100%;
        border-bottom: 0;
    }

    .list-header-view-accordion-header {
        @media (max-width: $screen-md-min) {
            display: flex;
            justify-content: center;
        }

    }

    .list-header-view-accordion-body-header {
        @media (min-width: $screen-md-min) {
            display: flex !important;
            flex-direction: row-reverse;
            justify-content: center;
        }
    }
}

.product-reviews-center-container-header-title {
    @extend .line-thru;
    //margin: 0 !important;
    border-bottom: 0;
    //font-size: $sc-font-size-xl;

    @media (max-width: $screen-md-min) {
        padding-bottom: $sc-padding-lv2;
    }
}
.product-reviews-center-container-header-number {
    font-weight: $sc-font-weight-light;
    margin-bottom: $sc-margin-lv4;
    text-transform: lowercase;
}

.product-reviews-center-container-wrapper {
    display: flex;
    flex-direction: column;
    margin: $sc-margin-lv8 auto;
    width: 80%;
    @extend .clearfix;

    & > div {
        flex: 1;
    }

    @media (min-width: $screen-sm-min) {
        width: 100%;
        flex-direction: row;
        padding: $sc-padding-lv4 $sc-padding-lv5;
    }
}

.product-reviews-center-container-left {
    display: flex;
    justify-content: center;
    align-items: center;

    .global-views-star-rating-empty, .global-views-star-rating-filled {
	    font-size: $sc-font-size-xl * 2;
    }
    .global-views-star-rating-value {
        display: block;
        text-align: center;
        font-size: $sc-font-size-l;
        line-height: 2;
    }
}

.product-reviews-center-global-views-rating-by-star-container {
    width: 100%;

    .global-views-rating-by-star {
        ul {
            display: flex;
            flex: 1;

            li {
                padding: 0 $sc-padding-lv3 0 0;
                flex: 1;

                &.global-views-rating-by-star-percentage-area {
                    flex: 4;
                }

                &.global-views-rating-by-star-second-label {
                    padding-right: 0;
                }
            }
        }
    }
}

.product-reviews-center-container-footer {
  @extend .product-reviews-center-review-container;
  @extend .clearfix;
  float: none;
  margin: $sc-margin-lv4 0;
  padding: 0 $sc-padding-lv3 $sc-padding-lv8;
  text-align: center;
}

.product-reviews-center-container-footer-button {
  @extend .button-tertiary;
  @extend .button-medium;
  width:100%;
  text-align: center;

  @media (min-width: $screen-sm-min){
    width: auto;
  }
}
.product-reviews-center-container-button {
  @extend .button-secondary;
  @extend .button-small;
  text-align: center;
  margin-left: $sc-margin-lv2;

  @media (min-width: $screen-sm-min){
    width: auto;
  }
}
[data-pusher="sc-pushable-xs"] .product-reviews-center-list .backbone-collection-view-row {
  @extend .row;
}
.product-reviews-center-list {
  @extend .col-md-12;
  @extend .col-lg-12;

  @media (max-width: $screen-md-min) {
      margin-top: $sc-margin-lv4;
  }

  .list-header-view-sorts,
  .list-header-view-filters {
    padding: 0;
  }

  .list-header-view-accordion-body-header {

    padding: $sc-padding-lv4 $sc-padding-lv2;
    background-color: $sc-color-list-header-view-accordion-body-header;
    margin-top: $sc-margin-lv4;
    border-left: none;
    border-right: none;
    border-bottom: 1px solid $sc-color-theme-border-light;
    border-top: 1px solid $sc-color-theme-border-light;

    @media (min-width: $screen-sm-min){
      padding: 0;
      margin: 0;
      border: none;
      background-color: initial;
      width: 100%;
      display: block;
    }
  }

  .list-header-view-accordion-body {
    margin: 0 -$sc-margin-lv4;
    padding-bottom: 0;

    @media (min-width: $screen-sm-min){
      margin: 0;
    }
  }
}

.product-reviews-center-list .list-header-view {
  border: none;
  margin-bottom: $sc-margin-lv2;

  @media (min-width: $screen-md-min) {
    .list-header-view-sorts {
      margin-bottom: 0;
    }

    .list-header-view-paginator {
      margin: 0;
    }
  }
}

.product-reviews-center-content {
  background: $sc-color-theme-background;
  margin-bottom: $sc-margin-lv3;

  @media (min-width: $screen-sm-min) {
    background: transparent;
    margin-bottom: 0px;
  }
}
.product-reviews-center-pusher {
  @extend .push-button-secondary;

  .product-reviews-center-pusher-title {
    display: inline-block;
    vertical-align: middle;
    margin-top: 0;
  }

  .product-reviews-center-pusher-rating {
    display: inline-block;
    vertical-align: middle;
  }

  .global-views-star-rating-area {
    margin-left: $sc-margin-lv2;
    float: right;
  }

  i.product-reviews-center-pusher-icon {
    position: relative;
    top: 0;
    right: 0;
    display: inline-block;
    vertical-align: middle;
    margin: 0;
    font-size: $sc-font-size-xxl;
    line-height: $sc-font-size-xxl;
    height: 0;
  }
}

[data-type="sc-pusher"] {
  .global-views-star-rating-empty, .global-views-star-rating-filled {
    font-size: 20px;
  }
}

.product-reviews-center-rating-header {
  .global-views-star-rating-area {
    margin-right: $sc-margin-lv1;
  }
  .global-views-star-rating i {
    font-size: 20px;
  }
}

.product-reviews-center-review-container{
    width: 80%;
    margin: 0 auto;
}

.product-reviews-center-footer {
  @extend .col-md-12;
  @extend .col-lg-12;
}

.product-reviews-center-more-info-content-container{
  background: transparent;
  overflow-x: hidden;

  @media (min-width: $screen-sm-min) {
    background: transparent;
  }
}
[data-action="sc-pushing"].product-reviews-center-more-info-content-container {
  padding-bottom: $sc-padding-lv2;
}

// TODO: USED FOR POSH THEME
// PDP UPDATES FOR MOBILE
// KEEP DESKTOP & TABLET VIEW ON MOBILE
@media (max-width: $screen-sm-min) {
    .product-reviews-center-content {
        // HIDE PUSHER
        .product-reviews-center-pusher {
            display: none;
        }
        // KEEP POSITIONING - ON SCREEN
        .product-reviews-center-more-info-content-container {
            position: static;
            background: $sc-neutral-shade-0;
            padding: 0 $sc-padding-lv1 $sc-padding-lv8;
            // HIDE BACK BUTTON
            .sc-pusher-header {
                display: none;
            }
            // RESIZE FOOTER BUTTON
            .product-reviews-center-container-footer-button {
                width: auto;
            }
        }
    }
}

.product-reviews-form-confirmation {
    @extend .product-reviews-form-preview;
    .facets-item-cell-list-price {
        display: none;
    }
    .global-views-star-rating {
        display: none;
    }
    .item-view-stock {
        display: none;
    }

    .global-views-star-rating{
        .global-views-star-rating-content-label{
            visibility: hidden;
        }
    }

    .product-reviews-preview-review-rating {
        .product-reviews-form-confirmation-rating-attribute {
            .global-views-star-rating-content-label {
                visibility: visible;
            }
        }
    }

    margin-bottom: $sc-margin-lv4;
}
.product-reviews-form-confirmation-message {
    @extend .global-views-message;
    @extend .global-views-message-success;
    display: block;
    margin-top: 0;
    margin-left: -$sc-margin-lv3;
    margin-right: -$sc-margin-lv3;

    background: $sc-color-success-background;
    margin-bottom: $sc-margin-lv6;

    @media (min-width: $screen-sm-min){
        margin-left: 0;
        margin-right: 0;
    }
    h2 {
        color: $sc-color-success;
        margin: 0 0 $sc-margin-lv2;
    }
    a {
        display: inline-block;
    }
}
.product-reviews-form-confirmation-item-cell {
    @extend .product-reviews-form-item-cell;
}
.product-reviews-form-confirmation-content {
    @extend .product-reviews-form-preview-content;
    @extend .row;
    margin-top: $sc-margin-lv4;
    margin-bottom: $sc-margin-lv6;

    .global-views-star-rating {
        display: inline-block;
    }
}
.product-reviews-form-confirmation-content-review {
    @extend .product-reviews-preview-review-content;
    padding: 0 $sc-padding-lv3 $sc-padding-lv6;
}
.product-reviews-form-confirmation-content-review h4 {
    margin-bottom: $sc-margin-lv4;
}

.product-reviews-form-confirmation-icon-sign{
    @extend .icon-ok-sign;
}

.product-reviews-form-confirmation-button-back{
    @extend .button-back;
    margin-bottom: 0;
}

.product-reviews-form-confirmation-button-back-icon{
    @extend .button-back-icon;
}

.product-reviews-form-confirmation-review-rating{
    @extend .product-reviews-preview-review-rating;
}

.product-reviews-form-confirmation-review-title{
}

.product-reviews-form-confirmation-content-username {
    font-weight: 600;
    margin-bottom: 20px;

    span {
        font-weight: 400;
        color: $sc-color-primary;
    }
}


.product-reviews-form-confirmation-content-description{}

.product-reviews-form-confirmation-divider-desktop {
    margin-bottom: 20px;
}

.product-reviews-form-preview {
  @extend .product-reviews-form;
}
.product-reviews-form-preview-item-cell {
    @extend .product-reviews-form-item-cell;
}
.product-reviews-form-preview-content {
    @extend .product-reviews-form-content;
}
.product-reviews-form-preview-new {
    @extend .product-reviews-form-new;
}
.product-reviews-form-preview-content-title{
    @extend .product-reviews-form-content-title;
}
.product-reviews-form-preview-content-label-required {
    @extend .product-reviews-form-content-label-required;
}
.product-reviews-form-preview-content-required {
    @extend .product-reviews-form-content-required;
}
.product-reviews-form-preview-content-group {
    @extend .product-reviews-form-content-group;
}
.product-reviews-form-preview-content-groups {
    @extend .product-reviews-form-content-groups;
}
.product-reviews-form-preview-content-group-input {
    @extend .product-reviews-form-content-group-input;
}
.product-reviews-form-preview-content-group-text {
    @extend .product-reviews-form-content-group-text;
}
.product-reviews-form-preview-help {
    @extend .input-optional;
}
.product-reviews-form-preview-actions {
    @extend .product-reviews-form-actions;
    margin: 0;
}
.product-reviews-form-preview-actions-button-submit {
    @extend .product-reviews-form-actions-button-submit;
}
.product-reviews-form-preview-actions-button-edit {
    @extend .product-reviews-form-actions-button-preview;
}
.product-reviews-form-preview-actions-button-cancel  {
    @extend .button-tertiary;
    @extend .button-large;
    margin-bottom: $sc-margin-lv2;
    margin-left: 0;
    text-align: center;

    @media (min-width: $screen-sm-min){
        margin-left: $sc-margin-lv2;
    }
}

.product-reviews-form-preview-divider-desktop{
    margin-bottom: $sc-margin-lv6;

    @media (min-width: $screen-md-min) {
        border-top: 1px solid $sc-color-theme-border-light;
        margin: $sc-margin-lv6 0;
    }
}

.product-reviews-form-preview-divider{
}

.product-reviews-form-preview-main{}

.product-reviews-form {
  @extend .container;
    .product-reviews-form-global-star-rating {
        .global-views-star-rating-label{
            display:none;
        }
    }
}
.product-reviews-form-item-cell {
    @extend .row;
    margin-top: $sc-margin-lv2;
    .facets-item-cell-list-left {
        @extend .col-xs-12;
        @extend .col-md-10;
        margin-bottom: $sc-margin-lv3;
        @media (min-width: $screen-md-min){
            margin-bottom: $sc-margin-lv6;
        }
    }
    .facets-item-cell-list-right{
        @extend .col-xs-8;
        @extend .col-md-10;
    }
    & .cart-add-to-cart-button-container {
        @extend .col-sm-6;
        @extend .col-md-12;
        padding: 0;
    }
    .facets-item-cell-list-title {
        padding-top: 0;
        a {
            @extend .medium-title-navigable;
        }
        span {
            text-transform: uppercase;
            font-weight: 600;
            color: $sc-color-copy-dark;
            font-size: 16px;
        }
    }
    @extend .col-md-3;
    @media (min-width: $screen-md-min){
        margin: 0;
        padding-left: 0;
    }
    .item-views-option-tile,
    .item-views-stock,
    .item-views-price,
    .facets-item-cell-list-quick-view-link,
    .item-cell-rating {
        display: none;
    }
}
.product-reviews-form-content {
    @extend .row;
    background: $sc-color-theme-background;
    padding: 0;
    @extend .col-md-9;
    @media (min-width: $screen-md-min){
        margin: 0;
    }
}

.product-reviews-form-new {
    padding: 0 $sc-padding-lv3;

    .global-views-star-rating-label {
        font-weight: $sc-font-weight-normal;
        margin-bottom: $sc-margin-lv1;
        font-size: 14px;
    }
    .global-views-star-rating-empty-pegs {
    }
    .global-views-star-rating-empty-pegs-error {
    }
    .global-views-star-rating-area-writable-pegs{
        top: 5px;
    }

    .global-views-star-rating {
        display: inline-flex;
        margin-bottom: $sc-margin-lv4;
    }
    .product-reviews-form-global-star-rating {
        .global-views-star-rating-empty, .global-views-star-rating-filled, .global-views-star-rating-error {
            font-size: 35px;
        }
        .global-views-star-rating-empty {
            @extend %fa-star-o;
        }
        .global-views-star-rating-error {
            @extend %fa-star-o;
        }
        .global-views-star-rating-area {
            margin-bottom: $sc-margin-lv1;
        }
        button {
            width: 32.5px;
            height: 34px;
            position: relative;
            float: none;
            top: 0;
        }
    }
    .product-reviews-form-content-rating {
        .global-views-star-rating-empty-pegs,
        .global-views-star-rating-area-writable-pegs button {
            margin-right: 0;
        }
    }
}
.product-reviews-form-content-title {
    display:inline-block;
    font-family: $sc-font-family;
    font-size: $sc-font-size-m;
    //font-weight: $sc-font-weight-semibold;
    color: $sc-color-copy-dark;
    margin-top: $sc-margin-lv4;
    margin-bottom: $sc-margin-lv2;
    letter-spacing: 0;
}
.product-reviews-form-content-label-required {
    @extend .input-optional;
    display: block;
}
.product-reviews-form-content-required {
    @extend .input-required;
}
.product-reviews-form-content-rating {
    @extend .clearfix;
}
.product-reviews-form-content-group {
    clear: both;
    padding: 0;
    margin-top: $sc-margin-lv4;

    .product-reviews-form-help {
        margin-top: $sc-margin-lv1;
        margin-bottom: $sc-margin-lv1;
    }

    &:first-child {
        margin-top: 0;
    }
}
.product-reviews-form-content-groups {
    padding:0;
    float: left;
    clear: both;
    margin-bottom: $sc-margin-lv3;
    @extend .col-md-8;
    @media (min-width: $screen-md-min){
        margin-bottom: $sc-margin-lv4;
        clear: both;
    }
}
.product-reviews-form-content-group-label {
    margin-bottom: $sc-margin-lv1;
}
.product-reviews-form-content-group-input {

    margin-bottom: $sc-margin-lv1;
}
.product-reviews-form-content-group-text {

    min-width: 100%;
    max-width: 294px;
    min-height: 105px;
}
.product-reviews-form-help {
    @extend .input-optional;
}
.product-reviews-form-actions {
    @extend .row;
    padding: $sc-margin-lv6 $sc-margin-lv3 $sc-margin-lv4;
    background: $sc-neutral-shade-0;
    clear: both;

    @media (min-width: $screen-md-min){
        padding: $sc-margin-lv6 0;
    }

    @media (max-width: $screen-xs-max) {
        text-align: center;
    }
}
.product-reviews-form-actions-button-submit {
    @extend .button-medium;
    @extend .button-primary;
    margin-bottom: $sc-margin-lv2;
}
.product-reviews-form-actions-button-preview {
    @extend .button-secondary;
    @extend .button-medium;
    margin-bottom: $sc-margin-lv2;
    margin-left: 0;

    @media (min-width: $screen-sm-min){
        margin-left: $sc-margin-lv2;
    }
}

.product-reviews-form-divider {
    margin-bottom: $sc-margin-lv6;

    @media (min-width: $screen-md-min) {
        border-top: 1px solid $sc-color-theme-border-light;
        margin: $sc-margin-lv6 0;
    }
}

.product-reviews-form-actions-button-back {
    @extend .button-link;
    margin-right: 0;

    @media (min-width: $screen-sm-min){
          float: right;
    }
}

.product-reviews-form-controls{}

.product-reviews-preview-review {
    padding: 0;

    h4 {
        margin-bottom: $sc-margin-lv4;
        word-wrap: break-word;
    }

    .global-views-star-rating{
        .global-views-star-rating-content-label{
            visibility: hidden;
        }
    }

    .product-reviews-preview-review-rating {
        .product-reviews-preview-review-rating-attribute {
            .global-views-star-rating-content-label {
                visibility: visible;
            }
        }
    }

}
.product-reviews-preview-review-title {
    @extend .product-reviews-form-content-title;
}
.product-reviews-preview-review-rating {
    padding: $sc-padding-lv6 $sc-padding-lv3 0;

    .product-reviews-preview-review-rating {
        .global-views-star-rating-area {
            margin-bottom: $sc-margin-lv4;
            font-size: 20px;
            letter-spacing: 2px;
        }
        .global-views-star-rating-label-visible {
            font-weight: $sc-font-weight-semibold;
            margin-top: $sc-margin-lv1;
            width: 90px;
            float: left;
            font-size: 14px;
        }
    }
    .global-views-star-rating {
        display: inline-flex;
        margin-bottom: $sc-margin-lv4;
    }
    .global-views-star-rating-empty-pegs {
      display: inline-block;
      background: $sc-neutral-shade-300;
      border: 2px solid $sc-color-theme-border-light;

      @extend .global-views-star-rating-pegs-small;
    }
    .global-views-star-rating-filled-pegs {
      @extend .global-views-star-rating-empty-pegs;
      background: $sc-color-secondary;
      border: 2px solid $sc-color-secondary;
    }
}
.product-reviews-preview-review-content {
    padding: 0 $sc-padding-lv3 $sc-padding-lv6;
    clear: both;
}
.product-reviews-preview-review-content-username {
    font-weight: 600;
    margin-bottom: $sc-margin-lv4;
    word-wrap: break-word;

    span {
        font-weight: 400;
        color: $sc-color-primary;
    }

}
.product-reviews-preview-review-icon-ok-sign{
    @extend .icon-ok-sign;
}

.product-reviews-preview-review-content-description{
    p {
        word-wrap: break-word;
    }
}

/* Edited for Posh Theme */

.product-reviews-form-preview {
  @extend .product-reviews-form;
}
.product-reviews-review-title{
    @extend .product-reviews-form-content-title;
    margin: 0;
    text-transform: none;
}
.product-reviews-form-preview-content-label-required {
    @extend .product-reviews-form-content-label-required;
}
.product-reviews-form-preview-content-required {
    @extend .product-reviews-form-content-required;
}
.product-reviews-form-preview-content-group {
    @extend .product-reviews-form-content-group;
