.quote-details-disclaimer-bottom-content,
.quote-details-disclaimer-bottom {
  margin-bottom: $sc-margin-lv4;
  margin-top: $sc-margin-lv2;
}

.quote-details-disclaimer-bottom-content {
  display: none;

  @media (min-width: $screen-md-min) {
  display: block;
  }
}

.quote-details-disclaimer-bottom {
  @media (min-width: $screen-md-min) {
    display: none;
  }
}

.quote-details-disclaimer-message {
  color: $sc-color-secondary;
}

.quote-details-header-info-expiration-date-icon-overdue,
.quote-details-header-info-expiration-date-icon-closeoverdue {
  @extend .icon-flag;
  margin-left: $sc-margin-lv1;
}

.quote-details-header-info-expiration-date-icon-overdue {
  color: $sc-color-error;
}

.quote-details-header-info-expiration-date-icon-closeoverdue {
  color: $sc-color-warning;
}

.quote-details-msg {
  @extend .message-info;
  margin-top: $sc-margin-lv1;
  margin-bottom: $sc-margin-lv2;
  width: 100%;
  padding: $sc-margin-lv4;
  font-size: $sc-font-size-xs;

  p {
    @extend .message;
    color: $sc-color-theme-700;
    padding: 0;
    margin-bottom: $sc-margin-lv2;
    font-size: $sc-font-size-xs;

    &:last-child {
      margin-bottom: 0;
    }
  }

  ul {
    margin-bottom: $sc-margin-lv2;
  }

  li {
    color: $sc-color-theme-700;
  }
}

.quote-details-msg-certificate {
  @extend .quote-details-msg;
  padding: $sc-margin-lv2 $sc-margin-lv4;
}
.quote-details-msg-certificate-icon {
  @extend .icon-info;
  color: $sc-color-theme-700;
  margin-right: $sc-margin-lv1;
}
.quote-details-billing-row {
  @extend .row;
}
