/* Edited for Posh Theme */

.shopping-layout {
    @extend %sc-layout;
}

#main-container {
    z-index: 1;
}

.shopping-layout-header {
    @include common-header;
}

.shopping-layout-footer {
    @extend %sc-footer !optional;
}

.shopping-layout-breadcrumb, .shopping-layout-notifications {
    @extend .container;
    @include site-max-width-container;
    padding-left: $sc-content-padding;
    padding-right: $sc-content-padding;
}

.shopping-layout-breadcrumb {
    .global-views-breadcrumb {
        @media (min-width: $screen-md-min) {
            margin-top: 10px;
        }
    }
}

.shopping-layout-no-javascript-msg {
    @extend .message-warning;
    width: 100%;
}

.shopping-layout-content {
    //padding-top: $sc-padding-lv2;
}

.site-search {
	@extend .nav-search;
}

.site-search-content {
	@extend .nav-search-content;
}

.site-search-content-form {
}

.site-search-content-input {
	@extend .nav-search-content-input;

	.itemssearcher-input:focus{
		outline:none;
		outline-style:none;
		box-shadow:none;
		//border-color:transparent;
	}
}

.site-search-input-icon {
	display: none;
	@extend .nav-search-input-icon;
	& .svg-search {
		fill: $sc-neutral-shade-300;
	}

}

.site-search-input-reset {
	@extend .nav-search-input-reset;
	right: 0;
}

.site-search-button-submit {
	@extend .nav-search-button-submit;
	border-radius: $sc-border-radius;
}

.site-search-button-close {
	@extend .nav-search-button-close;
}

.site-search-input-reset-icon {
	@extend .nav-search-input-reset-icon;
}

.social-sharing-flyout-hover-icons {
	position: absolute;
	top: 10px;
	left: 25px;
	z-index: 1000;
	display: none;
}

.product-details-image-gallery:hover {
	.social-sharing-flyout-hover-icons {
		display: none;
		@media (min-width: $screen-sm-min){
			display: block;
		}
	}
}

/* Edited for Posh Theme */

$sc-color-social-sharing-flyout-content-social-facebook-icon: #3b5998;
$sc-color-social-sharing-flyout-content-social-twitter-icon: #00b6f1;
$sc-color-social-sharing-flyout-content-social-google-icon: #dd4b38;
$sc-color-social-sharing-flyout-content-social-pinterest-icon: #bd071d;
$sc-color-social-sharing-flyout-content-social-icon-color: $sc-neutral-shade-300;

.social-sharing-flyout-content {
	position: relative;
	float: right;
}
.social-sharing-flyout-content-button {
	@extend .sc-flyout-control;
	font-size: 15px;
	min-width: 85px;
}
.social-sharing-flyout-content-button-icon{
	@extend .fa;
	@extend %fa-share-alt-square;
	font-size: 20px;
}
.social-sharing-flyout-content-social {
	@extend .sc-flyout-content;
	width: 165px;
	left: auto;
	right: 0;
}
.social-sharing-flyout-content-social-facebook,
.social-sharing-flyout-content-social-twitter,
.social-sharing-flyout-content-social-google,
.social-sharing-flyout-content-social-pinterest  {
	text-align: left;
	padding-left: 0;
	color: $sc-color-theme-700;
	display: block;
	font-size: 15px;
	margin-right: $sc-margin-lv4;
	display: inline-block;

	span {
		display: none;
		@media (min-width: $screen-md-min) {
            display: inline;
            font-size: 0;
		}
	}
}
.social-sharing-flyout-content-social-pinterest {
	margin-bottom: 0;
}
.social-sharing-flyout-content-social-facebook-icon {
    color: $sc-color-social-sharing-flyout-content-social-icon-color;
	font-size: $sc-font-size-xxl;
	@extend .fa;
    @extend %fa-facebook;

    &:hover {
        color: $sc-color-link;
    }
}
.social-sharing-flyout-content-social-twitter-icon {
	color: $sc-color-social-sharing-flyout-content-social-icon-color;
	font-size: $sc-font-size-xxl;
	@extend .fa;
    @extend %fa-twitter;

    &:hover {
        color: $sc-color-link;
    }
}
.social-sharing-flyout-content-social-google-icon {
	color: $sc-color-social-sharing-flyout-content-social-icon-color;
	font-size: $sc-font-size-xxl;
	@extend .fa;
    @extend %fa-google-plus;

    &:hover {
        color: $sc-color-link;
    }
}
.social-sharing-flyout-content-social-pinterest-icon {
	color: $sc-color-social-sharing-flyout-content-social-icon-color;
	font-size: $sc-font-size-xxl;
	@extend .fa;
    @extend %fa-pinterest;

    &:hover {
        color: $sc-color-link;
    }
}

.facebook-like-btn
{
	margin: $sc-margin-lv2 $sc-margin-lv2 0 0;
}

.social-sharing-flyout-icons {
	margin-top: $sc-margin-lv3;
}

.social-sharing-flyout-icons:after {
	content: ".";
	visibility: hidden;
	display: block;
	height: 0;
	clear: both;
}

.store-locator-details {
	@extend .store-locator-main;

	@media (max-width: $screen-sm-min) {
		padding: 0px;
	}
}

.store-locator-details-title {
	@extend .store-locator-main-title;

	@media (max-width: $screen-sm-min) {
		display: none;
	}
}

.store-locator-details-layout-left {
	@extend .store-locator-main-layout-left;
}

.store-locator-details-layout-right {
	@extend .store-locator-main-layout-right;
}

.store-locator-details-store-info {
	background:$sc-neutral-shade-0;
	position:absolute;
	top:270px;

	@media (min-width: $screen-sm-min){
		position:static;
	}
}

.store-locator-details-main-nav-back {
	background: $sc-color-theme-background;
	padding: $sc-padding-lv3;
	text-align:left;
	margin-left: $sc-margin-lv4 *  -1;
	margin-right: $sc-margin-lv4 * -1;
	margin-bottom: $sc-margin-lv4;

	@media (min-width: $screen-sm-min){
		margin-left: 0px;
	    margin-right: 0px;
    }
}

