}

/* Matrix Multi Add */
.matrix-multi-add {
    margin-bottom: $sc-margin-lv8;
}

.matrix-multi-add-table-wrapper table {
    width: 100%;
}

.matrix-multi-add-quantity {
    padding: 0;
}

.multi-add-to-cart-quantity-value {
    background-color: #dddddd !important;
    min-width: auto !important;
}

