{{#if showCurrencySelector}}
	<div class="global-views-currency-selector">
		<span class="global-views-currency-selector-addon">
			{{currentCurrencySymbol}}
		</span>
		<select data-toggle="currency-selector" class="global-views-currency-selector-select">
			{{#each availableCurrencies}}
				<option value="{{code}}" {{#if isSelected}}selected{{/if}}>
					{{displayName}}
				</option>
			{{/each}}
		</select>
	</div>
{{/if}}



{{!----
Use the following context variables when customizing this template: 
	
	showCurrencySelector (Boolean)
	availableCurrencies (Array)
	currentCurrencyCode (String)
	currentCurrencySymbol (String)

----}}

{{#if showHosts}}
<div class="global-views-host-selector">
	<span class="global-views-host-selector-addon">
		<i class="global-views-host-selector-globe-icon"></i>
	</span>
	<select data-toggle="host-selector" class="global-views-host-selector-select">
		{{#each availableHosts}}
		
			{{#if hasLanguages}}
				<optgroup label="{{title}}">
					{{#each languages}}
						<option value="{{host}}" {{#if isSelected}}selected{{/if}}>
							{{displayName}}
						</option>
					{{/each}}
				</optgroup>
			{{/if}}
			
		{{/each}}
	</select>
</div>
{{/if}}

{{!----
The context variables for this template are not currently documented. Use the {{log this}} helper to view the context variables in the Console of your browser's developer tools.

----}}

{{!-- Edited for Posh Theme --}}

{{#if currentPageLowerThanTotalPagesAndCurrentPageGreaterThan0AndPagesCountGreaterThan1}}
<nav class="global-views-pagination">
	<ul class="global-views-pagination-links {{#if showPaginationLinksCompactClass}} global-views-pagination-links-compact {{/if}}">

		{{#if isCurrentPageDifferentThan1}}
		<li class="global-views-pagination-prev">
			<a href="{{previousPageURL}}" class="global-views-pagination-previous-link">
				{{!-- <i class="global-views-pagination-prev-icon"></i> --}}
                Previous
			</a>
		</li>
		{{else}}
		<li class="global-views-pagination-prev-disabled">
			<a href="{{currentPageURL}}" class="global-views-pagination-previous-link">
				{{!-- <i class="global-views-pagination-prev-icon"></i> --}}
                Previous
			</a>
		</li>
		{{/if}}

		{{#if showPageList}}
		{{#if isRangeStartGreaterThan1}}
		<li class="global-views-pagination-disabled">
			<a href="{{currentPageURL}}">...</a>
		</li>
		{{/if}}

		{{#each pages}}
		{{#if isCurrentPageActivePage}}
		<li class="global-views-pagination-links-number">
			<a class="global-views-pagination-active" href="{{fixedURL}}">{{pageIndex}}</a>
		</li>
		{{else}}
		<li class="global-views-pagination-links-number">
			<a href="{{URL}}">{{pageIndex}}</a>
		</li>
		{{/if}}
		{{/each}}

		{{#if isRangeEndLowerThanTotalPages}}
		<li class="global-views-pagination-disabled">
			<a href="{{currentPageURL}}">...</a>
		</li>
		{{/if}}
		{{/if}}

		{{#if isCurrentPageLast}}
		<li class="global-views-pagination-next-disabled">
			<a href="{{currentPageURL}}" class="global-views-pagination-next-link">
				{{!-- <i class="global-views-pagination-next-icon"></i> --}}
                Next
			</a>
		</li>
		{{else}}
		<li class="global-views-pagination-next">
			<a href="{{nextPageURL}}" class="global-views-pagination-next-link">
				{{!-- <i class="global-views-pagination-next-icon"></i> --}}
                Next
			</a>
		</li>
		{{/if}}
	</ul>

    {{#if showPageIndicator}}
	<p class="global-views-pagination-count">{{translate '$(0) of $(1)' currentPage totalPages}}</p>
	{{/if}}
</nav>
{{/if}}



{{!----
Use the following context variables when customizing this template: 
	
	currentPageLowerThanTotalPagesAndCurrentPageGreaterThan0AndPagesCountGreaterThan1 (Boolean)
	showPageIndicator (Boolean)
	paginatorClass (String)
	currentPage (Number)
	totalPages (Number)
	isCurrentPageDifferentThan1 (Boolean)
	currentPageURL (String)
	previousPageURL (String)
	nextPageURL (String)
	showPageList (Boolean)
	showPaginationLinksCompactClass (Boolean)
	isRangeStartGreaterThan1 (Boolean)
	isRangeEndLowerThanTotalPages (Boolean)
	isCurrentPageLast (Boolean)
	pages (Array)

----}}

{{#each rates}}
<div class="global-views-rating-by-star" data-id="stars{{index}}">
  <ul>
    <li class="global-views-rating-by-star-label">
      {{#if showLink}}
        <a href="{{url}}" class="global-views-rating-by-star-link">
      {{/if}}
        
        {{#if @last}}
          {{translate '1 star'}}
        {{else}}
          {{translate '$(0) stars' index}}
        {{/if}}

      {{#if showLink}}
        </a>
      {{/if}}
    </li>
    <li class="global-views-rating-by-star-percentage-area">
      <div class="global-views-rating-by-star-percentage-area-progress-bar">
        <div class="global-views-rating-by-star-percentage-area-progress-bar-filled" style="width: {{percentage}}%;">
        </div>
      </div>
    </li>
    <li class="global-views-rating-by-star-second-label">

      {{#if showLink}}
        <a href="{{url}}" class="global-views-rating-by-star-link">
      {{/if}}
        
        {{#if ../showCount}}
          <span class="global-views-rating-by-star-label-count">
            {{#if isOneReview}}
              {{translate '1 review'}}
            {{else}}
              {{translate '$(0) reviews' count}}
            {{/if}}
          </span>
        {{/if}}

      {{#if showLink}}
        </a>
      {{/if}}
    </li>
  </ul>  
</div>
{{/each}}



{{!----
Use the following context variables when customizing this template: 
	
	showCount (Boolean)
	showPercentage (Boolean)
	rates (Array)

----}}

// Edited for Posh Theme

.header-logo-wrapper {
    flex: 0 0 $sc-mobile-header-logo-width;
    padding-left: $sc-mobile-header-logo-left-padding;

    @media (min-width: $screen-sm-min) and (max-width: $screen-sm-max) {
        flex: 0 0 $sc-tablet-header-logo-width;
    }

    @media(min-width: $screen-md-min) {
        display: none;
    }
}

.header-logo-image {
    display: block;
    vertical-align: bottom;
    width: $sc-mobile-header-logo-width - $sc-mobile-header-logo-left-padding; // for IE, see #106

    @media (min-width: $screen-sm-min) and (max-width: $screen-sm-max) {
        width: $sc-tablet-header-logo-width - $sc-mobile-header-logo-left-padding; // for IE, see #106
    }

	@media(min-width: $screen-md-min) {
        width: auto;
        width: $sc-desktop-header-logo-width; // for IE, see #106
    }
    
}
.header-logo {
    display: inline-block;
    vertical-align: bottom;
}
.header-logo-sitename {}

/* Edited for Posh Theme */

.header-menu-myaccount-back {
    @extend .header-sidebar-menu-back;
}

.header-menu-myaccount-pop-icon {
    @extend .header-sidebar-menu-pop-icon;
}

.header-menu-myaccount-menu-push-icon {
    @extend .header-sidebar-menu-push-icon;
}

.header-sidebar-menu-wrapper .header-menu-myaccount-signout-link {
    display: none;
}

.header-menu-myaccount a.active {
    background-color: $sc-color-theme-background;
}

%default-link {
    color: $sc-color-link;

    &:hover, &:focus {
        color: $sc-color-link-hover;
        text-decoration: none;
    }

    &:active {
        color: $sc-color-link-active;
    }
}

//applies only to the myaccount menu on desktop header
.header-menu-profile {

    position: relative;

    .header-menu-myaccount-anchor {
        display: none;
    }

    .header-menu-myaccount-overview-anchor {
        @extend %default-link;
        @media (min-width: $screen-md-min) {
            font-size: $sc-font-size-m;
            float: left;
            padding: $sc-padding-lv3 0;
        }
    }

    .header-menu-myaccount-back {
        display: none;
    }

    .header-menu-myaccount-menu-push-icon {
        display: none;
    }

    .header-menu-myaccount-overview {
        margin-bottom: $sc-margin-lv7;
        border-bottom: 1px solid $sc-color-theme-border-light;
        @extend .clearfix;
    }

    .header-menu-myaccount-item-level2 {
        float: left;
        width: 33.33%;
        padding-right: $sc-padding-lv8;
        margin-bottom: $sc-margin-lv3;

        &:first-of-type {
            display: none;
        }

        &.line-item-home {
            display: none;
        }

        &.line-item-home {
            display: none;
        }
    }

    .header-menu-myaccount-anchor-level2 {
        @extend %default-link;
        font-weight: $sc-font-weight-semibold;
        padding-left: 5px;
    }


    .header-menu-myaccount-anchor-level3 {
        @extend .header-menu-level3-anchor;
        color: $sc-color-primary;
        background-color: transparent;

        &:hover {
            color: $sc-color-theme-700;
        }

        text-align: left;
    }

    .header-menu-myaccount-signout-link {
        @extend %default-link;
        float: right;
        display: block;
        padding: $sc-padding-lv3 0;

        &:hover .header-menu-myaccount-signout-icon {
            color: $sc-color-link-hover;
        }
    }

    .header-menu-myaccount-signout-icon {
        @extend %default-link;
        @extend .fa;
        @extend %fa-sign-out;
        font-size: $sc-font-size-m;
    }
}

.header-menu-myaccount-level4 {

    @media (min-width: $screen-md-min) {
        display: none;
    }
}


//Fix for my account header menu. Removing overview. Adding style for headers.
.header-profile-menu-myaccount-container {
    li {
        span {
            &.header-menu-myaccount-span-level2 {
                font-weight: $sc-font-weight-normal;
                padding-left: $sc-padding-lv1;
                font-size: $sc-font-size-xxl;
                font-family: $sc-font-family-body;
            }
        }

        a[name="Overview"] {
            display: none;
        }

        &.line-item-loyalty {
            a.header-menu-myaccount-anchor-level2 {
                font-weight: $sc-font-weight-normal;
                padding-left: $sc-padding-lv1;
                font-size: $sc-font-size-xxl;
                font-family: $sc-font-family-body;
            }
        }
    }

}

// Edited for Posh Theme

// header myaccount menu
.header-menu-myaccount {
    font-size: $sc-font-size-s; // was $sc-small-font-size;
}

///////////////////////////////////////////////////////////////
//header secondary nav
.header-menu-secondary-nav {
    margin: 0 0 0 ($sc-main-navigation-link-margin - 10px);
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    font-size: $sc-main-navigation-link-font-size;

    @media (min-width: $screen-md-min) {
        display: block;
    }

    @media (min-width: $screen-lg-min) {
        margin: 0 0 0 $sc-main-navigation-link-margin;
    }
}

///////////////////////////////////////////////////////////////
//header search

.header-menu-search {
    display: none;
    //order: 2;
    //padding-right: $sc-padding-lv2;
    @media (min-width: $screen-md-min) {
        display: block;
        //margin-left: $sc-margin-lv2;
        padding-left: $sc-padding-lv2;
        padding-right: $sc-padding-lv2;
        border-left: 1px solid $sc-header-top-background-color;
    }
    @media (min-width: $screen-lg-min) {
        margin-left: $sc-margin-lv3;
        padding-right: $sc-padding-lv3;
        padding-left: $sc-padding-lv3;
    }
}

.header-menu-mobile-search {
    margin-top: 5px;
}
.header-menu-mobile-search,
.header-menu-mobile-cart {
    display: inline-block;
    margin-left: 10px;
    margin-right: 10px;
    @media (min-width: $screen-md-min) {
        display: none;
    }
}

.header-mini-cart-menu-cart-link {
    height: 34px;
    line-height: 34px;
}

.header-menu-mobile-cart {
    height: 30px;
    & .header-mini-cart-menu-cart-link {
        overflow: visible !important;
        line-height: normal;
        height: auto;
        margin-bottom: 4px;
    }
}

.header-menu-search-link {
    font-size: 20px;
    background: none;
    cursor: pointer;
    position: relative;
    color: $sc-top-header-link-color;
    @media (min-width: $screen-md-min) {
        line-height: $sc-header-height;
        border: none;
        padding: 0;
        margin-top: 7px;
    }

    &:hover {
        color: $sc-header-icon-hover-color;
    }

    &.active {
        //Search Content dropdown arrow
        &::before {
            position: absolute;
            bottom: -17px;
            right: -0px;
            display: inline-block;
            border-right: 12px solid transparent;
            border-bottom: 12px solid $sc-color-theme-border;
            border-left: 12px solid transparent;
            border-bottom-color: transparentize($sc-theme-color-overlay, 1);
            content: '';
            z-index: 1001;
        }

        &::after {
            position: absolute;
            bottom: -20px;
            right: -0px;
            display: inline-block;
            border-right: 11px solid transparent;
            border-bottom:11px solid $sc-color-theme-border;
            border-left: 11px solid transparent;
            content: '';
            z-index: 1001;
        }
    }
}

.header-menu-search-link:focus {
    outline: none;
}

.header-menu-search-link.active {
    color: $sc-header-icon-hover-color;
}

///////////////////////////////////////////////////////////////
//header maxi menu

.header-menu-level1 {
    display: none;

    @media (min-width: $screen-md-min) {
        display: block;
        min-height: $sc-main-navigation-height;
    }
}

.header-menu-level1 > li {
    display: inline-block;
    position: relative;
    vertical-align: middle;

    &:last-of-type {
        .header-menu-level-container {
            right: 0;
        }
    }
}

.header-menu-level1-anchor {
    font-size: 30px;
    display: block;
    position: relative;
    height: $sc-main-navigation-height;
    line-height: $sc-main-navigation-height;
    //padding: 0 $sc-padding-lv2;
    margin-right: $sc-main-navigation-link-margin-right - 10px;
    margin-left: $sc-main-navigation-link-margin-left - 10px;
    color: $sc-main-navigation-link-color;
    font-family: $sc-font-family-header;
    font-weight: $sc-font-weight-normal;
    text-transform: $sc-main-navigation-link-text-transform;
    letter-spacing: $sc-main-navigation-link-letter-spacing;

    &:hover, &:focus {
        color: $sc-main-navigation-link-hover-color;
    }

    &:active {
        color: $sc-main-navigation-link-active-color;
    }


    @media (min-width: $screen-md-min) {
        margin-right: $sc-main-navigation-link-margin-right - 5;
        margin-left: $sc-main-navigation-link-margin-left - 5;
        font-size: $sc-font-size-xl;
    }

    @media (min-width: 1320px) {
        margin-right: $sc-main-navigation-link-margin-right;
        margin-left: $sc-main-navigation-link-margin-left;
        font-size: $sc-font-size-xxxxl;
    }
}

.open .header-menu-level1-anchor {
    color: $sc-main-navigation-link-hover-color;
}

.header-menu-home-anchor,
.header-menu-shop-anchor {
    @extend .header-menu-level1-anchor;
}

//maxi menu level 2
.header-menu-back {
    display: none;
}

.header-menu-level2-anchor {
    text-transform: $sc-main-navigation-submenu-heading-text-transform;
    font-weight: $sc-font-weight-normal;
    color: $sc-main-navigation-submenu-heading-color;
    padding: $sc-main-navigation-submenu-v-padding $sc-main-navigation-submenu-h-padding;
    display: block;
    text-align: left;
    height: auto;
    min-width: $sc-main-navigation-submenu-heading-width;
    background-color: $sc-header-background-color;
    font-family: $sc-font-family-body;
    font-size: $sc-font-size-base;

    &:hover {
        color: $sc-main-navigation-submenu-heading-hover-color;
        background-color: $sc-main-navigation-submenu-heading-bgr-hover-color;
    }

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-base + 2;
    }

    @media (min-width: $screen-lg-min) {
        font-size: $sc-font-size-base + 2;
    }
}

//maxi menu level 3
.header-menu-level3-anchor {
    display: block;
    text-transform: $sc-main-navigation-submenu-link-text-transform;
    padding: $sc-main-navigation-submenu-link-vertical-padding $sc-main-navigation-submenu-link-horizontal-padding 0;
    color: $sc-main-navigation-submenu-link-color;
    text-align: $sc-main-navigation-submenu-link-align;

    &:hover {
        color: $sc-main-navigation-submenu-link-hover-color;
    }

    &:active, &.active {
        color: $sc-main-navigation-submenu-link-active-color;
    }
}

.header-menu-categories-thumbnail-wrapper {
    position: relative; // necessary for header-menu-level3 to achieve full width
}

//container fix for maxi menu on desktop
.header-menu-push-icon, .header-menu-pop-icon {
    display: none;
}

.header-menu-level-container {
    // for 3rd level use
    //background-color: rgba($sc-theme-color-overlay, 0.7);
    //left: 0;
    //width: 100%;

    position: absolute;
    top: 100%;
    z-index: 1003;
    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));

    transition: opacity 150ms ease-out,
    visibility 150ms ease-out;
    opacity: 0;
    height: 0;
    //overflow: hidden;
    visibility: hidden;

    > li {
        //@extend .container; // for 3rd level use
        > ul {
            > li {
                position: relative;

                &.categories-menu-arrow {
                    &:after {
                        border: solid rgba(0, 0, 0, 0.3);
                        border-width: 2px 2px 0 0;
                        display: block;
                        content: '';
                        transform: rotate(45deg);
                        width: 6px;
                        height: 6px;
                        position: absolute;
                        z-index: 1;
                        right: 15px;
                        top: 50%;
                        margin-top: -3px;
                    }
                }

                > ul {
                    pointer-events: none;
                    opacity: 0;
                    position: absolute;
                    left: 85%;
                    background: transparentize($sc-theme-color-overlay, 1-0.175);
                    width: 100%;
                    top: 0;
                    text-align: left;
                    @include box-shadow(0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175));
                    transition: opacity 0.1s ease-out,
                    left 0.1s ease-in-out;

                    > li {
                        a {
                            @extend .header-menu-level2-anchor;

                        }
                    }
                }

                &:hover {
                    > a {
                        background-color: $sc-main-navigation-submenu-heading-bgr-hover-color;
                    }

                    > ul {
                        pointer-events: auto;
                        opacity: 1;
                        left: 100%;
                        //changing values for a quick roll-back
                        transition: opacity 0.3s ease-out,
                        left 0.1s ease-in-out;
                    }
                }
            }
        }
    }
}

//maxi menu opened
.open .header-menu-level-container {
    display: block;
    opacity: 1;
    visibility: visible;
    height: auto;
}

.categories-thumbnail {
    display: inline-block;

}

