// Edited for Posh Theme

.header-mini-cart {
	display: none;
	background: $sc-color-theme-content-background;
	width: 320px;
	padding: $sc-padding-lv4; // was $sc-base-padding * 4;
	position: absolute;
	z-index: 1000002;
	right: 0;
	box-shadow: 0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175);
	//border: 1px solid $sc-color-theme-border;

	&:before {
		position: absolute;
		top: -7px;
		right: 14px;
		display: inline-block;
		border-right: 7px solid transparent;
		border-bottom: 7px solid $sc-color-theme-content-background;
		border-left: 7px solid transparent;
		content: '';
	}

	&:after {
		position: absolute;
		top: -6px;
		right: 15px;
		display: inline-block;
		border-right: 6px solid transparent;
		border-bottom: 6px solid $sc-color-theme-content-background;
		border-left: 6px solid transparent;
		content: '';
	}

}

.header-mini-cart {
	.items-views-selected-option-picker{
		display: none;
	}
	.items-views-selected-option-label,
	.items-views-selected-option-color p {
		margin-bottom: 0;
	}
}

.open .header-mini-cart {
	display: block;
}

.header-mini-cart-menu-cart-legend{
	@extend .header-menu-cart-legend;
}

.header-mini-cart-container {
	max-height: 411px;
	overflow-y: auto;
	overflow-x: hidden;
	margin-bottom: $sc-margin-lv4; // was $sc-medium-margin;
