//header sidebar toggle
.header-sidebar-toggle-wrapper {
    margin-left: $sc-margin-lv2;

    @media(min-width: $screen-md-min) {
        display: none;
    }
}

.header-sidebar-toggle {
    width: $sc-target-size-mobile;
    text-align: center;
    background-color: transparent;
    width: 25px;
    height: 30px;
    margin-right: 15px;

    & span,
    & span:before,
    & span:after {
        cursor: pointer;
        border-radius: 1px;
        height: 2px;
        width: 25px;
        background-color: $sc-header-icon-color;
        position: absolute;
        display: block;
        content: '';
        transition: all 500ms ease-in-out;
    }

    & span:before {
        top: -8px;
    }

    & span:after {
        bottom: -8px;
    }
}

.header-sidebar-opened .header-sidebar-toggle {
    & span {
        background-color: transparent;
    }

    & span:before,
    & span:after {
        top: 0;
    }

    & span:before {
        transform: rotate(45deg);
    }

    & span:after {
        transform: rotate(-45deg);
    }
}

