///////////////////////////////////////////////////////////////
//header settings

.header-menu-settings-dropdown {
    display: none;
    position: absolute;
    z-index: 1001;
    background: $sc-neutral-shade-0;
    padding: $sc-padding-lv6;
    right: 0;
    box-shadow: 0 6px 12px transparentize($sc-theme-color-overlay, 1-0.175);
    border: 1px solid $sc-color-theme-border-light;
    width: 335px;

    &:before {
        position: absolute;
        top: -7px;
        right: 12px;
        display: inline-block;
        border-right: 7px solid transparent;
        border-bottom: 7px solid $sc-color-theme-border-light;
        border-left: 7px solid transparent;
        border-bottom-color: transparentize($sc-theme-color-overlay, 1-0.5);
        content: '';
    }

    &:after {
        position: absolute;
        top: -6px;
        right: 13px;
        display: inline-block;
        border-right: 6px solid transparent;
        border-bottom: 6px solid $sc-neutral-shade-0;
        border-left: 6px solid transparent;
        content: '';
    }
}

.open .header-menu-settings-dropdown {
    display: block;
}

.header-site-search {
    display: none;
    width: 100%;
    background: white;
    position: static;

    .site-search {
        background: white;
        position: absolute;
        top: 65px;
        left: 0;
        right: 0;
        bottom: -13px;
        padding: 13px 0;
        z-index: 1000000;
        @include box-shadow(0 3px 7px rgba(0, 0, 0, .3))
    }
}

