.home-slider-container {
    margin-bottom: $sc-margin-lv4;
    margin-left: 0;
    margin-right: 0;

    @extend .row;

    // This block is only added if the slide have captions
    .caption-on {
        //making the sliders visible/hidden individually
        .home-slide-caption-container {
            &.caption-display {
                display: block;
                @media (min-width: $screen-sm-min) {
                    display: flex;
                    display: -ms-flexbox;
                }
            }
        }
    }

    .home-image-slider {
        position: relative;

        .bx-wrapper {
            @media (min-width: $screen-sm-min) {
                margin-bottom: $sc-margin-lv4;
            }

            .bx-viewport {
                height: auto !important;
            }

            .home-slide-main-container .home-slide-image-container {
                overflow: hidden;
                height: auto;
                text-align: center;

                background-size: 0%;

                @media (min-width: $screen-sm-min) {
                    background-size: cover;
                    background-position: center center;
                }

                //slider image
                img {
                    margin: auto 0;
                    height: 100%;
                    min-width: auto;
                    max-width: 100%;

                    float: none;
                    width: 100% !important;
                    margin: 0 auto -6px !important;
                    min-height: 50px;
                    opacity: 1;

                    @media (min-width: $screen-sm-min) {
                        max-width: none;
                        min-width: 1200px;
                    }

                    @media (min-width: $screen-md-min) {
                        opacity: 0;
                    }

                    //class for emulate the background behaviour, controlling crop sides
                    &.backgrounded-left {
                        min-width: 1200px !important;
                        float: left;
                    }

                    &.backgrounded-right {
                        min-width: 1200px !important;
                        float: right;
                    }


                }

                &.use-image {
                    img {
                        min-width: auto;
                    }

                    @media (max-width: $screen-sm-min) {
                        background-image: none !important;
                    }
                    @media (min-width: $screen-md-min) {
                        img {
                            opacity: 0;
                        }
                    }
                }

                &.left {
                    @media (min-width: $screen-sm-min) {
                        background-image: none !important;
                        img {
                            min-width: 1200px !important;
                            float: right;
                            opacity: 1;
                        }
                    }
                }

                &.right {
                    @media (min-width: $screen-sm-min) {
                        background-image: none !important;
                        img {
                            min-width: 1200px !important;
                            float: left;
                            opacity: 1;
                        }
                    }
                }

                &.centered {
                    @media (min-width: $screen-sm-min) {
                        img {
                            min-width: 1200px !important;
                            float: left;
                            opacity: 0; //important for mantain the right height in the slider
                        }
                    }
                }

                //added for mobile image
                .home-slide-image-mobile {
                    display: none;
                }

                .hide-small {
                    display: block;
                }

                @media (max-width: $screen-xs-max) {
                    .hide-small {
                        display: none;
                    }
                    .home-slide-image-mobile {
                        display: block;
                    }
                }
            }

        }
    }


    // pager and controls -------

    .home-image-slider {
        .bx-controls {
            .bx-pager {
                padding-top: 15px;

                @media (min-width: $screen-md-min) {
                    padding-top: 0;
                    position: absolute;
                    width: 100%;
                    bottom: 0;
                }

                .bx-pager-item {
                    vertical-align: middle;
                }
            }
        }

    }

    .home-image-slider .bx-controls,
    .home-image-slider .bx-controls .bx-pager {
        margin-top: 0;
        @media (min-width: $screen-sm-min) {
            margin-top: $sc-margin-lv2;
        }
    }

    // Prev and Next arrows
    .home-gallery-prev-icon,
    .home-gallery-next-icon {
        display: $sc-home-carousel-icons-display;
        position: absolute;
        top: 50%;
        width: $sc-target-size-mobile;
        height: $sc-target-size-mobile;
        font-size: 30px;
        text-align: center;
        color: $sc-home-carousel-icons-color-desktop;
        margin-top: -20px;
        line-height: 40px;
        z-index: 10;
        @media (max-width: $screen-sm-min) {
            display: $sc-home-carousel-icons-display;
            color: $sc-home-carousel-icons-color-mobile;
            margin-top: -38.5px;
            background: rgba(0, 0, 0, 0.1);
            padding: 0 10px 5px;
        }
    }

    .home-gallery-prev-icon {
        left: 10px;
        @extend .icon-angle-left;
    }

    .home-gallery-next-icon {
        right: 10px;
        @extend .icon-angle-right;
    }

    // Pager specifics --------

    .bx-pager-link {
        color: $sc-home-carousel-icons-color-mobile !important;
        background-color: $sc-home-carousel-icons-color-mobile !important;
        width: 10px !important;
        height: 10px !important;
        border-radius: 100% !important;
        font-size: 0;
        text-indent: -9999px;

        @media (min-width: $screen-md-min) {
            color: $sc-home-carousel-icons-color-desktop !important;
            background-color: $sc-home-carousel-icons-color-desktop !important;
        }

        &.active {
            background-color: $sc-home-carousel-icons-color-mobile;

            @media (min-width: $screen-md-min) {
                background-color: $sc-home-carousel-icons-color-desktop !important;
            }
        }

    }


    // captions
    .home-slide-caption-container {
        width: 100%;
        padding: $sc-margin-lv4 $sc-margin-lv2;
        text-align: center;
        background: #FFF;
        pointer-events: none;
        display: none;

        @media (min-width: $screen-sm-min) {
            padding: 0;
            background-position-x: inherit;
            position: absolute;
            background: none;
            width: 100%;
            right: 0;
            bottom: 0;
            height: 100%;

            //Automatic vergical alignment
            justify-content: center;
            align-items: center;

            &.carousel-left-box,
            &.carousel-center-box,
            &.carousel-right-box {
                .home-slide-caption {
                    background: rgba($sc-home-cta-background-color-value, 0.8)
                }
            }

            // These classes can be assigned in the Carousel configuration
            // Larger viewports only
            &.carousel-left,
            &.carousel-left-box {
                text-align: left;
                justify-content: flex-start;

                .home-slide-caption {
                    margin-left: 0;
                }
            }
            &.carousel-center,
            &.carousel-center-box {
                text-align: center;
            }
            &.carousel-right,
            &.carousel-right-box {
                text-align: right;
                justify-content: flex-end;

                .home-slide-caption {
                    margin-right: 0;
                }
            }


        }

        .home-slide-caption {
            //title and description

            padding: $sc-padding-lv6 $sc-padding-lv2 $sc-padding-lv3 $sc-padding-lv2;

            @media (min-width: $screen-sm-min) {
                background: none;
                margin: auto;
                width: auto;
                max-width: 50%;

                padding: $sc-padding-lv8 70px $sc-padding-lv8 70px;

                &.left {
                    text-align: left;
                }
                &.right {
                    text-align: right;
                }
                &.centered {
                    text-align: center;
                }

            }

            @media (max-width: $screen-sm-min) {
                width: 100% !important;
            }

            .home-info-title {
                margin-bottom: $sc-margin-lv2;
                font-size: $sc-home-cta-title-size-mobile;
                color: $sc-home-cta-title-color;

