/* Promo Area */
.home-promo {
    display: flex; /* or inline-flex */
    margin: 0 0;
    flex-wrap: wrap;
    background-color: $sc-home-promo-background-color;
    color:$sc-color-secondary;

    & h2 {
        padding-bottom: $sc-padding-lv1;
        color:$sc-color-secondary;
    }

    & p {
        margin: 0 $sc-margin-lv5 $sc-margin-lv5 $sc-margin-lv5;
        color:white;
        font-size: $sc-font-size-l;
        line-height: $sc-font-size-xl + .5rem;
        letter-spacing: 1px;
        font-family: $sc-font-family-body;
        font-weight: 400;
        @media (min-width: $screen-sm-min) {
            margin: 0 0 $sc-margin-lv5 0;
            display: block;
            font-size: 1.3rem;
            line-height: $sc-font-size-xxl + .5rem;
