.address-list {
    h2{
        margin-bottom:$sc-margin-lv4;
    }
}

.address-details-info{
    margin-bottom:$sc-margin-lv2;
    min-height: 145px;
}
.address-details-container p {
    font-style: normal;
}
.address-details-container {
    padding: $sc-padding-lv3;
}
.address-details-container-multiselect-address-line {
    p {
        display: inline-block;
    }
}
.address-details-select-address {
    @extend .button-primary;
    @extend .button-medium;
    width: 100%;
}
.address-details-default-shipping-icon {
    @extend .icon-info;
    color: $sc-color-primary;
    vertical-align: top;
}
.address-details-address-name{
    font-style:normal;
    font-weight: $sc-font-weight-normal;
    font-size:$sc-font-size-m;
    word-wrap: break-word;
}

.address-details-actions{
    margin-top:$sc-margin-lv3;
    text-align: right;
}

.address-details-edit-address,
.address-details-remove-address {
    @extend .button-edit;
    margin-right: $sc-margin-lv2;
    cursor: pointer;
}
.address-details-edit-address {
    margin-bottom: $sc-margin-lv2;
}
.address-details-remove-address {
    margin-right: 0;
}
.address-details-change-address{
    @extend .button-tertiary;
    @extend .button-small;
    margin-top: $sc-margin-lv3;
    display:inline-block;
    @media (min-width: $screen-sm-min) {
        margin-top: 0;
    }
}

.address-details-remove-address {
    margin-bottom: 0;
}
.address-details-default-shipping,
.address-details-default-billing {
    font-size: $sc-font-size-s;
    margin-bottom: 0;
}

.address-details-container-multiselect-address-title{
    font-style:normal;
    text-transform:capitalize;
}

.address-details-container-multiselect-address-city{
    display:inline;
}

.address-details-container-multiselect-address-state{
    display:inline;
}

.address-details-container-multiselect-address-zip{
    display:inline;
}

//Modal AddressDetailsView
.global-views-modal-content .global-views-modal-content-body {
    .address-edit-fields {
        margin-top: 0;
    }
    .address-edit {
        background: none;
        padding: 0;
        margin: 0;
    }
}

.address-details-invalid-remove-msg {
    @extend .message-info;
}

.address-details-new-address {
    @extend .new-address-card-container;
    height: 235px;
}

.address-details-new-address-title {
    display: inline-block;
    margin-top: 60px;
    color: $sc-neutral-shade-300;
    font-size: 1.1rem;
    font-weight: bold;
}

.address-details-info-icon {
    @extend .icon-info;
    color: $sc-color-theme-700;
    font-size: $sc-font-size-xl;
    cursor: pointer;
}

.address-details-selector {
    @extend .address-card-container;
    cursor: pointer;
    border-bottom: 0px;
    padding: $sc-padding-lv3;
}

.address-details {
    @extend .address-card-container;
}

.address-details-selected {
    background-color: $sc-color-theme-content-background;
}

.address-details-action {
    @extend .button-link;
}

.address-details-selector-option {
    cursor: pointer;
    margin-bottom: 0;
}

.address-details-new-address-plus-icon {
    @extend .icon-plus;
    font-size: 2.5rem;
    color: $sc-color-theme-500;
}

.address-edit-fields {
  margin-bottom: $sc-margin-lv4;
  margin-top: $sc-margin-lv2;
  display: inline-block;
}

.address-edit-fields-required,
.address-edit-fields-group-label-required {
  @extend .input-required;
}
.address-edit-fields-group-input {
  @extend .input-large;
}
.address-edit-fields-group-input-zip {
  @extend .input-small;
}
.address-edit-fields-group-input-checkbox {
  font-weight: $sc-font-weight-normal;
}
.address-edit-fields-group-form-controls p {
  margin-bottom: $sc-margin-lv3;
}

.address-edit-fields-company-optional-label,
.address-edit-fields-phone-optional-label {
  @extend .input-optional;
  font-weight: $sc-font-weight-normal;
  display: inline;
}
.address-edit-fields-addr2-optional-label {
  @extend .input-optional;
}

.address-edit-fields-input-required{
  @extend .input-required;
}
.address-edit-fields-input-help{
  @extend .help-text;
}

.address-edit-fields-icon-question-sign{
  @extend .sc-tooltip;
}

.address-edit {
    @extend .box-column;
}
.address-edit-form-button-submit {
    @extend .button-primary;
    @extend .button-medium;
}
.address-edit-form-button-cancel {
    @extend .button-secondary;
    @extend .button-medium;
    margin-top: $sc-margin-lv2;

    @media (min-width: $screen-sm-min) {
        margin-left: $sc-margin-lv2;
        margin-top: 0;
    }
}
.address-edit-form-button-reset {
    @extend .button-tertiary;
    @extend .button-medium;
    margin-top: $sc-margin-lv2;
    margin-top: $sc-margin-lv2;

    @media (min-width: $screen-sm-min) {
        margin-left: $sc-margin-lv2;
        margin-top: 0;
    }
}

.address-list {
  @extend .address-edit;
}
.address-list-default-addresses,
.address-list-other-addresses {
  .backbone-collection-view-row {
    margin-bottom: $sc-margin-lv4;
  }
}
.address-list-button-container {
  margin: 0 0 $sc-margin-lv6 0;
}
.address-list-button-info-cards-new {
  @extend .button-info-cards-new;
}
.address-list-button-back {
  @extend .button-back;
}
.address-list-button-back-icon {
  @extend .button-back-icon;
}

{{#if isNewAddress}}
{{!--<a class="address-details-new-address" href="/addressbook/new" data-toggle="show-in-modal">
    <div class="address-details-new-address-title">
        <p><i class="address-details-new-address-plus-icon"></i></p>
        {{translate 'Add Address'}}
    </div>
</a>--}}
{{else}}
    {{#if showSelector}}
        <a class="address-details-selector {{#if isSelected}}address-details-selected{{/if}}" data-action="select" data-id="{{internalid}}">
            <input type="radio" name="address-options-{{manageOption}}" class="address-details-selector-option" data-id="{{internalid}}" value="{{internalid}}" {{#if isSelected}} checked {{/if}}>
            {{#if isSelected}}
                <b>{{translate 'Selected'}}</b>
            {{else}}
                {{translate 'Select'}}
            {{/if}}
        </a>
    {{/if}}

    <div class="address-details {{#if isSelected}}address-details-selected{{/if}}">
        <div class="address-details-container" data-id="{{internalid}}" {{#if isManageOptionsSpecified}} data-manage="{{manageOption}}" {{/if}}>
            <address>
                {{#if showMultiSelect}}
                    <div class="address-details-container-multiselect-address-selector">
                        <label class="address-details-container-multiselect-address-selector-checkbox">
                            <input type="checkbox" {{#if isAddressCheck}} 'checked' {{/if}} data-id="{{internalid}}" data-action="multi-select-address" />
                        </label>
                    </div>
                {{/if}}

                <div class="address-details-info{{#if showMultiSelect}} address-details-container-multiselect-address {{/if}}">
                    <p class="address-details-container-multiselect-address-title" data-name="company">
                        <b>{{title}}</b>
                    </p>
                    {{#if showCompanyAndFullName}}
                        <p class="address-details-container-multiselect-address-company" data-name="company">
                            {{company}}
                        </p>
                        <p class="address-details-container-multiselect-address-name" data-name="fullname">
                            {{fullname}}
                        </p>
                    {{else}}
                        {{#if showFullNameOnly}}
                            <span class="address-details-address-name" data-name="fullname">
                                {{fullname}}
                            </span>
                        {{/if}}
                    {{/if}}
                    <p class="address-details-container-multiselect-address-details-addr1" data-name="addr1">
                        {{addressLine1}}
                    </p>
                    {{#if showAddressLine1}}
                        <p class="address-details-container-multiselect-address-details-addr2" data-name="addr2">
                            {{addressLine2}}
                        </p>
                    {{/if}}
                    <p class="address-details-container-multiselect-address-line">
						<span class="address-details-container-multiselect-address-details-city" data-name="city">
                            {{city}}
                        </span>
                        {{#if showState}}
                            <span class="address-details-container-multiselect-address-details-state" data-name="state">
                                {{state}}
                            </span>
                        {{/if}}
                        <span class="address-details-container-multiselect-address-zip" data-name="zip">
                            {{zip}}
                        </span>
                    </p>
                    <p class="address-details-country" data-name="country">
                        {{country}}
                    </p>
                    <p class="address-details-phone" data-name="phone">
                        <a href="tel:{{phone}}">{{phone}}</a>
                    </p>
                </div>
            </address>

            {{#if showDefaultLabels}}
                {{#if isDefaultShippingAddress}}
                    <p class="address-details-default-shipping">
                        <i class="address-details-default-shipping-icon"></i>
                        {{translate 'Default Shipping Address'}}
                    </p>
                {{/if}}

                {{#if isDefaultBillingAddress}}
                    <p class="address-details-default-billing">
                        <i class="address-details-default-shipping-icon"></i>
                        {{translate 'Default Billing Address'}}
                    </p>
                {{/if}}
            {{/if}}

            {{#if showActionButtons}}
                <p class="address-details-actions">
                    <a class="address-details-action" href="/addressbook/{{internalid}}" data-action="edit-address" data-id="{{internalid}}" data-toggle="show-in-modal">
                        {{translate 'Edit'}}
                    </a>
                    {{#if showRemoveButton}}
                        <button class="address-details-action" data-action="remove" data-id="{{internalid}}" {{#if isInvalidAddressToRemove}}disabled{{/if}}>
                            {{translate 'Remove'}}
                        </button>
                        {{#if isInvalidAddressToRemove}}
                            <i class="address-details-info-icon" data-toggle="tooltip" title="{{translate 'You cannot remove this address because it was already assigned to a shipment'}}"></i>
                        {{/if}}
                    {{/if}}
                </p>
            {{/if}}

            {{#if showError}}
                <div data-type="address-details-error-container">
                    <div class="address-details-error-message">{{translate 'Invalid address, please provide the following:'}}</div>
                    {{#each invalidAttributes}}
                        <div class="address-details-error-message"> - {{this}} </div>
                    {{/each}}
                </div>
            {{/if}}
        </div>
    </div>
{{/if}}




{{!----
Use the following context variables when customizing this template:

model (Object)
model.internalid (String)
model.country (String)
model.state (String)
model.city (String)
model.zip (String)
model.addr1 (String)
model.addr2 (String)
model.phone (String)
model.fullname (String)
model.company (String)
model.isvalid (String)
internalid (String)
isManageOptionsSpecified (Boolean)
isAddressCheck (Boolean)
title (String)
showCompanyAndFullName (Boolean)
company (String)
fullname (String)
showFullNameOnly (Boolean)
addressLine1 (String)
showAddressLine1 (Boolean)
addressLine2 (String)
city (String)
showState (Boolean)
state (String)
zip (String)
country (String)
phone (String)
showDefaultLabels (Boolean)
isDefaultShippingAddress (Boolean)
isDefaultBillingAddress (Boolean)
showSelectionButton (Boolean)
isASelectMessageSpecified (Boolean)
showActionButtons (Boolean)
showChangeButton (Boolean)
showRemoveButton (Boolean)
invalidAttributes (Array)
isInvalidAddressToRemove (Boolean)
manageOption (String)
showError (Boolean)
showMultiSelect (Boolean)
selectMessage (String)

----}}

