/* Edited for Posh Theme */

.backbone-collection-view-row{
    //@extend .row;
    display: flex;
    flex-wrap: wrap;
    padding: 0px;
}

[data-validation-error] i.global-views-star-rating-empty{
	@extend .global-views-star-rating-error;
}

[data-validation-error] i.global-views-star-rating-empty-pegs{
