.balance-button-back {
	@extend .button-back;
}

.balance-button-back-icon {
	@extend .button-back-icon;
}

.balance-content {
	@extend .box-column;
}

.balance-billing-header {
	margin-bottom: $sc-margin-lv2;
	@media (min-width: $screen-sm-min) {
		margin-bottom: $sc-margin-lv6;
	}
}

.balance-indicator {
	@extend .information-box;
	margin-bottom: $sc-margin-lv5;
}

.balance-indicator-body {
	@extend .information-box-body;
}

.balance-indicator-summary {
	@extend .information-box-summary;
}

.balance-indicator-title {
	margin-bottom: $sc-margin-lv4;
}

.balance-indicator-title-value {
	@extend .sc-value;
	font-weight: $sc-font-weight-bold;
}

.balance-indicator-bar {
	width: 100%;
	background-color: $sc-color-theme-background;
	height: 35px;
	margin-bottom: $sc-margin-lv6;
}
.balance-indicator-bar-progress {
	height: 100%;
	width: 0;
	background-color: $color-balance-progress-bar;
}

.balance-indicator-details {
	@extend .row;
}

.balance-indicator-details-outstanding-balance {
	@extend .col-sm-6;
	margin-bottom: $sc-margin-lv3;
}

%balance-reference-color {
	width: 15px;
	height: 15px;
	display: block;
	float: left;
	margin-right: $sc-margin-lv3;
	margin-top: $sc-margin-lv1;
}

.balance-indicator-details-outstanding-reference {
	@extend %balance-reference-color;
	background-color: $color-balance-progress-bar;
}

.balance-indicator-details-outstanding-label {
	@extend .sc-label;
}
.balance-indicator-details-outstanding-value {
	@extend .sc-value;
}

.balance-indicator-details-available-credit {
	@extend .col-sm-6;
}

.balance-indicator-details-available-credit-reference {
	@extend %balance-reference-color;
	background-color: $sc-color-theme-background;
}

.balance-indicator-details-available-credit-label {
	@extend .sc-label;
}

.balance-indicator-details-available-credit-value {
	@extend .sc-value;
}

.balance-credit-and-account {
	@extend .row;
}

.balance-summary-credits {
	@extend .col-sm-6;
	margin-bottom: $sc-margin-lv4;
}

.balance-summary-credits-card {
	@extend .information-box;
}

.balance-summary-credits-body {
	@extend .information-box-body;
}

.balance-summary-credits-title {
	font-weight: $sc-font-weight-bold;
	margin-bottom: $sc-margin-lv3;
}

.balance-summary-credits-deposits {
	margin-bottom: $sc-margin-lv3;
}

.balance-summary-credits-deposits-label {
	@extend .sc-label;
}

.balance-summary-credits-deposits-value {
	@extend .sc-value;
	float: right;
}

.balance-summary-credits-credit-memos-label {
	@extend .sc-label;
}

.balance-summary-credits-credit-memos-value {
	@extend .sc-value;
	float: right;
}

.balance-summary-account-details {
	@extend .col-sm-6;
}

.balance-summary-account-details-card {
	@extend .information-box;
}

.balance-summary-account-details-body {
	@extend .information-box-body;
}

.balance-summary-account-details-title {
	margin-bottom: $sc-margin-lv3;
	font-weight: $sc-font-weight-bold;
}
.balance-summary-account-terms {
	margin-bottom: $sc-margin-lv3;
}
.balance-summary-account-terms-label {
	@extend .sc-label;
}
.balance-summary-account-terms-value {
	@extend .sc-value;
	float: right;
}

.balance-summary-account-currency-label {
	@extend .sc-label;
}
.balance-summary-account-currency-value {
	@extend .sc-value;
	float: right;
}
.balance-actions {
	@extend .row;
	@media (min-width: $screen-sm-min) {
		float: left;
		width:100%;
	}
}

.balance-actions-proceed {
	margin-bottom: $sc-margin-lv2;
	display:block;
	padding:0 $sc-padding-lv1  * 3;
	@media (min-width: $screen-sm-min) {
		padding:0 $sc-padding-lv1 0 $sc-padding-lv1  * 3;
		display:inline;
	}
}

.balance-actions-print {
	display:block;
	padding:0 $sc-padding-lv1  * 3;
	@media (min-width: $screen-sm-min) {
		display:inline;
		padding:0 $sc-padding-lv1  * 3 0 $sc-padding-lv1;
	}
}

.balance-continue-button {
	@extend .button-primary;
	@extend .button-medium;
}

.balance-print-button {
	@extend .button-secondary;
	@extend .button-medium;
}

// Atoms
//
// Atoms are the abstract basic building blocks of matter.
// Applied to web interfaces, atoms are the HTML tags, such as a form label, an input or a button.
// They cant be broken down any further.
//
// Styleguide 2.

// Buttons
//
// Buttons are tools that enable users to perform a task. Some tasks are more important than others, and creating effective buttons with the right combination of size, color, position and wording is required.
// Good design can subconsciously communicate priority to users. Using the right combination of these components buttons will guide the user through the tasks required to achieve the main purpose or use case of a website.
//
// Styleguide 2.1.

@import "atoms/buttons.scss";

// Copy
//
// Copy refers to the text that appears within a user interface. Users rarely read every word on a page.
// Thus, creating visual hierarchy is essential for users to understand the information. It also impacts how users scan a website.
// To make copy scannable you should provide good contrast between the different elements.
// This creates different focus points and guides the user through the content.
// For example, highlighting links and bolding important information creates scannable focus-points.
// Poor contrast results in users getting lost in the content and causes them to scan more slowly.
//
// Styleguide 2.2.

@import "atoms/typography.scss";

// Form elements
//
// Web forms establish or enhance a relationship between the user and the organization.
// They establish a dialogue and, therefore, represent a two way communication between two parties. Form elements enable this communication.
//
// Styleguide 2.3.

@import "atoms/forms.scss";

// Icons
//
// Icons are a visual representation of an object, action, or idea that communicate meaning in a graphical way.
//
// Styleguide 2.4.

@import "atoms/icons.scss";

// Messages
//
// When users are interacting with an application they often expect feedback. This feedback can have different tones or communicate different types of information.
// For example, the application may display confirmation messages if a form is submitted successfully or error message if there were problems.
//
// Styleguide 2.5.

@import "atoms/messages.scss";

// Purchase controls
//
// Purchase controls are a set of elements that enable the user to filter and narrow a list of items.
// For example, while shopping, a user can search according to multiple criteria.
// Purchase Controls enable the user to select the desired item options and add it to their shopping cart or wishlist.
//
// Styleguide 2.6.

@import "atoms/purchase-controls.scss";

// Menu and Navigation
//
// Menu and Navigation atoms are used for navigating the website.
// A well designed navigation allows users to easily understand where they are at in the website and where they can go to.
//
// Styleguide 2.7.

@import "atoms/breadcrumb.scss";
@import "atoms/stepped-process.scss";

@import "atoms/thumbnails.scss";

@import "base/mixins.scss";
@import "base/reset.scss";
@import "base/base.scss";
@import "base/bootstrap-overrides.scss";
@import "base/columns.scss";
@import "base/lists.scss";
@import "base/spacing.scss";
@import "base/sticky-button.scss";

// Molecules
//
// Molecules are the smallest fundamental units of compound made up of two or more atoms held together to form a simple piece of functionality.
// They are simple combinations built to reuse, but not necessarily have meaning on their own without further context.
// Molecules often serve as the backbone for a more complex system.
//
// Styleguide 3.

@import "molecules/accordions.scss";
@import "molecules/flyout.scss";
@import "molecules/image-gallery.scss";
@import "molecules/information_cards.scss";
@import "molecules/information-box.scss";
@import "molecules/item-cells.scss";
@import "molecules/list-header.scss";
@import "molecules/pagination.scss";
@import "molecules/pusher.scss";
@import "molecules/search.scss";
@import "molecules/summary_box.scss";
@import "molecules/tabs.scss";
@import "molecules/_quantity-input.scss";

@import "posh-theme/typography.scss";
@import "posh-theme/colors.scss";
@import "posh-theme/buttons.scss";
@import "posh-theme/header.scss";
@import "posh-theme/navigation.scss";
@import "posh-theme/home.scss";
@import "posh-theme/footer.scss";
@import "posh-theme/plp.scss";
@import "posh-theme/grouping.scss";

// Style
//
// SuiteCommerce Advanced style is focused on a minimalist use of simple elements, typography and flat colors. This allows the design to be more streamlined and efficient. Making it easier to convey information while still looking visually appealing and approachable.
// With minimal design, elements are able to load faster and resize easily and still look sharp on high-definition screens. Additionally, elements can be responsive to different browsers across different devices.
//
// Styleguide 1.

@import "variables/colors.scss";
@import "variables/typography.scss";
@import "variables/variables.scss";
@import "variables/spacing.scss";

/* Edited for Posh Theme */

// Breadcrumb
//
// Breadcrumbs allow users to know the current location on a hierarchically structured website.
// They are commonly used to navigate back to a higher level of hierarchy.
//
// Markup:
// <div class="breadcrumb">
//   <ul>
//     <li class="breadcrumb-item"><a href="#">Home</a></li> <span class="breadcrumb-divider">/</span>
//     <li class="breadcrumb-item"><a href="#">Category</a></li> <span class="breadcrumb-divider">/</span>
//     <li class="breadcrumb-item">Item</li>
//   </ul>
// </div>
//
// Styleguide 2.7.1

.breadcrumb {
	display: none;

	@media(min-width: $screen-md-min) {
		clear: both;
		// margin-bottom: $sc-margin-lv6;
		display: block;
	}
}

.breadcrumb-item {
    display: inline;

    a {
        color: $sc-color-theme;
        text-transform: uppercase;
    }
}

.breadcrumb-item-active {
	@extend .breadcrumb-item;
	color: $sc-color-theme-700;
}

.breadcrumb-divider {
	display: inline;
	padding: 0 $sc-padding-lv1;
}

.breadcrumb-divider-icon {
	@extend .icon-angle-right;
	font-size: $sc-font-size-m;
}

/* Edited for Posh Theme */

// Primary buttons
//
// The primary action is generally the action that completes a task or desired action.
// Primary buttons use bright and use saturated colors to provide stronger visual weight that catches the users attention to avoid confusion and competing with secondary actions.
//
// Markup:
// <button class="button-primary button-medium">Button Primary</button>
//
// Styleguide 2.1.1

.button-primary {
	background: $sc-button-primary-background-color;
	color: $sc-button-primary-text-color;
	font-weight: $sc-font-weight-normal;
	border: 1px solid $sc-button-primary-border-color;
	border-radius: $sc-button-primary-border-radius;
	text-transform: $sc-button-primary-text-transform;
	display: inline-block;

	&:hover {
		color: $sc-button-primary-hover-text-color;
		background: $sc-button-primary-hover-background-color;
		border-color: $sc-button-primary-hover-border-color;
		text-decoration: none;
	}

	&:active, &:focus {
		color: $sc-button-primary-active-text-color;
		background: $sc-button-primary-active-background-color;
		border-color: $sc-button-primary-active-border-color;
	}

	&:disabled, &-disabled {
		cursor: default;
		pointer-events: none;
		opacity: 0.6;
	}
}

// Secondary buttons
//
// A secondary action is not as important as a primary action. You should give secondary buttons less visual weight than primary actions.
// Less saturated colors are used on secondary buttons resulting in distinguishable buttons that don't stand out as much as primary buttons.
//
// Markup:
// <button class="button-secondary button-medium">Button Secondary</button>
//
// Styleguide 2.1.2

.button-secondary {
	background: $sc-button-secondary-background-color;
	color: $sc-button-secondary-text-color;
	font-weight: $sc-font-weight-normal;
	border: 1px solid $sc-button-secondary-border-color;
	border-radius: $sc-button-secondary-border-radius;
	text-transform: $sc-button-secondary-text-transform;
	display: inline-block;

	&:hover {
		color: $sc-button-secondary-hover-text-color;
		background: $sc-button-secondary-hover-background-color;
		border-color: $sc-button-secondary-hover-border-color;
		text-decoration: none;
	}

	&:active, &:focus {
		color: $sc-button-secondary-active-text-color;
		background: $sc-button-secondary-active-background-color;
		border-color: $sc-button-secondary-active-border-color;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

// Tertiary buttons
//
// Tertiary buttons are either not related to the main task, or the least desired actions.
// Visually tertiary buttons blend in to the interface background avoiding to distract the user from their primary task.
//
// Markup:
// <button class="button-tertiary button-medium">Button Tertiary</button>
//
// Styleguide 2.1.3

.button-tertiary {
	background: $sc-button-tertiary-background-color;
	color: $sc-button-tertiary-text-color;
	font-weight: $sc-font-weight-normal;
	border: 1px solid $sc-button-tertiary-border-color;
	border-radius: $sc-button-tertiary-border-radius;
	text-transform: $sc-button-tertiary-text-transform;
	display: inline-block;

	&:hover {
		color: $sc-button-tertiary-hover-text-color;
		background: $sc-button-tertiary-hover-background-color;
		border-color: $sc-button-tertiary-hover-border-color;
		text-decoration: none;
	}

	&:active, &:focus {
		color: $sc-button-tertiary-active-text-color;
		background: $sc-button-tertiary-active-background-color;
		border-color: $sc-button-tertiary-active-border-color;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

// Link buttons
//
// Markup:
// <button class="button-link">Button link</button>
//
// Styleguide 2.1.4

.button-link {
	background: none;
	color: $sc-color-link;
	font-weight: $sc-font-weight-normal;
	border: none;
	display: inline-block;
	padding: $sc-padding-lv2 0;
	margin-right: $sc-margin-lv2;

	&:hover {
		color: $sc-color-link-hover;
	}

	&:focus {
		color: $sc-color-link-hover;
	}

	&:active {
		color: $sc-color-link-hover;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

// Button sizes
//
// There are three button sizes, which can be combined with Primary, Secondary and Tertiary buttons to create different hierarchies.
//
// .button-small - Small buttons are used for optional or non related tasks. For example, a button to remove an item on the cart is optional and its not the main task. Small buttons do not scale for small devices, they remain small.
// .button-medium - Medium buttons are used for most actions within a page. Both medium and large buttons are width 100% on small devices.
// .button-large -  Use large buttons when they are the primary goal on a page. For example in a form or Product details page. Large buttons scale to 100% wide on small devices.
//
// Markup:
// <button class="button-small button-primary">Button</button>
// <button class="button-medium button-primary">Button</button>
// <button class="button-large button-primary">Button</button>
//
// Styleguide 2.1.5

.button-xlarge {
	width: $sc-button-large-mobile-width;
	padding: $sc-button-xlarge-padding-v $sc-button-xlarge-padding-h;
	font-size: $sc-button-xlarge-font-size;
	line-height: $sc-button-xlarge-line-height;
	letter-spacing: $sc-button-xlarge-letter-spacing;
	text-align: center;

	@media(min-width: $screen-sm-min) {
		width: $sc-button-large-desktop-width;
	}
}

.button-large {
	width: $sc-button-large-mobile-width;
	padding: $sc-button-large-padding-v $sc-button-large-padding-h;
	font-size: $sc-button-large-font-size;
	line-height: $sc-button-large-line-height;
	letter-spacing: $sc-button-large-letter-spacing;
	text-align: center;

	@media(min-width: $screen-sm-min) {
		width: $sc-button-large-desktop-width;
	}
}

.button-medium {
	width: $sc-button-medium-mobile-width;
	padding: $sc-button-medium-padding-v $sc-button-medium-padding-h;
	font-size: $sc-button-medium-font-size;
	line-height: $sc-button-medium-line-height;
	letter-spacing: $sc-button-medium-letter-spacing;
	text-align: center;

	@media(min-width: $screen-sm-min) {
		width: $sc-button-medium-desktop-width;
	}
}

.button-small {
	width: $sc-button-small-mobile-width;
	padding: $sc-button-small-padding-v $sc-button-small-padding-h;
	font-size: $sc-button-small-font-size;
	line-height: $sc-button-small-line-height;
	letter-spacing: $sc-button-small-letter-spacing;
	text-align: center;

	@media(min-width: $screen-sm-min) {
		width: $sc-button-small-desktop-width;
	}
}

// Push button primary (mobile only)
//
// Push buttons are used to open push panels that appear in smaller screens which hide information from the user until they request for it.
// Push buttons have a chevron indicating that the user is navigating somewhere rather than performing an action.
// Push buttons have a title and sometimes hint information related to the information in the push panel.
//
// Markup:
// <button class="push-button-primary">Push Button Primary <i></i></button>
//
// Styleguide 2.1.6

.push-button-primary {
	width: 100%;
	color: $sc-neutral-shade-0;
	background: $sc-color-secondary;
	letter-spacing: 1px;
	font-size: $sc-font-size-l;
	padding: $sc-padding-lv3;
	text-align:left;

	&:active {
		background: $sc-button-secondary-hover-background-color;
	}

	> i {
		@extend .fa;
		@extend %fa-angle-right;
		font-size: $sc-font-size-xxl;
		float: right;
		color: $sc-neutral-shade-0;
	}

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

// Push button secondary (mobile only)
//
// Markup:
// <button class="push-button-secondary">Push Button Secondary <i></i></button>
//
// Styleguide 2.1.7

.push-button-secondary {
	width: 100%;
	color: $sc-color-copy-dark;
	background: $sc-neutral-shade-0;
	font-size: $sc-font-size-s;
	padding: $sc-padding-lv3;
	border: 1px solid $sc-color-theme-border-light;
	text-align: left;
	position: relative;

	&:active {
		background: $sc-color-theme-background;
	}

	> i {
		@extend .fa;
		@extend %fa-angle-right;
		font-size: $sc-font-size-xxl;
		float: right;
		color: $sc-color-copy-dark;
		position: absolute;
		right: 15px;
		top: 50%;
		height: 26px;
		margin-top: -12px;
	}

	span {
		@extend p;
		margin-top: $sc-margin-lv3;
		color: $sc-neutral-shade-700;
		padding-right: $sc-padding-lv3;
		text-overflow: ellipsis;
		overflow: hidden;
	}

	.global-views-star-rating {
		display: inline-block;
		vertical-align: middle;
		float: none;
	}

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

// Push button with description (mobile only)
//
// Push buttons can contain hint information related to what is inside the push panel.
// This might help the user understand what sort of information to expect when pressing the button.
//
// Markup:
// <button class="push-button-description">
//   Push button with description <i></i>
//   <p>
//     Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi elementum id mi nec finibus...
//   </p>
// </button>
//
// Styleguide 2.1.8

.push-button-description {
	@extend .push-button-secondary;

	p {
		margin: $sc-margin-lv3 $sc-margin-lv3 0 0;
		font-size: $sc-font-size-s;
		color: $sc-color-copy;
	}
}

// Split buttons
//
// Split buttons are used when multiple actions can be performed.
// The most relevant or common action a user can perform is placed on the button while other actions are placed inside a dropdown menu.
// There are two actionable areas on a split button, the primary action and the dropdown menu. Each has their own independent hover and focus status.
// Split buttons can be combined with any of the available button sizes.
//
// Markup:
// <div class="split-button">
//   <button type="button" class="button-tertiary button-small split-button-action">
//     Primary Action
//   </button>
//   <button type="button" class="button-tertiary button-small split-button-toggle" data-toggle="dropdown">
//     <i class="split-button-toggle-icon"></i>
//   </button>
//   <ul class="split-button-dropdown" role="menu">
//     <li><a href="#">Primary action</a></li>
//     <li><a href="#">Secondary action</a></li>
//     <li><a href="#">Third action</a></li>
//     <li class="split-button-item-divider"><a href="#">Separate link</a></li>
//   </ul>
// </div>
//
// Styleguide 2.1.9

.split-button {
	position: relative;
	display: inline-block;
	vertical-align: middle;

	.split-button-action {
		padding: 13px 16px;
		vertical-align: top;
		line-height: 14px;
		border-top-right-radius: 0;
		border-bottom-right-radius: 0;
		outline: none;
	}

	.split-button-toggle {
		border-left: 0;
		margin-left: -4px;
		border-top-left-radius: 0;
		border-bottom-left-radius: 0;
		outline: none;

		.split-button-toggle-icon {
			@extend .icon-angle-down;
		}

		&:hover {
			.split-button-toggle-icon {
				color: white;
			}
		}
	}

	&-dropdown {
		display:none;
		position: absolute;
		top: 100%;
		right: 0;
		z-index: 1000;
		min-width: 160px;
		text-align: left;
		list-style: none;
		background-color: $sc-neutral-shade-0;
		-webkit-background-clip: padding-box;
		background-clip: padding-box;
		border: 1px solid $sc-color-theme-border;
		border-radius: 0;
		color: $sc-color-secondary;
		margin-top: $sc-margin-lv1;
		font-size: 14px;
		@include box-shadow(0 6px 6px transparentize($sc-theme-color-overlay, 1-0.175));

		a {
			display: block;
			padding: $sc-padding-lv2;
			color: $sc-color-secondary;

			&:hover {
				color: $sc-color-link;
			}
		}

		.split-button-item-divider {
			border-top: 1px solid $sc-color-theme-border;
		}
	}
}

.open .split-button-toggle {
	@extend .button-secondary:hover;

	i.split-button-toggle-icon {
		color: $sc-neutral-shade-0;
	}
}

.open .split-button-dropdown {
	display: inline-block;
}

// Pill buttons
//
// Pills are a series of buttons grouped together on a single line.
// Button groups are used for buttons that control the view of an area and only one of them can be selected at a time.
// Make sure to provide visual contrast to the active button.
//
// Markup:
// <div class="pill-button-group">
//   <a class="pill-button button-medium pill-button-active">Button 1</a>
//   <a class="pill-button button-medium">Button 2</a>
//   <a class="pill-button button-medium">Button 3</a>
// </div>
//
// Styleguide 2.1.10

.pill-button {
	display: table-cell;
	border: 1px solid $sc-color-theme-border;
	color: $sc-color-secondary;
	width: auto;
	vertical-align: middle;
	line-height: 1;
	background: $sc-neutral-shade-0;

	&:hover {
		background-color: $sc-color-secondary;
		border: 1px solid $sc-color-secondary;
		color: $sc-neutral-shade-0;
	}

	&-active {
		background-color: $sc-color-secondary;
		border: 1px solid $sc-color-secondary;
		color: white;
	}
}

.pill-button-group {
	.pill-button:first-child,
	.pill-button-active:first-child {
		border-radius: $sc-border-radius 0 0 $sc-border-radius;
		border-right: none;
	}

	.pill-button:last-child,
	.pill-button-active:last-child {
		border-radius: 0 $sc-border-radius $sc-border-radius 0;
		border-left: none;
	}
}

// Icon buttons
//
// Markup:
// <button class="icon-button icon-button-primary"> <i class="fa fa-shopping-cart"></i></button>
// <button class="icon-button icon-button-secondary"> <i class="fa fa-search"></i></button>
// <button class="icon-button icon-button-controls"> <i class="fa fa-bars"></i></button>
// <button class="icon-button icon-button-controls active"> <i class="fa fa-bars"></i></button>
//
// Styleguide 2.1.11

.icon-button {
	// background: $sc-color-primary;
	width: $sc-target-size;
	height: $sc-target-size;
	overflow: hidden;

	i {
		color: $sc-neutral-shade-0;
		width: $sc-target-size;
		height: $sc-target-size;
		font-size: $sc-font-size-xxl;
		line-height: $sc-target-size;

		@media(min-width: $screen-md-min) {
			font-size: $sc-font-size-xl;
		}
	}

	&.icon-button-primary {
		background: $sc-color-primary;

		&:hover {
			background: $sc-button-primary-hover-background-color;
		}

		&:active {
			background: $sc-button-primary-active-background-color;
		}
	}

	&.icon-button-secondary {
		background: $sc-color-secondary;

		&:hover {
			background: $sc-button-secondary-hover-background-color;
		}

		&:active {
			background: $sc-button-secondary-active-background-color;
		}
	}
}

.icon-button-controls {
	color: $sc-neutral-shade-300;
	text-align:center;
	display:inline-block;
	@extend .icon-button;

	&.active, .active{
		color: $sc-color-secondary;

		i {
			color: $sc-color-secondary;
		}
	}

	i {
		color: $sc-neutral-shade-300;
	}
}

.button-back {
	display:block;
	margin-bottom: $sc-margin-lv3;
	padding-top: $sc-padding-lv1;
	padding-bottom: $sc-padding-lv1;

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.button-back-icon {
	@extend .icon-base;
	@extend %fa-angle-left;
	margin-right: $sc-margin-lv1;
}

.button-positioning-stack2next {
	margin-top: $sc-margin-lv2;

	@media (min-width: $screen-sm-min) {
		margin-top: 0;
		margin-left: $sc-margin-lv1;
	}
}

.button-positioning-next2stack {
	display: inline-block;
	margin-left: $sc-margin-lv1 ;

	@media (min-width: $screen-sm-min) {
		display: block;
		margin-top: $sc-margin-lv2;
		margin-left: 0;
	}
}

.button-generic {
	width: 100%;
	text-align: center;
	line-height: 1;
	display: block; // Fix ie width 100%
	position: relative; // Fix ie width 100%
}

.button-list-header {
	height: $sc-target-size;
	line-height: $sc-target-size;
	min-width: $sc-target-size;

	padding: 0 $sc-padding-lv4;
	letter-spacing:0.5px;
	font-size: $sc-font-size-m;
	overflow: hidden;
}

.button-list-header-shrink {
	padding-left: $sc-padding-lv2;
	padding-right: $sc-padding-lv2;

	@media (min-width: $screen-sm-min){
		padding-left: $sc-padding-lv4;
		padding-right: $sc-padding-lv4;
	}
}

// Labels
//
// Clear labels let the user know which data is being requested in each field.
// An optimal placement of labels would be above the input. This helps the user to scan through the form and increase completion rate.
// Labels also serve to distinguish between input fields that are required vs those that are optional.
//
// Markup:
// <label>Label</label><br>
// <label>Label <small>(optional)</small></label><br>
// <label>Required <small class="required">*</small></label><br>
//
// Styleguide 2.3.1

label {
	display:inline-block;
	margin-bottom: $sc-margin-lv2;
	font-size: $sc-font-size-m;
	color: $sc-color-copy-dark;

	small {
		font-size: $sc-font-size-s;

		&.required {
			color: $sc-color-copy-light;
		}
	}
}

// TODO: This definition should be deleted. Previously used to indicate required fields.
.input-required {
	font-size: $sc-font-size-s;
	color: $sc-color-copy-light;
}

// TODO: This definition should be deleted. Previously used to indicate optional fields.
.input-optional {
	font-size: $sc-font-size-s;
}

// Form input fields
//
// Text fields are the most common type of fields on forms. They enable the user to enter data.
//
// Markup:
// <label for="sample-field">Sample field:</label>
// <input type="text" name="sample-field" /><br>
// <label for="sample-field-disabled">Sample field disabled:</label>
// <input type="text" name="sample-field-disabled" disabled /><br>
//
// Styleguide 2.3.2

input {
	padding: $sc-padding-lv2;
	display: block;
	color: $sc-color-copy;
	font-size: $sc-font-size-m;
	border: 1px solid $sc-color-theme-border;
	width: 100%;
	margin-bottom: $sc-margin-lv2;

	&:disabled {
		border-color: $sc-color-theme-border-light;
		background-color: $sc-color-theme-background;
	}
}

input[type="text"],
input[type="password"],
input[type="email"],
input[type="tel"],
input[type="search"],
input[type="date"],
input[type="number"]
{
	@include appearance(none);
	@include placeholder {
		color: $sc-color-copy-light;
	}
	@include border-radius(0);
}

input[type="checkbox"],
input[type="radio"] {
	display: inline-block;
	width: auto;
	margin-right: $sc-margin-lv2;
}

input[type="date"]
{
	-webkit-appearance: none;
}

input[type="date"]::-webkit-outer-spin-button, /* Removes arrows */
input[type="date"]::-webkit-inner-spin-button, /* Removes arrows */
input[type="date"]::-webkit-clear-button, /* Removes blue cross */
input[type="date"]::-webkit-calendar-picker-indicator /* Removes arrow calendar */ {
  -webkit-appearance: none;
  display: none;
}

input.input-large, select.input-large {
	width: 100%;

	@media (min-width: $screen-md-min) {
		width: auto;
		min-width: 300px;
	}
}

input.input-medium, select.input-medium {
	width: 100%;
	min-width: 200px;

	@media (min-width: $screen-sm-min) {
		width: auto;
		min-width: 300px;
	}
}

input.input-small, select.input-small {
	min-width: 80px;
}

// Form select fields
//
// Select menus are used when multiple pre-populated options are available for the user to select.
// Use select menus when you have more than 3 options for users to select from otherwise use radio buttons.
//
// Markup:
// <label for="sample-select">Sample select:</label>
// <select name="sample-select">
//   <option selected disabled>choose one</option>
//   <option value="1">1</option>
//   <option value="2">2</option>
//   <option value="3">3</option>
// </select>
// <label for="sample-select">Sample select disabled:</label>
// <select name="sample-select" disabled>
//   <option selected disabled>choose one</option>
//   <option value="1">1</option>
//   <option value="2">2</option>
//   <option value="3">3</option>
// </select>
//
// Styleguide 2.3.3

select {
	@include angle-down-background($sc-color-theme-700); // TODO: Mixin definition should be changed in order to use chevron icon instead an image background
	@include appearance(none);
	@include border-radius(0);
	display: block;
	border: 1px solid $sc-color-theme-border;

	padding: $sc-padding-lv2;
	padding-right: $sc-padding-lv6;
	margin-bottom: $sc-margin-lv2;

	background-color: $sc-color-theme-content-background;
	background-repeat: no-repeat;
	background-size: $sc-navigation-icon-size;
	background-position: 100% 100%;

	color: $sc-neutral-shade-500;
	font-size: $sc-font-size-m;

	&:disabled {
		border:1px solid $sc-color-theme-border-light;
		background-color: $sc-color-theme-background;
	}
}

// Select with icon
//
// Markup:
// <div class="selector">
// 	<span class="selector-icon">
// 		<i class="fa fa-globe"></i>
// 	</span>
// 	<select class="selector-select">
// 		<option disabled selected>Choose one</option>
// 		<option value="1">1</option>
// 		<option value="2">2</option>
// 		<option value="3">3</option>
// 	</select>
// </div>
//
// Styleguide 2.3.4

.selector {
	display: block;
	margin: $sc-margin-lv3;
	float: left;
	@media(min-width: $screen-md-min) {
		margin: 0px;
	}

	&-select {
		width: 175px;
		border: 1px solid $sc-color-theme-border-light;
		border-left: none;
		height: $sc-target-size;
		float: right;
		@media(min-width: $screen-md-min) {
			width: 232px;
		}
	}

	&-icon {
	  width: 40px;
	  background: $sc-color-theme-background;
	  color: $sc-color-theme-700;
	  font-size: $sc-font-size-xxl;
	  border: 1px solid $sc-color-theme-border-light;
	  border-right: none;
	  float: left;
	  line-height: $sc-target-size;
	  height: $sc-target-size;
	  text-align: center;
	}
}

@media screen and (min-width:0\0) {
	//ie dont understand appearance on selects. so hide background.
	select[class] {
		background-image:none;
		padding-right: $sc-padding-lv2;
	}
}

// Input Type Textarea
//
// Text areas are used for multi-line input fields, typically used if the user needs to enter larger data like explanations or messages.
//
// Markup:
// <textarea>Textarea</textarea>
//
// Styleguide 2.3.5

textarea {
	border:1px solid $sc-color-theme-border;
	padding: $sc-margin-lv2;
	font-size: $sc-font-size-m;
	color: $sc-color-copy-dark;
	width:100%;
	min-height: 105px;
	@include appearance(none);
	@include border-radius(0);

	@media (min-width: $screen-sm-min){
		width: auto;
		min-width: 300px;
	}
}

// Input Type Checkbox
//
// A group of Check boxes are used when multiple choices are possible within a list of options. Selecting one option does not deselect the others.
// Use a stand-alone check box if there is a single option that the user can turn on or off.
// Both label and box are tappable, making it easier for the user to check the box.
//
// Markup:
// <div class="checkbox">
//   <input id="my-checkbox" type="checkbox">
//   <label for="my-checkbox"> Check me out</label>
// </div>
// <br>
// <div class="checkbox">
//   <input id="my-checkbox-1-disabled" type="checkbox" checked disabled>
//   <label for="my-checkbox-1-disabled"> This option is disabled</label>
//   <br>
//   <input id="my-checkbox-2-disabled" type="checkbox" disabled>
//   <label for="my-checkbox-2-disabled"> This option is disabled</label>
// </div>
//
// Styleguide 2.3.6

.checkbox {
	display: inline;

	input[type="checkbox"] {
		display: none;
	}

	input[type="checkbox"] + label::before {
		font-family: FontAwesome;
		content: "\f096";
		font-size: 18px;
		color: $sc-color-theme-700;
		display: inline-block;
		width: 18px;
		vertical-align: middle;
	}

	input[type="checkbox"]:disabled + label {
		color: $sc-neutral-shade-300;
	}

	input[type="checkbox"]:disabled + label::before {
		color: $sc-neutral-shade-300;
	}

	input[type="checkbox"]:checked + label::before {
		content: "\f046";
	}
}

// Input Type Radio
//
// Radio buttons are used when there are a few options to select from and only one selection is allowed.
// With radio buttons options remain visible and easier to scan.
//
// Markup:
// <div class="radio">
//  	<input id="radio-1" name="my-radio-button" value="1" type="radio" checked>
//		<label for="radio-1"> Check me out</label>
//		<br>
//  	<input id="radio-2" name="my-radio-button" value="2" type="radio">
//		<label for="radio-2"> Check me out</label>
// </div>
// <br>
// <div class="radio">
//  	<input id="radio-disabled-1" name="my-radio-disabled-button" value="1" type="radio" checked disabled>
//		<label for="radio-disabled-1"> This option is disabled</label>
//		<br>
//  	<input id="radio-disabled-2" name="my-radio-disabled-button" value="2" type="radio" disabled>
//		<label for="radio-disabled-2"> This option is disabled</label>
// </div>
//
// Styleguide 2.3.7

.radio {
	display: inline;

	input[type="radio"] {
		display: none;
	}

	input[type="radio"] + label::before {
		font-family: FontAwesome;
		content: "\f10c";
		font-size: 18px;
		color: $sc-color-theme-700;
		display: inline-block;
		width: 18px;
		vertical-align: middle;
	}

	input[type="radio"]:disabled + label {
		color: $sc-neutral-shade-300;
	}

	input[type="radio"]:disabled + label::before {
		color: $sc-neutral-shade-300;
	}

	input[type="radio"]:checked + label::before {
		content: "\f192";
	}
}

// Error state
//
// If an input returns an error while validating, a wide red border is applied to make it visible and stand out from other input elements.
// A brief error message is also displayed below.
//
// Markup:
// <div data-validation-error>
//   <input type="text"  />
//   <br>
//   <select>
//     <option selected disabled>choose one</option>
//     <option value="1">1</option>
//     <option value="2">2</option>
//     <option value="3">3</option>
//   </select>
//   <br>
//   <textarea>Textarea</textarea>
// </div>
//
// Styleguide 2.3.8

[data-validation-error] input,
[data-validation-error] select,
[data-validation-error] textarea
{
	border-color: $sc-color-error;
	outline-color: desaturate(lighten($sc-color-error, 18), 35);
}

// Help Text
//
// Use brief and concise help text only when needed. Help text is placed below the field since it's optional for the user to read it.
// Avoid long help text accompanying the form.
//
// Markup:
// <label>Sample field:</label>
// <input type="text"  />
// <small class="help-text"> Help text always must be defined after input.</small>
//
// Styleguide 2.3.9

.help-text {
	color: $sc-color-theme-700;
	display: block;
	margin-top: -$sc-margin-lv1;
	margin-bottom: $sc-margin-lv3;
}

// Tooltip
//
// Use tool-tips for longer descriptions that the user can click on when they need help for that field.
//
// Markup:
// <i class="sc-tooltip" data-toggle="tooltip" data-placement="right" title="Sample text"></i>
//
// Styleguide 2.3.10

.sc-tooltip {
	@extend .fa;
	@extend %fa-question-circle;
	color: $sc-color-secondary;
	cursor:pointer;
}

.tooltip.in {
	opacity: 1;
}

.tooltip > .tooltip-inner {
	background-color: $sc-color-theme-background;
	padding: $sc-padding-lv2;
	color: $sc-color-secondary;
	border:1px solid $sc-color-theme-border-light;
	font-size: $sc-font-size-s;
	text-align: left;
}

.tooltip.top > .tooltip-arrow {
	border-top-color: $sc-color-theme-border-light;
	border-width: 5px 5px 0;
}

.tooltip.bottom > .tooltip-arrow {
	border-bottom-color: $sc-color-theme-border-light;
	border-width: 0 5px 5px;
}

.tooltip.right .tooltip-arrow,
.tooltip.left .tooltip-arrow {
	border-right-color: $sc-color-theme-border-light;
}

// Popover
//
// Markup:
// <i class="sc-popover" placement="top" data-toggle="popover" title="Popover title" data-content="And here's some amazing content. It's very engaging. Right?"></i>
//
// Styleguide 2.3.11

.sc-popover {
	@extend .fa;
	@extend %fa-question-circle;
	color: $sc-color-secondary;
	cursor:pointer;
}

.popover {
	border: 1px solid $sc-color-theme-border-light;
	color: $sc-color-secondary;
	font-size: $sc-font-size-s;
	z-index: 99999;
	padding: 0;

	&.right > .arrow {
		border-right-color: $sc-color-theme-border-light;
	}

	&.left > .arrow {
		border-left-color: $sc-color-theme-border-light;
	}

	&.bottom > .arrow:after {
		border-bottom-color: $sc-color-theme-background;
	}
}

.popover-title {
	background-color: $sc-color-theme-background;
}

