// Stock Info Labels
//
// Markup:
// <p class="stock-info-in">
//   <span class="stock-info-in-icon">
//     <i></i>
//   </span>
//   In Stock
// </p>
// <p class="stock-info-low">
//   <span class="stock-info-low-icon">
//     <i></i>
//   </span>
//   Low Stock Level
// </p>
// <p class="stock-info-out">
//   <span class="stock-info-out-icon">
//     <i></i>
//   </span>
//   Out of Stock
// </p>
//
// Styleguide 2.6.8

.stock-info-in {
	@extend .message-success;
}

.stock-info-in-icon {
	@extend .icon-container-success;

	i {
		@extend .icon-success;
	}
}

.stock-info-low {
	@extend .message-warning;
}

.stock-info-low-icon {
	@extend .icon-container-warning;

	i {
		@extend .icon-warning;
	}
}

.stock-info-out {
	@extend .message-error;
}

.stock-info-out-icon {
	@extend .icon-container-error;

	i {
		@extend .icon-error;
	}
}

.stock-info-description {
	@extend .message-info;
}

.item-view-stock-icon-description {
	@extend .fa;
	@extend %fa-info-circle;
	color: $sc-color-info;
	font-size: $sc-font-size-s;
}

// Stepped process
//
// Steps are needed when users need to go through a process of multiple sub-tasks to achieve one main goal.
// Steps help indicate to users how long the process will take, which steps are already completed and, how many steps are left in order to complete the task.
//
// Markup:
// <div class="stepped-process">
//   <a data-action="previous-step" class="stepped-process-button-back">
//     <i></i>
//     Previous
//   </a>
//   <ol class="stepped-process-menu">
//     <li class="stepped-process-step">
//       <a class="stepped-process-step-anchor" href="#">
//         1 <span class="wizard-step-navigation-step-anchor-label">Shipping Address</span>
//       </a>
//     </li>
//     <li class="stepped-process-divider">/</li>
//     <li class="stepped-process-step">
//       <a class="stepped-process-step-anchor stepped-process-step-active" href="#">
//         2 <span class="wizard-step-navigation-step-anchor-label">Payment</span>
//       </a>
//     </li>
//     <li class="stepped-process-divider">/</li>
//     <li class="stepped-process-step">
//       <a class="stepped-process-step-anchor stepped-process-step-disabled" href="#">
//         3 <span class="wizard-step-navigation-step-anchor-label">Review</span>
//       </a>
//     </li>
//   </ol>
// </div>
//
// Styleguide 2.7.2

.stepped-process {
	@include clearfix();
}

.stepped-process-button-back {
	@extend .button-back;
	float: left;
	margin-bottom: 0;
	cursor: pointer;

	> i {
		@extend .button-back-icon;
	}
}

.stepped-process-menu {
	float: right;
	position: relative;

	&:before{
		content: ' ';
		background-color: $sc-color-theme-border-light;
		height: 3px;
		display: block;
		position: absolute;
		top: 50%;
		margin-top: -1px;
		z-index: -1;
		width: 95%;
	}

	@media (min-width: $screen-sm-min) {
		float: none;

		&:before {
			display: none;
			content: none;
			height: 0;
		}
	}
}

.stepped-process-step {
	display: inline-block;
}

.stepped-process-step-anchor {
	@extend .small-title-viewonly;
	@include border-radius(100%);

	display: block;
	width: 30px; //different than $sc-target-size by design
	height: 30px;
	border: 3px solid transparent;
	line-height: 24px;
	margin-right: 3px;
	text-align: center;
	background-color: $sc-color-theme-background;

	@media (min-width: $screen-sm-min){
		width: auto;
		height: auto;
		background: none;
		border-radius: 0;
	}
}

.stepped-process-step-active {
	background-color: $sc-color-theme-700;
	color: $sc-neutral-shade-0;
	cursor: default;

	&:hover {
		color: $sc-neutral-shade-0;
	}

	&:visited, &:focus {
		color: $sc-color-copy;
	}

	@media (min-width: $screen-sm-min) {
		color: $sc-color-copy;
		background-color: transparent;

		&:hover, &:focus {
			color: $sc-color-copy;
		}
	}
}

.stepped-process-step-disabled {
	border-color: $sc-color-theme-border-light;
	background-color: $sc-neutral-shade-0;
	color: $sc-color-theme-500;
	cursor: default;

	&:hover, &:visited, &:focus{
		color: $sc-color-theme-500;
	}

	@media (min-width: $screen-sm-min) {
		border-color: transparent;
		background: none;
	}
}

.stepped-process-divider,
.stepped-process-step-anchor-label {
	display: none;

	@media (min-width: $screen-sm-min) {
		display: inline-block;
	}
}

.stepped-process-divider {
	color: $sc-color-theme-500;
}

// Thumbnails Image Sizes
//
// Thumbails are reduced-size versions of product images.
// They are used in product lists and modals and enable users to easily recognize a product by viewing a smaller version of the original product picture.
//
// .thumbnail-small  	- Small image
// .thumbnail-medium    - Medium image
// .thumbnail-large   	- Large image
//
// Markup:
// <img class="thumbnail-small" src="/assets/img/thumbnail.png" />
// <img class="thumbnail-medium" src="/assets/img/thumbnail.png" />
// <img class="thumbnail-large" src="/assets/img/thumbnail.png" />
//
// Styleguide 2.8

$sc-image-large-size: 150px;
$sc-image-medium-size: 100px;
$sc-image-small-size: 60px;

.thumbnail-large {
	width: $sc-image-large-size;
	height: $sc-image-large-size;
	display: inline-block;
}

.thumbnail-medium {
	width: $sc-image-medium-size;
	height: $sc-image-medium-size;
	display: inline-block;
}

.thumbnail-small {
	width: $sc-image-small-size;
	height: $sc-image-small-size;
	display: inline-block;
}

/* Edited for Posh Theme */

body {
    font-family: $sc-body-font-family;
    font-size: $sc-body-font-size;
    font-style: $sc-body-font-style;
    line-height: $sc-body-line-height;
    letter-spacing: $sc-body-letter-spacing;
    direction: $sc-body-direction;

    @extend %scroll-y;

    @media (min-width: $screen-md-min) {
        font-size: $sc-body-font-size-desktop;
    }
}

// Font styles
//
// Markup:
// <h1>H1 heading</h1><br/>
// <h2>H2 heading</h2><br/>
// <h3>H3 heading</h3><br/>
// <h4>H4 heading</h4><br/>
// <h5>H5 heading</h5><br/>
// <p>Lorem ipsum dolor sit amet</p>
// <p><strong>Lorem ipsum dolor sit amet</strong></p>
// <p><small>Lorem ipsum dolor sit amet</small></p>
// <p><a href="#">Lorem ipsum dolor sit amet</a></p>
//
// Styleguide 2.2.1

h1,
h4 {
    font-weight: $sc-font-weight-bold;
}

h2,
h3 {
    font-weight: $sc-font-weight-light;
}

h1 {
    color: $sc-color-h1;
    font-family: $sc-h1-font-family;
    font-size: $sc-h1-font-size;
    font-style: $sc-h1-font-style;
    text-decoration: $sc-h1-text-decoration;
    line-height: $sc-h1-line-height;
