// Titles styles
//
// .xsmall-title    - Product Titles XSmall & Product Titles XSmall navigatable
// .small-title     - Product Titles Small & Product Titles Small navigatable
// .middle-title    - Product Titles Medium & Product Titles Medium navigatable
// .large-title     - Product Titles Large & Product Titles Large navigatable
//
// Markup:
// <div class="xsmall-title">Lorem ipsum dolor sit amet</div>
// <div class="small-title">Lorem ipsum dolor sit amet</div>
// <div class="middle-title">Lorem ipsum dolor sit amet</div>
// <div class="large-title">Lorem ipsum dolor sit amet</div>
//
// Styleguide 2.2.2

.large-title {
    @extend h1;
}

.large-title-navigable {
    @extend h1;
    color: $sc-color-secondary;

    :hover{
        color: $sc-color-link-hover;
        text-decoration: none;
    }
}

.large-title-viewonly {
    @extend h1;
    color: $sc-color-copy-dark;
}

.medium-title {
    font-weight: $sc-font-weight-semibold;
    font-size: $sc-font-size-l;
    color: $sc-color-copy-dark;
}

.medium-title-navigable {
    color:$sc-color-primary;
    font-size: $sc-font-size-l;
    font-weight:$sc-font-weight-semibold;

    &:hover{
        color: $sc-color-link-hover;
        text-decoration:none;
    }
}

.medium-title-viewonly {
    font-size: $sc-font-size-l;
    color: $sc-color-copy-dark;
    font-weight:$sc-font-weight-semibold;
}

.small-title {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-semibold;
}

.small-title-navigable {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-semibold;
    color: $sc-color-secondary;

    :hover{
        color: $sc-color-link-hover;
        text-decoration: none;
    }
}

.small-title-viewonly {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-semibold;
}

.xsmall-title {
    font-size: $sc-font-size-m;
    color: $sc-color-copy-dark;
    font-weight: $sc-font-weight-normal;
    text-transform: capitalize;
}

.xsmall-title-navigable {
    color: $sc-color-primary;
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-normal;
    text-transform: capitalize;

    &:hover{
        color: $sc-color-link-hover;
        text-decoration:none;
    }
}

.xsmall-title-viewonly {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-normal;
    text-transform: capitalize;
}

