	@extend .box-column;
	border-top: 1px solid $sc-color-theme-border-light;

	@media (min-width: $screen-sm-min) {
		border-top: 0;
	}
}

[data-type="sticky"].sticked {
	@media (max-width: $screen-xs) {
		visibility: hidden;
	}
}

.sticky-button-container-clone {
	display: none;

	@media (max-width: $screen-sm) and (orientation: portrait) {
		&.sticked {
			display: block;
			position: fixed;
			top: 0px;
			width: 100%;
			z-index: 99;
			margin-left: -15px;
			background-color: white;
			padding-bottom: 20px;
			padding-left: 15px;
			padding-right: 15px;
			padding-top: 20px;
		}
	}
}

// Accordion
//
// Expanders are used to show users a snippet of a larger body of text in order to keep the user interface lightweight and easy to scan.
// Content is revealed when requested by the user.
//
// Markup:
// <div class="accordion-head">
//   <a class="accordion-head-toggle" data-toggle="collapse" data-target="#accordion-id" aria-expanded="false" aria-controls="accordion-id">
//     this is the primary accordion
//     <i class="acordion-head-toggle-icon"></i>
//   </a>
// </div>
// <div class="accordion-body in" id="accordion-id">
//   <div class="accordion-container">
//     <p>some content</p>
//     <p>more content</p>
//   </div>
// </div>
//
// Styleguide 3.1

/* Edited for Posh Theme */

.accordion-head {
	@extend .small-title;
	text-transform: inherit;
}

.accordion-head-toggle,
.accordion-head-toggle-secondary {
	display: block;
	cursor: pointer;
	height: 100%;
	line-height: $sc-target-size;
	padding: 0 $sc-padding-lv5;
	font-size: $sc-font-size-m;
	border: 1px solid $sc-color-theme-border-light;
}

.accordion-head-toggle {
	color: $sc-color-primary;
	font-weight: $sc-font-weight-normal;
}

.accordion-head-toggle-secondary {
	color: $sc-color-copy;
}

.accordion-head-toggle:hover,
.accordion-head-toggle-secondary:hover {
	text-decoration: none;
	color: $sc-color-copy-dark;
}

.acordion-head-toggle-icon,
.accordion-toggle-icon,
.accordion-toggle-icon-secondary {
	@extend .fa;
	@extend %fa-angle-up;
	float: right;
	height: $sc-target-size;
	line-height: $sc-target-size;
	font-size: $sc-font-size-xl;
	color: $sc-color-secondary;
	@include transition (transform $sc-animation-base-duration ease)
}

.accordion-toggle-icon-secondary {
	color: $sc-color-copy;
}

.collapsed .acordion-head-toggle-icon,
.collapsed .accordion-toggle-icon,
.collapsed .accordion-toggle-icon-secondary {
	@include rotate(180deg);
}

.accordion-body,
.accordion-body-secondary,
.accordion-body-alt {
	overflow: hidden;
	background-color: $sc-color-theme-content-background;
	border: 1px solid $sc-color-theme-border-light;
	border-top: 0;
	width: 100%;
	@media (min-width: $screen-md-min) {
		padding-left: 5px;
	}
}

.accordion-body-alt {
	//background-color: $sc-color-theme-background;
}

.accordion-container {
	margin: $sc-margin-lv4;
}

.accordion-container-table {
	margin: 0;
}

.accordion-divider {
	margin-bottom: $sc-margin-lv4;
}

// Datepicker
//
// Dates have many possible representations varying by culture and personal preference.
// Providing a visual representation of dates, in a familiar calendar format, facilitates entering dates in a consistent format.
// The Date picker is used by the user either to select a date or to filter by a range of dates.
// In cases where it is available, system native pickers are preferred since they are familiar to the user.
// For cases in which there is no native date picker available, SuiteCommerce Advanced relies on the bootstrap-datepicker component.
//
// It's required include 'bootstrap-datepicker' library in your Backbone.View.
//
// Markup:
// <div class="datepicker-container">
//   <label for="sample-datepicker">Date:</label>
//   <div class="datepicker-container-input">
//     <input
//       type="text"
//       data-provide="datepicker"
//       class="sample-datepicker"
//       name="sample-datepicker"
//       autocomplete="off"
//       data-format="yyyy-mm-dd"
//       data-todayhighlight="true"/>
//     <i class="datepicker-calendar-icon"></i>
//     <a data-action="clear-value">
//       <i class="datepicker-clear-icon"></i>
//     </a>
//   </div>
// </div>
//
// Styleguide 3.13

.datepicker-container {
	display: inline-block;

	.datepicker-container-input {
		position: relative;

		input {
			margin: 0;
		}

		.datepicker-calendar-icon {
			@extend .fa;
			@extend %fa-calendar;
			position: absolute;
			padding: $sc-padding-lv3;
			//width: 44px;
			//height: 44px;
			top: 0;
			right: 0;
			cursor: pointer;

			@media (min-width: $screen-md-min) {
				right: 0;
				top: 0;
			}
		}

		a {
			display: none;

			.datepicker-clear-icon {
				@extend .fa;
				@extend %fa-times-circle;
				color: $sc-color-theme-500;
				font-size: $sc-font-size-l;
			}
		}
	}
}

.datepicker {
	position:absolute;
	z-index:1;
	display:none;
	left: 0;
	width: 300px;
	border:1px solid $sc-color-theme-border-light;

	box-shadow: 0 $sc-flyout-arrow-shadow-height $sc-flyout-arrow-shadow-height * 2 transparentize($sc-theme-color-overlay, 1-0.175);
	padding: $sc-padding-lv4;
	margin-top: $sc-flyout-arrow-height;
	background: $sc-neutral-shade-0;

	&:before {
		position: absolute;
		top: -$sc-flyout-arrow-height;
		left: 14px;
		display: inline-block;
		border-right: $sc-flyout-arrow-height solid transparent;
		border-bottom: $sc-flyout-arrow-height solid $sc-color-theme-border-light;
		border-left: $sc-flyout-arrow-height solid transparent;
		content: '';
	}

	&:after {
		position: absolute;
		top: -$sc-flyout-arrow-shadow-height;
		left: 15px;
		display: inline-block;
		border-right: $sc-flyout-arrow-shadow-height solid transparent;
		border-bottom: $sc-flyout-arrow-shadow-height solid $sc-neutral-shade-0;
		border-left: $sc-flyout-arrow-shadow-height solid transparent;
		content: '';
	}

	.datepicker-switch {
		width: 100%;
		cursor: pointer;
		text-align: center;
		color: $sc-color-secondary;
	}

	.day {
		cursor:pointer;
		text-align: center;
		color: $sc-color-theme-700;
		padding: $sc-padding-lv1 $sc-padding-lv2;
	}

	.day.today {
		background-color: $sc-color-theme-700;
		color: $sc-neutral-shade-0;

		&:hover {
			background-color: $sc-color-theme-500;
	  	}
	}

	.next, .prev {
		color: white;
		cursor: pointer;
		width: $sc-target-size;
		height: $sc-target-size;
		line-height: $sc-target-size;

		@extend .fa;
		font-size:20px;
		display:inline;
		&:before{
			color: $sc-color-theme-700;
		}
	}

	.prev {
		text-align: left;
		@extend %fa-angle-double-left;
	}

	.next {
		text-align: right;
		@extend %fa-angle-double-right;
	}

	.month, .year {
		cursor: pointer;
		text-align: center;
		display: block;
		width: $sc-target-size;
		height: $sc-target-size;
		line-height: $sc-target-size;
		float: left;
		margin: 1%;
	}

	.day:hover, .month:hover, .year:hover {
		background-color: $sc-color-theme-background;
	}

	.day.active {
		background-color: $sc-color-primary;
		color: $sc-neutral-shade-0;
	}

	.day.old {
		color: $sc-color-theme-500;
	}

	.day.new,
	.day.disabled,
	.month.disabled,
	.year.disabled {
		color:  $sc-color-theme-500;
	}
}

.datepicker-orient-top {
	margin-top: $sc-margin-lv2;
}

.datepicker-orient-bottom {
	margin-bottom: $sc-margin-lv2;

	&:before {
		top: initial;
		bottom: -7px;
		border-top: $sc-flyout-arrow-height solid $sc-color-theme-border-light;
		border-bottom: 0;
	}

	&:after {
		top: initial;
		bottom: -6px;
		border-top: $sc-flyout-arrow-height solid $sc-neutral-shade-0;
		border-bottom: 0;
	}
}

.datepicker-days table thead tr th {
	color: $sc-color-secondary;
}

// Flyout
//
// A flyout is a contextual popup that is used to temporarily show details related to what the user is currently doing.
// Flyouts can be used to show options or controls in response to a user tap or click.
// A flyout is always dismissed when the user taps outside of it or re-taps on the element that triggered the flyout.
//
// Markup:
// <div class="sc-flyout">
//   <button class="sc-flyout-control" data-toggle="dropdown">
//     Button
//     <i class="sc-flyout-toggle-icon"></i>
//   </button>
//   <div class="sc-flyout-content">
//     <p>A flyout is a contextual popup that is used to temporarily show details related to what the user is currently doing.</p>
//   </div>
// </div>
//
// Styleguide 3.2

.sc-flyout {
	display: inline-block;
	position: relative;
	vertical-align: middle;
}

.sc-flyout-control {
	@extend .button-small;
	color: $sc-button-tertiary-text-color;
	padding: 0 $sc-padding-lv3;
	font-weight: $sc-font-weight-semibold;
	border: 1px solid $sc-color-theme-border-light;
	height: $sc-target-size;
	line-height: $sc-target-size;
	text-align: center;
	cursor: pointer;
	background: white;
	border-radius: $sc-border-radius;

	&:hover {
		color: $sc-button-tertiary-hover-text-color;
		text-decoration: none;

		&:disabled{
			opacity: 0.6;
			color: $sc-button-tertiary-text-color;
		}
	}

	&:focus {
		color: $sc-button-tertiary-text-color;
		outline: none;

		&:hover{
			color: $sc-button-tertiary-hover-text-color;
		}
	}

	&:active{
		color: $sc-button-tertiary-active-text-color;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.sc-flyout-toggle-icon {
	@extend .fa;
	@extend %fa-angle-down;
	float: right;
	height: $sc-target-size;
	line-height: $sc-target-size;
	font-size: $sc-font-size-l;
	color: $sc-color-secondary;
	margin-left: $sc-margin-lv2;
	@include transition (transform $sc-animation-base-duration ease);
}

.sc-flyout-content {
	@extend .dropdown-menu;
	@include transition (opacity $sc-animation-base-duration ease);

	display: none;
	opacity: 0;
	border: 1px solid $sc-color-theme-border-light;
	box-shadow: 0 6px 6px transparentize($sc-theme-color-overlay, 1-0.175);
	left: 0;
	padding: $sc-padding-lv3;
	margin-top: 7px;
	background: $sc-neutral-shade-0;
	position:absolute;
	z-index:1;

	&:before {
		position: absolute;
		top: -7px;
		left: 14px;
		display: inline-block;
		border-right: 7px solid transparent;
		border-bottom: 7px solid $sc-color-theme-border-light;
		border-left: 7px solid transparent;
		border-bottom-color: transparentize($sc-theme-color-overlay, 1-0.2);
		content: '';
	}

	&:after {
		position: absolute;
		top: -6px;
		left: 15px;
		display: inline-block;
		border-right: 6px solid transparent;
		border-bottom: 6px solid $sc-neutral-shade-0;
		border-left: 6px solid transparent;
		content: '';
	}
}

.open .sc-flyout-content {
	display: block;
	opacity: 1;
}

.open .sc-flyout-control {
	@extend .button-tertiary:hover;
}

.open .sc-flyout-toggle-icon {
	@include rotate(180deg);
}

.sc-flyout-bg {
	background: $sc-neutral-shade-700;
	opacity: 0.1;
	height: 100%;
	width: 100%;
	position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    filter: alpha(opacity=10);

    @media (min-width: $screen-sm-min) {
    	display: none;
    }
}

// Image gallery
//
// Carousels are used to display multiple pieces of content in a single, noticeable space.
// In our implementation, this organism is used in different ways:
//   - To display interactive banners for example, Home Page
//   - To display images of a product for example, Product Details
//   - To display a list of products for example, Merchandizing Zone
//
// It's required call to BxSlider plugin in order to build the gallery.
// To do that, you have to execute from the Backbone.View the following; Utils.initBxSlider($('[data-slider]'), [options]).
//
// Markup:
// <ul class="image-gallery bxslider" data-slider>
//   <li>
//     <img src="/assets/img/carousel-home-1.png" itemprop="image">
//   </li>
//   <li>
//     <img src="/assets/img/carousel-home-2.png" itemprop="image">
//   </li>
//   <li>
//     <img src="/assets/img/carousel-home-3.png" itemprop="image">
//   </li>
// </ul>
//
// Styleguide 3.3

.image-gallery {
	list-style: none;
	margin: 0;
}

.image-gallery li {
	display: inline-block;
}

.image-gallery img {
    display: block;
    max-width: 100%;
    height: auto;
}

// Wrapped in HTML tag to add the smallest hevier selector
// to fix bug on IE
html {

	.bx-controls {
		margin-top: $sc-margin-lv4;
	}

	.bx-wrapper {
		margin-bottom: 0;

		.bx-viewport {
			border: none;
			-webkit-box-shadow: none;
			-moz-box-shadow: none;
			box-shadow: none;
			left: 0;
		}

		.bx-pager {

			text-align: center;
			margin-top: $sc-margin-lv4;

			.touch & {
				overflow-x: scroll;
				-webkit-overflow-scrolling: touch;
				white-space: nowrap;
			}

			a {
				display: block;
				width:10px;
				height: 10px;
				border-radius:100%;
				opacity: 0.5;

				background-color: desaturate(darken($sc-color-theme-500, 13), 25);
				margin: 0 $sc-margin-lv1;
			}

			a.active{
				opacity: 1;
			}

			img{
				display: none;
			}

			@media (min-width: $screen-md-min) {
				margin-bottom: $sc-margin-lv4;
				position: static;

				img{
					display: inline;
				}

				a {
					margin: 0 8px;
					padding: 3px;

					width:	auto;
					height: auto;
					border-radius:0;
					background-color: transparent;
				}

			}
		}

		img {
			margin: 0 auto;
		}


	}

	.bx-pager-item img {
		max-width: 50px;
	}

}

/* 271491 IE8 styles for carousel */
.bx-wrapper .bx-pager .bx-pager-item {
  display: inline-block;
}

.carousel-next-arrow {
	@extend .fa;
	@extend %fa-angle-right;
}

.carousel-prev-arrow {
	@extend .fa;
	@extend %fa-angle-left;
}

// Information cards
//
// Information cards are blocks of content that group related pieces of information.
// Cards have meaning by themselves, independently to where they are being displayed.
// For example, an address is composed of related pieces of information that grouped together have meaning regardless of where it is being displayed.
//
// Markup:
// <div class="info-card-button-container">
//   <button class="button-info-cards-new"> Add new Address</button>
// </div>
// <section class="info-card">
//   <h4>John Smith</h4>
//   <p class="info-card-content">2955 Campus Drive</p>
//   <p class="info-card-content">Suite 100</p>
//   <p class="info-card-content">San Mateo, CA 94403</p>
//   <p class="info-card-content">United States</p>
//   <p class="info-card-content">305 424 1363</p>
//   <button class="button-edit">Edit</button>
// </section>
//
// Styleguide 3.4



.info-card {
	background: white;
	border: 1px solid $sc-color-theme-border-light;
	padding: $sc-padding-lv3;
}

.address-card-container {
	color: $sc-color-copy;
	display: block;
	padding: 0px;
	border: 1px solid $sc-color-theme-border;
	width: 100%;
	word-wrap: break-word;
}

.new-address-card-container {
	@extend .address-card-container;
	text-align: center;
	border-style: dashed;
}

.info-card-content {
	margin: $sc-margin-lv1 0;
}

.button-edit {
	@extend .button-small;
	@extend .button-tertiary;
}

.button-info-cards-new {
	@extend .button-small;
	@extend .button-secondary;
}

.info-card-button-container {
	@extend .row;
	padding: $sc-padding-lv3;
}

// Information box
//
// Markup:
// <div class="information-box">
// 	<div class="information-box-body">Body</div>
// 	<div class="information-box-summary">Summary</div>
// <div/>
//
// Styleguide 3.9

.information-box {
	width: 100%;
	border: 1px solid $sc-color-theme-border-light;
	background-color: $sc-color-theme-content-background;
}

.information-box-body {
	padding: $sc-padding-lv3;

	@media (min-width: $screen-sm-min) {
		padding: $sc-padding-lv4;
	}
}

.information-box-summary {
	padding: $sc-padding-lv2 $sc-padding-lv3;
	border-top: 1px solid $sc-color-theme-border-light;
	background-color: $sc-color-theme-background;

	@media (min-width: $screen-sm-min) {
		padding: $sc-padding-lv2 $sc-padding-lv4;
	}
}

