/* Edited for Posh Theme */

// List header
//
// List header is a component that contains controls used to refine (filter and/or sort) content within a list.
// In small devices, the controls are shown inside an expander (closed by default); in large devices controls are shown directly on top of the list.
//
// Markup:
// <nav class="list-header">
//   <div class="list-header-actions">
//     <button class="list-header-viewoption active"> <i class="list-header-viewoption-list"></i></button>
//     <button class="list-header-viewoption"> <i class="list-header-viewoption-box"></i></button>
//     <button class="list-header-viewoption"> <i class="list-header-viewoption-grid"></i></button>
//   </div>
//   <div class="list-header-expander">
//     <button class="list-header-expander-button" data-toggle="collapse" data-target="#list-header-filters" aria-expanded="true" aria-controls="list-header-filters">
//       Filter
//       <i class="list-header-expander-icon"></i>
//     </button>
//   </div>
//   <div class="list-header-filters collapse" id="list-header-filters">
//     <div class="list-header-filters-wrapper">
//       <div class="list-header-filters-row">
//         <div class="list-header-filter-column">
//           <select class="list-header-filter-input">
//             <option>Show 12</option>
//             <option>Show 24</option>
//             <option>Show 48</option>
//           </select>
//         </div>
//         <div class="list-header-filter-column">
//           <select class="list-header-filter-input">
//             <option>Low price</option>
//             <option>High price</option>
//           </select>
//         </div>
//         <div class="list-header-filter-column">
//           <button class="list-header-filter-facets">
//             Filter by
//             <i class="list-header-filter-facets-icon"></i>
//           </button>
//         </div>
//       </div>
//     </div>
//   </div>
// </nav>
//
// Styleguide 3.5

.list-header {
	background: transparent;

	@media (min-width: $screen-lg-min){
	  float: right;
	}
}

.list-header-actions {
	float: right;
}

.list-header-expander {
	display:inline-block;

	@media (min-width: $screen-sm-min) {
		float: right;
		margin-right: $sc-margin-lv2;
	}

	@media (min-width: $screen-md-min ) {
		display: none;
	}
}

.list-header-expander-button {
	@extend .button-tertiary;
	@extend .button-list-header;

	&:hover .list-header-expander-icon {
		color: white;
	}
}

.list-header-expander-icon {
	@extend .accordion-toggle-icon;

	display: inline-block;
	font-size: $sc-navigation-icon-size;
	margin-left: $sc-margin-lv1;
	position: relative;
}

.list-header-filters {
	clear:both;
	margin-left: -$grid-gutter-width / 2;
	margin-right: -$grid-gutter-width / 2;

	@media (min-width: $screen-md-min) {
		float: right;
		clear: none;
		border: 0;
		margin: 0;
		visibility: visible;
		display: block; //override for bootrap collapse
	}
}

