/* Edited for Posh Theme */

// Pagination
//
// Pagination is used to navigate between lists of products or records.
// Pagination is generally accompanied by a results counter.
//
// Markup:
// <nav>
//   <p class="pagination-count">1-12 of 37 results</p>
//   <ul class="pagination-links">
//     <li class="pagination-prev">
//       <a href="#">
//         <i class="icon-chevron-left"></i>
//       </a>
//     </li>
//     <li><a href="#">1</a></li>
//     <li><a class="pagination-active" href="#">2</a></li>
//     <li><a href="#">3</a></li>
//     <li><a href="#">...</a></li>
//     <li><a href="#">9</a></li>
//     <li><a href="#">10</a></li>
//     <li class="pagination-next">
//       <a href="#">
//         <i class="icon-chevron-right"></i>
//       </a>
//     </li>
//   </ul>
// </nav>
//
// Styleguide 3.6

.pagination-links {
    @extend .clearfix;
    float: right;
    width: 50%;
    text-align: right;
    padding: $sc-padding-lv1 0;

    @media (min-width: $screen-sm-min) {
        float: none;
        width: 100%;
        display: flex;
        justify-content: center;
    }

	li {
        float: left;
        // display: inline-block;
		// height: $sc-target-size;
		// width: $sc-target-size;
		text-align: center;
	}

	a {
		display: block;
		text-decoration: none;
        padding: $sc-padding-lv1 $sc-padding-lv2;
		// height: $sc-target-size;
		// line-height: $sc-target-size;
	}
}

.pagination-count {
    float: left;
    width: 50%;
    text-align: right;
	padding: $sc-padding-lv2 $sc-padding-lv4;    
    
    @media (min-width: $screen-sm-min) {
        float: none;
        width: auto;
        text-align: center;
    }
}

.pagination-links-number {
	display: none;
	// background-color: $sc-color-theme-background;

	@media (min-width: $screen-md-min) {
		display: block;
	}
}

.pagination-prev,
.pagination-next {
    color: $sc-color-copy !important;
	// background-color: $sc-color-theme-background;

	a:hover{
		// background-color: $sc-color-theme-background;
	}
}

.pagination-prev,
.pagination-prev-disabled {}

.pagination-next,
.pagination-next-disabled {
	margin-left: $sc-margin-lv1;

	@media (min-width: $screen-md-min) {
		margin-left: 0;
	}
}

.pagination-disabled,
.pagination-prev-disabled,
.pagination-next-disabled {
	display:none;
}

.pagination-active {
	color: $sc-color-copy !important;
	cursor: default;

	&:hover{
		color: $sc-color-copy;
	}
}

// Push pane
//
// A push pane is used for presenting complementary information within the context of a page.
// Push panes are used specifically in small screen devices. This type of pane is always triggered by the user when a push button is tapped.
// Push panes slide from right to left using a push animation.
//
// It's not required HTML markup to build a Push Pane, this component has to be invoked from a Backbone.View.
// To do that, it's necesary execute "this.application.getLayout().showInPush(view)", where "view" parameter
// is the content which you want to show.
//
// Styleguide 3.10

$sc-pusher-width-xs: 100%;
$sc-pusher-width-md: 472px;

.sc-pushing {
    position:fixed;
    z-index: 99; //fix for "save for later" removing modal
}

// mobile only
[data-pusher="sc-pushable-xs"]{
    width: $sc-pusher-width-xs;
    left: $sc-pusher-width-xs;
    background: #FFF;
    //display: none;
    @include translate3d(200%, 0, 0); //
    //right: -$sc-pusher-width-xs;
    //background: $sc-neutral-shade-0;

    @include transition-transform(400ms ease);

    &.sc-pushing-reopened {
        transition: none;
    }

    height: 100%;
    position: fixed;
    top: 0;
    z-index: 800;
    padding-left: $sc-padding-lv4;
    padding-right: $sc-padding-lv4;
    padding-bottom: $sc-padding-lv8;

    .sc-pusher-header {
        margin-left: -$sc-margin-lv4;
        margin-right: -$sc-margin-lv4;
        @media (min-width: $screen-sm-min) {
            display: none;
        }
    }
    @extend %scroll-y;
    @media (min-width: $screen-sm-min) {
        width: auto;
        height: auto;
        position: static;
        padding-left: 0;
        padding-right: 0;
        background: none;
        transform: none;
    }
    &.product-details-information-tab-content-panel,
    &.product-reviews-center-more-info-content-container {
        transform: none;
    }
}

// both tablet and mobile
[data-pusher="sc-pushable-md"]{
    width: $sc-pusher-width-xs;
    right: -$sc-pusher-width-xs;
    background: $sc-neutral-shade-0;

    @include transition-transform(400ms ease);

    &.sc-pushing-reopened {
        transition: none;
    }

    height: 100%;
    position: fixed;
    top: 0;
    z-index: 800;
    padding-left: $sc-padding-lv4;

    .sc-pusher-header {
        margin-left: -$sc-margin-lv4;
        @media (min-width: $screen-md-min) {
            display: none;
        }
    }

    @media (min-width: $screen-sm-min) {
        width: $sc-pusher-width-md;
        right: -$sc-pusher-width-md;
    }
    @extend %scroll-y;
    @media (min-width: $screen-md-min) {
        width: auto;
        height: auto;
        position: static;
        padding-left: 0;
        transform: none;
        background: transparent;
    }
}

[data-pusher="sc-pushable-xs"][data-action="sc-pushing"]{
    @include translate3d(-100%, 0, 0); //
    //display: block;
    @media (min-width: $screen-sm-min){
        transform: none;
    }
}

[data-pusher="sc-pushable-md"][data-action="sc-pushing"]{
    @media (min-width: $screen-sm-min){
        @include translate3d(-$sc-pusher-width-md, 0, 0);
    }
    @include translate3d(-$sc-pusher-width-xs, 0, 0);
    @media (min-width: $screen-md-min){
        transform: none;
    }
}

$sc-push-header-height: 69px;

.sc-pusher-header {
    background: $sc-color-theme-background;
    border-bottom: 1px solid $sc-color-theme-border-light;;
    margin-bottom: $sc-margin-lv4;
    height: $sc-push-header-height;
}

.sc-pusher-header-back {
    color: $sc-color-link;
    text-transform: capitalize;
    display: inline-block;
    width:50%;
    line-height: $sc-push-header-height;
    padding-left: $sc-padding-lv2;
}
.sc-pusher-header-back:after {
    font-family: FontAwesome;
    content: "\f053";
    float: left;
    margin-right: $sc-margin-lv2;
}

.sc-pushing-partial {
    overflow-y: hidden;
}

.main-push-overlay {

    display: none;

    @media (min-width: $screen-sm-min) and (max-width: $screen-sm-max) {
        height: 100%;
        width: 100%;
        position: fixed;
        z-index: 99;
        top: 0;
        left: 0;

        &.active {
            display: block;
        }
    }
}

// Quantity input
//
// Some of the molecules listed on this styleguide are responsive and change their behaviour according to the device or browser width.
// This is one of those, so, you might change the width of your browser window and see the molecule change its behaviour.
//
// Markup:
// <div class="quantity-input-container">
//   <button class="quantity-input-button-remove">-</button>
//   <input type="number" name="quantity" class="quantity-input" value="1" min="1" />
//   <button class="quantity-input-button-add">+</button>
// </div>
//
// Styleguide 3.11

.quantity-input-container {
	display: block;
}

input[type="text"].quantity-input,
input[type="number"].quantity-input {
 	width: 60px;
 	height: 40px;
 	text-align: right;
 	padding: $sc-margin-lv2;
	background-color: white;
    border: 1px solid $sc-color-theme-border;
	float: left;
	margin-bottom: 0;
    display: inline-block;
    vertical-align: bottom;

	@media (min-width: $screen-md-min) {
		display: inline-block;
		background: white;
		float: none;
		border: 1px solid $sc-color-theme-border;
	}
}

.quantity-input-button {
	background-color: $sc-color-theme-500;
	color: $sc-neutral-shade-0;
	border: none;
	width: 40px;
	height: 40px;
	//float: left;
    display: inline-block;

	&:hover, &:active  {
		background-color: $sc-color-theme-700;
	}

	&:focus {
		outline: none;
	}

	@media (min-width: $screen-md-min) {
		//display: none;
	}
}

.quantity-input-button-add {
	@extend .quantity-input-button;
	border-radius: 0 $sc-border-radius $sc-border-radius 0;
}

.quantity-input-button-remove {
	@extend .quantity-input-button;
	border-radius: $sc-border-radius 0 0 $sc-border-radius;
}

