// Styleguide 3.8

.content-tabs {
	display: none;
	margin-bottom: $sc-margin-lv3;

	@media (min-width: $screen-sm-min){
		display: block;
	}
}

.content-tabs-title {
	&.active a, &:hover a {
		color: $sc-color-secondary;
		border-bottom: 2px solid $sc-color-theme-700;
	}

	&:only-child {
		display: none;
	}

	a {
		padding: $sc-padding-lv1 $sc-padding-lv3;
		float: left;
		font-size: $sc-font-size-l;
		color: $sc-color-copy-light;
		border-bottom: 1px solid $sc-color-theme-300;
	}
}

.tab-content {
	clear:both;

	@media (min-width: $screen-sm-min){
		padding: $sc-padding-lv3 0;
	}
}

.tab-content-panel {
	@media (min-width: $screen-sm-min){
		padding-bottom: 0;
		-webkit-transition: all 0.5s ease;
		-moz-transition: all 0.5s ease;
		-ms-transition: all 0.5s ease;
		-o-transition: all 0.5s ease;
		transition: all 0.5s ease;
		opacity: 0;
		height: 0;
		overflow: hidden;

		&.active {
			opacity: 1;
			height: auto;
		}
	}
}

.tab-content-panel-title {
	margin-bottom: $sc-margin-lv4;
}

.tab-action {
	display: none;

	@media (min-width: $screen-sm-min){
		display: block;
	}
}

.tab-action-more {
	margin-top: $sc-margin-lv2;
	display:block;
}

.tab-action-less {
	display: none;
	margin-top: $sc-margin-lv2;
}

