// <div class="styleguide-margin-lv7"><span>sc-margin-lv7</span></div>
// <div class="styleguide-margin-lv8"><span>sc-margin-lv8</span></div>
//
// Styleguide 1.3.2
$sc-margin-base: 5px;
$sc-margin-lv1: $sc-margin-base;
$sc-margin-lv2: $sc-margin-base * 2;
$sc-margin-lv3: $sc-margin-base * 3;
$sc-margin-lv4: $sc-margin-base * 4;
$sc-margin-lv5: $sc-margin-base * 5;
$sc-margin-lv6: $sc-margin-base * 6;
$sc-margin-lv7: $sc-margin-base * 7;
$sc-margin-lv8: $sc-margin-base * 8;

// Content variables
//
// The content is set to span a width of 100%, yet the content doesn't always touch the edges.
