.product-reviews-form-confirmation {
    @extend .product-reviews-form-preview;
    .facets-item-cell-list-price {
        display: none;
    }
    .global-views-star-rating {
        display: none;
    }
    .item-view-stock {
        display: none;
    }

    .global-views-star-rating{
        .global-views-star-rating-content-label{
            visibility: hidden;
        }
    }

    .product-reviews-preview-review-rating {
        .product-reviews-form-confirmation-rating-attribute {
            .global-views-star-rating-content-label {
                visibility: visible;
            }
        }
    }

    margin-bottom: $sc-margin-lv4;
}
.product-reviews-form-confirmation-message {
    @extend .global-views-message;
    @extend .global-views-message-success;
    display: block;
    margin-top: 0;
    margin-left: -$sc-margin-lv3;
    margin-right: -$sc-margin-lv3;

    background: $sc-color-success-background;
    margin-bottom: $sc-margin-lv6;

    @media (min-width: $screen-sm-min){
        margin-left: 0;
        margin-right: 0;
    }
    h2 {
        color: $sc-color-success;
        margin: 0 0 $sc-margin-lv2;
    }
    a {
        display: inline-block;
    }
}
.product-reviews-form-confirmation-item-cell {
    @extend .product-reviews-form-item-cell;
}
.product-reviews-form-confirmation-content {
    @extend .product-reviews-form-preview-content;
    @extend .row;
    margin-top: $sc-margin-lv4;
    margin-bottom: $sc-margin-lv6;

    .global-views-star-rating {
        display: inline-block;
    }
}
.product-reviews-form-confirmation-content-review {
    @extend .product-reviews-preview-review-content;
    padding: 0 $sc-padding-lv3 $sc-padding-lv6;
}
.product-reviews-form-confirmation-content-review h4 {
    margin-bottom: $sc-margin-lv4;
}

.product-reviews-form-confirmation-icon-sign{
    @extend .icon-ok-sign;
}

.product-reviews-form-confirmation-button-back{
    @extend .button-back;
    margin-bottom: 0;
}

.product-reviews-form-confirmation-button-back-icon{
    @extend .button-back-icon;
}

.product-reviews-form-confirmation-review-rating{
    @extend .product-reviews-preview-review-rating;
}

.product-reviews-form-confirmation-review-title{
}

.product-reviews-form-confirmation-content-username {
    font-weight: 600;
    margin-bottom: 20px;

    span {
        font-weight: 400;
        color: $sc-color-primary;
    }
}


.product-reviews-form-confirmation-content-description{}

.product-reviews-form-confirmation-divider-desktop {
    margin-bottom: 20px;
}

.product-reviews-form-preview {
  @extend .product-reviews-form;
}
.product-reviews-form-preview-item-cell {
    @extend .product-reviews-form-item-cell;
}
.product-reviews-form-preview-content {
    @extend .product-reviews-form-content;
}
.product-reviews-form-preview-new {
    @extend .product-reviews-form-new;
}
.product-reviews-form-preview-content-title{
    @extend .product-reviews-form-content-title;
}
.product-reviews-form-preview-content-label-required {
    @extend .product-reviews-form-content-label-required;
}
.product-reviews-form-preview-content-required {
    @extend .product-reviews-form-content-required;
}
.product-reviews-form-preview-content-group {
    @extend .product-reviews-form-content-group;
}
.product-reviews-form-preview-content-groups {
    @extend .product-reviews-form-content-groups;
}
.product-reviews-form-preview-content-group-input {
    @extend .product-reviews-form-content-group-input;
}
.product-reviews-form-preview-content-group-text {
    @extend .product-reviews-form-content-group-text;
}
.product-reviews-form-preview-help {
    @extend .input-optional;
}
.product-reviews-form-preview-actions {
    @extend .product-reviews-form-actions;
    margin: 0;
}
.product-reviews-form-preview-actions-button-submit {
    @extend .product-reviews-form-actions-button-submit;
}
.product-reviews-form-preview-actions-button-edit {
    @extend .product-reviews-form-actions-button-preview;
}
.product-reviews-form-preview-actions-button-cancel  {
    @extend .button-tertiary;
    @extend .button-large;
    margin-bottom: $sc-margin-lv2;
    margin-left: 0;
    text-align: center;

    @media (min-width: $screen-sm-min){
        margin-left: $sc-margin-lv2;
    }
}

.product-reviews-form-preview-divider-desktop{
    margin-bottom: $sc-margin-lv6;

    @media (min-width: $screen-md-min) {
        border-top: 1px solid $sc-color-theme-border-light;
        margin: $sc-margin-lv6 0;
    }
}

.product-reviews-form-preview-divider{
}

.product-reviews-form-preview-main{}

.product-reviews-form {
  @extend .container;
    .product-reviews-form-global-star-rating {
        .global-views-star-rating-label{
            display:none;
        }
    }
}
.product-reviews-form-item-cell {
    @extend .row;
    margin-top: $sc-margin-lv2;
    .facets-item-cell-list-left {
        @extend .col-xs-12;
        @extend .col-md-10;
        margin-bottom: $sc-margin-lv3;
        @media (min-width: $screen-md-min){
            margin-bottom: $sc-margin-lv6;
        }
    }
    .facets-item-cell-list-right{
        @extend .col-xs-8;
        @extend .col-md-10;
    }
    & .cart-add-to-cart-button-container {
        @extend .col-sm-6;
        @extend .col-md-12;
        padding: 0;
    }
    .facets-item-cell-list-title {
        padding-top: 0;
        a {
            @extend .medium-title-navigable;
        }
        span {
            text-transform: uppercase;
            font-weight: 600;
            color: $sc-color-copy-dark;
            font-size: 16px;
        }
    }
    @extend .col-md-3;
    @media (min-width: $screen-md-min){
        margin: 0;
        padding-left: 0;
    }
    .item-views-option-tile,
    .item-views-stock,
    .item-views-price,
    .facets-item-cell-list-quick-view-link,
    .item-cell-rating {
        display: none;
    }
}
.product-reviews-form-content {
    @extend .row;
    background: $sc-color-theme-background;
    padding: 0;
    @extend .col-md-9;
    @media (min-width: $screen-md-min){
        margin: 0;
    }
}

.product-reviews-form-new {
    padding: 0 $sc-padding-lv3;

    .global-views-star-rating-label {
        font-weight: $sc-font-weight-normal;
        margin-bottom: $sc-margin-lv1;
        font-size: 14px;
    }
    .global-views-star-rating-empty-pegs {
    }
    .global-views-star-rating-empty-pegs-error {
    }
    .global-views-star-rating-area-writable-pegs{
        top: 5px;
    }

    .global-views-star-rating {
        display: inline-flex;
        margin-bottom: $sc-margin-lv4;
    }
    .product-reviews-form-global-star-rating {
        .global-views-star-rating-empty, .global-views-star-rating-filled, .global-views-star-rating-error {
            font-size: 35px;
        }
        .global-views-star-rating-empty {
            @extend %fa-star-o;
        }
        .global-views-star-rating-error {
            @extend %fa-star-o;
        }
        .global-views-star-rating-area {
            margin-bottom: $sc-margin-lv1;
        }
        button {
            width: 32.5px;
            height: 34px;
            position: relative;
            float: none;
            top: 0;
        }
    }
    .product-reviews-form-content-rating {
        .global-views-star-rating-empty-pegs,
        .global-views-star-rating-area-writable-pegs button {
            margin-right: 0;
        }
    }
}
.product-reviews-form-content-title {
    display:inline-block;
    font-family: $sc-font-family;
    font-size: $sc-font-size-m;
    //font-weight: $sc-font-weight-semibold;
    color: $sc-color-copy-dark;
    margin-top: $sc-margin-lv4;
    margin-bottom: $sc-margin-lv2;
    letter-spacing: 0;
}
.product-reviews-form-content-label-required {
    @extend .input-optional;
    display: block;
}
.product-reviews-form-content-required {
    @extend .input-required;
}
.product-reviews-form-content-rating {
    @extend .clearfix;
}
.product-reviews-form-content-group {
    clear: both;
    padding: 0;
    margin-top: $sc-margin-lv4;

    .product-reviews-form-help {
        margin-top: $sc-margin-lv1;
        margin-bottom: $sc-margin-lv1;
    }

    &:first-child {
        margin-top: 0;
    }
}
.product-reviews-form-content-groups {
    padding:0;
    float: left;
    clear: both;
    margin-bottom: $sc-margin-lv3;
    @extend .col-md-8;
    @media (min-width: $screen-md-min){
        margin-bottom: $sc-margin-lv4;
        clear: both;
    }
}
.product-reviews-form-content-group-label {
    margin-bottom: $sc-margin-lv1;
}
.product-reviews-form-content-group-input {

    margin-bottom: $sc-margin-lv1;
}
.product-reviews-form-content-group-text {

    min-width: 100%;
    max-width: 294px;
    min-height: 105px;
}
.product-reviews-form-help {
    @extend .input-optional;
}
.product-reviews-form-actions {
    @extend .row;
    padding: $sc-margin-lv6 $sc-margin-lv3 $sc-margin-lv4;
    background: $sc-neutral-shade-0;
    clear: both;

    @media (min-width: $screen-md-min){
        padding: $sc-margin-lv6 0;
    }

    @media (max-width: $screen-xs-max) {
        text-align: center;
    }
}
.product-reviews-form-actions-button-submit {
    @extend .button-medium;
    @extend .button-primary;
    margin-bottom: $sc-margin-lv2;
}
.product-reviews-form-actions-button-preview {
    @extend .button-secondary;
    @extend .button-medium;
    margin-bottom: $sc-margin-lv2;
    margin-left: 0;

    @media (min-width: $screen-sm-min){
        margin-left: $sc-margin-lv2;
    }
}

.product-reviews-form-divider {
    margin-bottom: $sc-margin-lv6;

    @media (min-width: $screen-md-min) {
        border-top: 1px solid $sc-color-theme-border-light;
        margin: $sc-margin-lv6 0;
    }
}

.product-reviews-form-actions-button-back {
    @extend .button-link;
    margin-right: 0;

    @media (min-width: $screen-sm-min){
          float: right;
    }
}

.product-reviews-form-controls{}

.product-reviews-preview-review {
    padding: 0;

    h4 {
        margin-bottom: $sc-margin-lv4;
        word-wrap: break-word;
    }

    .global-views-star-rating{
        .global-views-star-rating-content-label{
            visibility: hidden;
        }
    }

    .product-reviews-preview-review-rating {
        .product-reviews-preview-review-rating-attribute {
            .global-views-star-rating-content-label {
                visibility: visible;
            }
        }
    }

}
.product-reviews-preview-review-title {
    @extend .product-reviews-form-content-title;
}
.product-reviews-preview-review-rating {
    padding: $sc-padding-lv6 $sc-padding-lv3 0;

    .product-reviews-preview-review-rating {
        .global-views-star-rating-area {
            margin-bottom: $sc-margin-lv4;
            font-size: 20px;
            letter-spacing: 2px;
        }
        .global-views-star-rating-label-visible {
            font-weight: $sc-font-weight-semibold;
            margin-top: $sc-margin-lv1;
            width: 90px;
            float: left;
            font-size: 14px;
        }
    }
    .global-views-star-rating {
        display: inline-flex;
        margin-bottom: $sc-margin-lv4;
    }
    .global-views-star-rating-empty-pegs {
      display: inline-block;
      background: $sc-neutral-shade-300;
      border: 2px solid $sc-color-theme-border-light;

      @extend .global-views-star-rating-pegs-small;
    }
    .global-views-star-rating-filled-pegs {
      @extend .global-views-star-rating-empty-pegs;
      background: $sc-color-secondary;
      border: 2px solid $sc-color-secondary;
    }
}
.product-reviews-preview-review-content {
    padding: 0 $sc-padding-lv3 $sc-padding-lv6;
    clear: both;
}
.product-reviews-preview-review-content-username {
    font-weight: 600;
    margin-bottom: $sc-margin-lv4;
    word-wrap: break-word;

    span {
        font-weight: 400;
        color: $sc-color-primary;
    }

}
.product-reviews-preview-review-icon-ok-sign{
    @extend .icon-ok-sign;
}

.product-reviews-preview-review-content-description{
    p {
        word-wrap: break-word;
    }
}

/* Edited for Posh Theme */

.product-reviews-form-preview {
  @extend .product-reviews-form;
}
.product-reviews-review-title{
    @extend .product-reviews-form-content-title;
    margin: 0;
    text-transform: none;
}
.product-reviews-form-preview-content-label-required {
    @extend .product-reviews-form-content-label-required;
}
.product-reviews-form-preview-content-required {
    @extend .product-reviews-form-content-required;
}
.product-reviews-form-preview-content-group {
    @extend .product-reviews-form-content-group;
}
.product-reviews-form-preview-content-groups {
    @extend .product-reviews-form-content-groups;
}
.product-reviews-form-preview-content-group-input {
    @extend .product-reviews-form-content-group-input;
}
.product-reviews-form-preview-content-group-text {
    @extend .product-reviews-form-content-group-text;
}
.product-reviews-form-preview-help {
    @extend .input-optional;
}
.product-reviews-form-preview-actions {
    @extend .product-reviews-form-actions;
}
.product-reviews-form-preview-actions-button-submit {
    @extend .product-reviews-form-actions-button-submit;
}
.product-reviews-form-preview-actions-button-edit {
    @extend .product-reviews-form-actions-button-preview;
}

.product-reviews-center-list .backbone-collection-view-row {
	margin: 0;
}
.reviews-comments{
	clear:both;
	display:block;
	overflow:hidden;
}
.product-reviews-review-comment-item-cell{
	// text-align:left;
    // @extend .clearfix;
    @extend .col-sm-12;
    @extend .col-md-3;
}
.product-reviews-review-comment-item-cell-date{
	color: $sc-color-secondary;
	vertical-align: middle;
}
.product-reviews-review{
    @extend .row;
	// padding: 0 0 $sc-padding-lv4 0;

	// @media (min-width: $screen-sm-min){
	// 	padding-bottom: $sc-padding-lv2;
	// }
	h5 {
		text-transform:uppercase;
	}
}
[data-action="sc-pushing"] .product-reviews-review{
	padding:$sc-padding-lv4 0 0;

}
[data-action="sc-pushing"] .list-header-view {
	border-bottom: 0;
	margin: 0 -20px;
	padding: $sc-padding-lv4;
	background: $sc-neutral-shade-0;
}
.comment-username-icon-verified {
	@extend .icon-ok-sign;
}
.product-reviews-review-review {
	// clear:both;
	// display:inline-block;
    // width: 100%;
    @extend .col-sm-12;
    @extend .col-md-9;
}
.product-reviews-review-review-global-star-rating-container {
    @extend .clearfix;
}
.product-reviews-review-review-description {
	@extend .col-sm-12;
	@extend .col-xs-12;
	padding-left: 0;
	padding-right: 0;
}
.product-reviews-review-review-rating {
	@extend .col-sm-4;
	@extend .col-xs-12;
	padding-left: 0;
	padding-right: 0;
	margin-top: $sc-margin-lv3;

	@media (min-width: $screen-sm-min){
		margin-top: 0;
	}
	.product-review-rating-per-attribute {
		@media (min-width: $screen-sm-min){
			float: right;
		}
	}
	.global-views-star-rating {
	  min-width: 210px;
	}
}
.product-review-comment-description-rating {
	@extend .col-sm-3;
	@extend .col-xs-12;
	padding-left: 0;
	padding-right: 0;
}
.product-review-comment-footer{
	margin: 0 0 $sc-margin-lv4;

	p {
        color: $sc-color-theme-300;
		margin-right: $sc-margin-lv2;
		display: inline-block;

		@media (max-width: $screen-xs-min) {
			display: block;
		}
	}

	button.product-review-comment-footer-button:first-child {
		padding-right: $sc-padding-lv1;
	}
}
.product-review-comment-footer-button {
    @extend .button-link;
    color: $sc-color-theme-300;

	display: inline-block;
	padding: $sc-padding-lv2;

	.product-reviews-review-comment-footer-button-icon-like {
		@extend .icon-like;
	}

	.product-reviews-review-comment-footer-button-icon-unlike {
		@extend .icon-unlike;
	}
}

.product-reviews-review-comment-footer-button:hover,
.product-reviews-review-comment-footer-button:focus:hover {
    text-decoration: none;
}

.product-reviews-review-voted, .product-reviews-review-voted:focus, .product-reviews-review-voted:hover, .product-reviews-review-voted:focus:hover {
	color: $sc-neutral-shade-0;
	background: $sc-color-secondary;
	text-decoration: none;
	pointer-events: none;
}

.product-reviews-review-icon-ok-sign{
	@extend .icon-ok-sign;
}

.product-reviews-review-rating-per-attribute{
	@extend .product-review-rating-per-attribute;
}

.product-reviews-review-comment-footer{
    @extend .product-review-comment-footer;
    @extend .col-sm-12;
    padding-left: 0;
    padding-right: 0;

    p, button {
        color: $sc-color-copy-light;
        font-style: italic;
    }
}

.product-reviews-review-comment-footer-button{
	@extend .product-review-comment-footer-button;
}

.product-reviews-review-comment-username{
	font-size: $sc-font-size-s;
}

{{!-- Edited for Posh Theme --}}

<div class="product-reviews-review" data-id="{{reviewId}}">
	<div class="product-reviews-review-comment-item-cell">
        <p class="product-reviews-review-comment-username">
		{{translate '<span>$(0)</span>' reviewAuthor}}
		{{#if isReviewVerified}}
			- <i class="product-reviews-review-icon-ok-sign" data-toggle="tooltip" data-placement="right" title="{{translate 'verified purchaser'}}"></i>
		{{/if}}
	    </p>
        <p class="product-reviews-review-comment-item-cell-date">
            {{ reviewCreatedOn }}
        </p>
	</div>
	<div class="product-reviews-review-review">
        <div class="product-reviews-review-review-global-star-rating-container">
		    <div data-view="ProductReview.Review.Global.StarRating"></div>
        </div>

	    <h4 class="product-reviews-review-title">
		    {{reviewTitle}}
	    </h4>

		<p class="product-reviews-review-review-description">
            {{{translate reviewText}}}
		</p>
		<div class="product-reviews-review-review-rating">
            {{#if isReviewRatingPerAttributesLegthGreaterThan0}}
                <div class="product-reviews-review-rating-per-attribute">
                    <div data-view="Global.StarRatingAttribute"></div>
                </div>
            {{/if}}
		</div>

        {{#if showActionButtons}}
        <div class="product-reviews-review-comment-footer">
            <p>{{translate  'Was this review helpful?'}}</p>

            <button class="product-reviews-review-comment-footer-button {{usefulButtonClass}}" type="button" data-action="vote" data-type="mark-as-useful" data-review-id="{{reviewId}}">
                <!-- <i class="product-reviews-review-comment-footer-button-icon-like"></i>

                <span>
                    {{#if usefulCountGreaterThan0}}
                        {{translate ' ($(0))' usefulCount }}
                    {{/if}}
                </span>
                -->

                {{#if usefulCountGreaterThan0}}
                    {{translate 'Yes ($(0))' usefulCount }}
                {{else}}
                    {{translate 'Yes'}}
                {{/if}}
            </button>

            <button class="product-reviews-review-comment-footer-button {{notUsefulButtonClass}}" type="button" data-action="vote" data-type="mark-as-not-useful" data-review-id="{{reviewId}}">
                <!-- <i class="product-reviews-review-comment-footer-button-icon-unlike"></i>

                <span>
                    {{#if notusefulCountGreater}}
                        {{translate ' ($(0))' notUsefulCount}}
                    {{/if}}
                </span> -->

                {{#if notusefulCountGreater}}
                    {{translate 'No ($(0))' notUsefulCount}}
                {{else}}
                    {{translate 'No'}}
                {{/if}}
            </button>
        </div>
        <div data-type="alert-placeholder"></div>
        {{/if}}
	</div>
</div>




{{!----
Use the following context variables when customizing this template:

	reviewCreatedOn (String)
	reviewTitle (String)
	reviewAuthor (String)
	isReviewVerified (Boolean)
	reviewText (String)
	isReviewRatingPerAttributesLegthGreaterThan0 (Boolean)
	showActionButtons (Boolean)
	usefulButtonClass (String)
	reviewId (String)
	usefulCountGreaterThan0 (Boolean)
	usefulCount (String)
	notUsefulButtonClass (String)
	notusefulCountGreater (Boolean)
	notUsefulCount (String)

----}}

.product-views-option-checkbox {
	width: 100%;
	margin-bottom:$sc-margin-lv4;

	[data-validation-error="block"]{
  		display:table;
  		margin-top:$sc-margin-lv1;
  	}
}

.product-views-option-checkbox-label {}

.product-views-option-checkbox-label-required {
	@extend .input-required;
}

.product-views-option-checkbox-input {

}

.product-views-option-checkbox-required-reference-container {
	padding:$sc-padding-lv3 0;
}

.product-views-option-color{
	margin-bottom:$sc-margin-lv2;

	[data-validation-error="block"]{
  		display:table;
  		margin-top:$sc-margin-lv1;
  	}
}

.product-views-option-color-label{
	font-weight:$sc-font-weight-bold;
}

.product-views-option-color-container{
	@extend .color-picker-lg;
	clear:both;
}

.product-views-option-color-container-small{
	@extend .color-picker-sm;
	clear:both;
}

.product-views-option-color-picker {
	@extend .picker-color;
	@extend .picker-color-lg;
}
.product-views-option-color-picker-label {
	@extend .picker-color-label;
}
.product-views-option-color-picker-input {
	@extend .picker-color-input;
}
.product-views-option-color-picker-box {
	@extend .picker-color-box;
}
.product-views-option-color-picker-box.active {
	@extend .picker-color-box-active;
}
.product-views-option-color-picker-box.muted {
	@extend .picker-color-box-disabled;
