}

.quote-review-summary-container {
  @extend .summary-container;
}

.quote-review-summary-body {
}

.quote-review-summary-title {
  @extend .summary-title;
}

.quote-review-summary-grid-float {
  @extend .summary-grid-float;
}

.quote-review-summary-grid-right {
  @extend .summary-grid-right;
}

.quote-review-summary-subtotal {
  @extend .summary-subtotal;
}

.quote-review-summary-grid-float {
  @extend .summary-grid-float;
}

.quote-review-summary-shipping-cost-formatted,
.quote-review-summary-tax-total-formatted,
