.quote-review-summary-order-message {
  display: none;

  @media (min-width: $screen-sm-min) {
  display: block;
  margin-bottom: $sc-margin-lv2;
  }
}

.quote-review-termsandconditions-module-default {
  display: none;

  @media (min-width: $screen-sm-min) {
  display: block;
  float: right;
  margin-bottom: $sc-margin-lv4;
  }
}

.quote-review-termsandconditions-module-text {
}

.quote-review-termsandconditions-module-top,
.quote-review-termsandconditions-module-bottom {
  @media (min-width: $screen-sm-min) {
    display: none;
  }
}

.quote-review-termsandconditions-module-bottom {
  margin-top: $sc-margin-lv4;
}

.quote-review-submitbutton-module-top {
  margin-top: $sc-margin-lv2;
  margin-bottom: $sc-margin-lv4;

  @media (min-width: $screen-sm-min) {
  display: none;
  }
}

.quote-review-submitbutton-module-button {
  @extend .wizard-step-button-continue !optional;
}

.quote-review-summary-button-container {
  @extend .col-xs-12;
  background: $sc-color-theme-background;
  padding: $sc-padding-lv3;
  margin-bottom: $sc-margin-lv2;
  display: none;

  @media (min-width: $screen-sm-min) {
  display: block;
  }
}

.quote-review-summary-button-place-order {
  @extend .button-large;
  @extend .button-generic;
  @extend .button-primary;
  display: block;
}

.quote-review-summary-promocode {
  clear: both;
  display: block;
  overflow: hidden;
  margin-bottom: 10px;
}

.quote-review-summary-promocode-expander-head-toggle {
  @extend .accordion-head-toggle-secondary;
}

.quote-review-summary-promocode-expander-toggle-icon {
  @extend .accordion-toggle-icon-secondary;
}

.quote-review-summary-promocode-expander-body {
  @extend .accordion-body-alt;
}

.quote-review-summary-promocode-tooltip {
  @extend .sc-tooltip;
}

.quote-review-disclaimer {
  @extend .col-md-8;
  margin-bottom: $sc-margin-lv4;
  margin-top: $sc-margin-lv2;
}

.quote-review-disclaimer-message {
  color: $sc-color-secondary;
}

{{#if showBackToAccount}}
<a href="/" class="quote-list-button-back">
	<i class="quote-list-button-back-icon"></i>
	{{translate 'Back to Account'}}
</a>
{{/if}}

<section class="quote-list">
	<header class="quote-list-header">
		<h2>{{pageHeader}}</h2>
	</header>
	<div data-view="List.Header"></div>
	<div class="quote-list-results-container">
		{{#if collectionLength}}

		<table class="quote-list-quotes-table">
			<thead class="quote-list-content-table">
			<tr class="quote-list-content-table-header-row">
				<th class="quote-list-content-table-header-row-title">
					<span>{{translate 'Quote No.'}}</span>
				</th>
				{{#each columns}}
				<th class="quote-list-content-table-header-row-request-{{type}}">
					{{label}}
				</th>
				{{/each}}
			</tr>
			</thead>
			<tbody data-view="Quote.List.Items"></tbody>
		</table>

		{{else}}
		{{#if isLoading}}
		<p class="quote-list-empty">{{translate 'Loading...'}}</p>
		{{else}}
		<div class="quote-list-empty-section">
			<h5>{{translate 'No quotes were found'}}</h5>
		</div>
		{{/if}}
		{{/if}}
	</div>
</section>


