{{!-- Edited for Posh Theme --}}

<div class="header-sidebar-wrapper">

    <div class="header-sidebar-menu-wrapper" data-type="header-sidebar-menu">

        <ul class="header-sidebar-menu">
            {{#unless isStandalone}}
                {{#each treveraExtras.categories}}
                    {{#if text}}
                        <li>
                            <a {{objectToAtrributes this}} {{#if categories}}data-action="push-menu"{{/if}} name="{{text}}">
                                {{text}}
                                {{#if categories}}<i class="header-sidebar-menu-push-icon"></i>{{/if}}
                            </a>
                            {{#if categories}}
                                <ul>
                                    <li>
                                        <a href="#" class="header-sidebar-menu-back" data-action="pop-menu" name="back-sidebar">
                                            <i class="header-sidebar-menu-pop-icon"></i>
                                            {{translate 'Back'}}
                                        </a>
                                    </li>

                                    <li>
                                        <a {{objectToAtrributes this}}>
                                            {{translate 'Browse $(0)' text}}
                                        </a>
                                    </li>

                                    {{#each categories}}
                                        <li>
                                            <a {{objectToAtrributes this}} {{#if categories}}data-action="push-menu"{{/if}}>
                                                {{text}}
                                                {{#if categories}}<i class="header-sidebar-menu-push-icon"></i>{{/if}}
                                            </a>

                                            {{#if categories}}
                                                <ul>
                                                    <li>
                                                        <a href="#" class="header-sidebar-menu-back" data-action="pop-menu">
                                                            <i class="header-sidebar-menu-pop-icon"></i>
                                                            {{translate 'Back'}}
                                                        </a>
                                                    </li>

                                                    <li>
                                                        <a {{objectToAtrributes this}}>
                                                            {{translate 'Browse $(0)' text}}
                                                        </a>
                                                    </li>

                                                    {{#each categories}}
                                                        <li>
                                                            <a {{objectToAtrributes this}} name="{{text}}">{{text}}</a>
                                                        </li>
                                                    {{/each}}
                                                </ul>
                                            {{/if}}
                                        </li>
                                    {{/each}}
                                </ul>
                            {{/if}}
                        </li>
                    {{/if}}
                {{/each}}
            {{/unless}}

            {{#if showExtendedMenu}}
                <li class="header-sidebar-menu-myaccount" data-view="Header.Menu.MyAccount"></li>
            {{/if}}

        </ul>

    </div>

    <div class="header-sidebar-links">
        {{#if extraHeaderView.socialMediaLinks}}
            <!-- This is not showing, will need work in the view -->
            <div class="header-sidebar-social">
                <ul class="header-sidebar-social-list">
                    {{#each extraHeaderView.socialMediaLinks}}
                        <li>
                            <a {{objectToAtrributes item}} data-hashtag="{{datahashtag}}" data-touchpoint="{{datatouchpoint}}" data-target="{{datatarget}}">
                                {{#if icon}}<i class="header-sidebar-social-icon icon-{{icon}}"></i>{{else}}{{text}}{{/if}}
                            </a>
                        </li>
                    {{/each}}
                </ul>
            </div>
        {{/if}}

        <div class="header-sidebar-profile-menu header-menu-profile-logged-out" data-view="Header.Profile"></div>

        {{#if showExtendedMenu}}
            <a class="header-sidebar-user-logout" href="#" data-touchpoint="logout" name="logout">
                <i class="header-sidebar-user-logout-icon"></i>
                {{translate 'Sign Out'}}
            </a>
        {{/if}}

        {{!--
        <div data-view="QuickOrderHeaderLink"></div>
        --}}
        <div data-view="RequestQuoteWizardHeaderLink"></div>

        {{#if showLanguages}}
            <div data-view="Global.HostSelector"></div>
        {{/if}}
        {{!--
        {{#if showCurrencies}}
            <div data-view="Global.CurrencySelector"></div>
        {{/if}}
        --}}

    </div>

</div>



{{!----
Use the following context variables when customizing this template:

	categories (Array)
	showExtendedMenu (Boolean)
	showLanguages (Boolean)
	showCurrencies (Boolean)

----}}

.order-wizard-step-header{
	@extend .wizard-step-header;
}

.order-wizard-step-review-wrapper,
.order-wizard-step-content-wrapper{
	@extend .row;
}

.order-wizard-step-review-main,
.order-wizard-step-content-main,
.order-wizard-step-actions{
	@extend .col-xs-12;
	@extend .col-sm-7;
	@media (min-width: $screen-md-min) {
		width: 70%;
	}
}

.order-wizard-step-review-secondary,
.order-wizard-step-content-secondary-wrapper{
	@extend .col-xs-12;
	@extend .col-sm-5;
	float: right;
	@media (min-width: $screen-md-min) {
		width: 30%;
	}
}

.order-wizard-step-content-secondary{
	@extend .summary-wrapper;
    background: $sc-color-theme-content-background;
    overflow: hidden;
}

.order-wizard-step-message{
	@extend .wizard-step-message;

	margin-bottom: $sc-margin-lv6 !important;
}

.order-wizard-step-button-container{
	@extend .wizard-step-button-container !optional;
}

.order-wizard-step-button-continue {
	@extend .wizard-step-button-continue;
    margin-top: 0;
	margin-bottom: $sc-margin-lv2;
}

.order-wizard-step-button-back{
	@extend .wizard-step-button-back;
}

.order-wizard-step-guest-message{}
.order-wizard-step-title{}

$credit-card-height: 230px;
$credit-card-height-offset: 20px;

.paymentinstrument-creditcard {
    @extend .col-lg-4;
    @extend .col-sm-6;
    @extend .address-card-container;
    margin-bottom: $sc-margin-lv4;
}

.paymentinstrument-creditcard-require-field {
    margin-bottom: $sc-margin-lv4;
    margin-top: $sc-margin-lv2;
    display: inline-block;
}

.paymentinstrument-creditcard-number {
    display: inline-block;
}

.paymentinstrument-creditcard-required {
    @extend .input-required
}

.overview-payment .paymentinstrument-creditcard,
.order-wizard-paymentmethod-paymentinstrument-creditcard-module .paymentinstrument-creditcard {
    width: 100%;
    padding: 0;
}

.paymentinstrument-creditcard-content {
    width: 100%;

    @media(min-width: $screen-sm-min) {
        min-height: $credit-card-height + $credit-card-height-offset;
    }
}

.paymentinstrument-creditcard-list .paymentinstrument-creditcard-content,
.paymentinstrument-creditcard-module-list-placeholder .paymentinstrument-creditcard-content {
    @extend .info-card;

    @media(min-width: $screen-sm-min) {
        min-height: $credit-card-height + $credit-card-height-offset;
    }
}

.paymentinstrument-creditcard-section {
    @extend .col-xs-12;
    margin-bottom:$sc-margin-lv2;
    padding: 0;
}

.paymentinstrument-creditcard-security-code-section {
    @extend .col-xs-12;
    padding: 0;
}

.paymentinstrument-creditcard-header-icon {
    float: right;
    margin-right: $sc-margin-lv1;
    width:auto;
    height:$sc-target-size-mobile;
}

.paymentinstrument-creditcard-default {
    @extend .address-details-default-billing;
}

.paymentinstrument-creditcard-default-icon {
    @extend .address-details-default-shipping-icon;
}

.paymentinstrument-creditcard-actions {
    clear: both;
    margin-top: $sc-margin-lv3;
    text-align: right;
}

.paymentinstrument-creditcard-edit-form-button-edit {
    @extend .button-tertiary;
    @extend .button-small;
    margin-left: 0;
    text-align: center;
    margin-right:$sc-margin-lv2;

    @media (min-width: $screen-xs-min) and (max-width: $screen-lg-min)  {
        margin-bottom:$sc-margin-lv2;
    }
}

.paymentinstrument-creditcard-use-this-card-button {
    @extend .button-primary;
    @extend .button-medium;
    @extend .button-generic;
    margin-top:$sc-margin-lv2;
    margin-bottom:$sc-margin-lv2;

    @media (min-width: $screen-sm-min) {
        margin-bottom:0px;
    }
}

.paymentinstrument-creditcard-edit-form {
    clear:both;
}

.paymentinstrument-creditcard-edit-form-button-remove {
    @extend .button-tertiary;
    @extend .button-small;
}

.paymentinstrument-creditcard-name {
    margin-bottom:$sc-margin-lv2;
}

// checkout specific (hide back to myaccount btn)
.checkout-layout ~ .modal-container .paymentinstrument-creditcard-edit-back {
    display: none;
}

.paymentinstrument-creditcard-new-card {
    @extend .new-address-card-container;
    height: 154px;
}

.paymentinstrument-creditcard-new-card-title {
    display: inline-block;
    margin-top: 20px;
    color: $sc-neutral-shade-300;
    font-size: 1.1rem;
    font-weight: bold;
}

.paymentinstrument-creditcard-action {
    @extend .button-link;
}

.paymentinstrument-creditcard-container {
    padding: $sc-padding-lv2 $sc-padding-lv3;
}

.paymentinstrument-creditcard-selector {
    @extend .address-card-container;
    cursor: pointer;
    border-bottom: 0px;
    padding: $sc-padding-lv3;
}

.paymentinstrument-creditcard-selected {
    //background-color: $sc-neutral-shade-0;
}

.paymentinstrument-creditcard-selector-option {
    margin-bottom: 0px;
    cursor: pointer;
}

.paymentinstrument-creditcard-new-card-plus-icon {
    @extend .icon-plus;
    font-size: 2.5rem;
    color: $sc-color-theme-500;
}

.overview-payment-card {
    .paymentinstrument-creditcard-selected {
        min-height: 160px;
        border: none;
    }
    .paymentinstrument-creditcard-container {
        padding: 0px;
    }
}

.payment-wizard-step-content-main .order-wizard-paymentmethod-creditcard-module .paymentinstrument-creditcard-new-card {
  height: 234px;
}

/* Edited for Posh Theme */

.shopping-layout {
    @extend %sc-layout;
}

#main-container {
    z-index: 1;
}

.shopping-layout-header {
    @include common-header;
}

.shopping-layout-footer {
    @extend %sc-footer !optional;
}

.shopping-layout-breadcrumb, .shopping-layout-notifications {
    @extend .container;
    @include site-max-width-container;
    padding-left: $sc-content-padding;
    padding-right: $sc-content-padding;
}

.shopping-layout-breadcrumb {
    .global-views-breadcrumb {
        @media (min-width: $screen-md-min) {
            margin-top: 10px;
        }
    }
}

.shopping-layout-no-javascript-msg {
    @extend .message-warning;
    width: 100%;
}

.shopping-layout-content {
    //padding-top: $sc-padding-lv2;
}
